css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("equideow.com") {
  
/*Image de Fond*/
  div.body-background {
    background-image: url(http://creation.howrse.com.s3.amazonaws.com/nouveau/100000008-console.jpg);}
  
/*Couleur de Fond (enbas de page)*/
  body#global {
    background-color: #f8e6e4;}
  
/*Couleur Barre de Menu*/
  .menu-style-1 {
    background: #4d2e69;}
  
/*Couleur Messagerie et Profil*/
  .header-button {
    background: #4d2e69;}
  
/*Retrait du + pour les Equus et les Pass*/
  .header-currency .grid-cell:last-child {
    background-image: none;}
  
/*Arrondi des coins pour les Equus et les Pass*/
  .header-currency .grid-cell:first-child {
    border-radius: 4px 0 0 4px;}
  .header-currency .grid-cell:nth-child(2) {
    border-radius: 0 4px 4px 0;}
  
/*Couleur pour les Equus et les Pass*/
  .header-currency .grid-cell:first-child, .header-currency .grid-cell:nth-child(2) {
    background: #4d2e69;}
  .header-currency:hover .grid-cell:first-child,.header-currency:hover .grid-cell:nth-child(2){
    background:#ccade8}
  
/*Couleur et Arrondis de l'encadré des évènements*/
  .header-carousel-container {
    background: #4d2e69;
    border-radius: 4px 0 0 4px;}
  .header-carousel-button.disabled {
    background: #4d2e69;
    border-radius: 0 4px 4px 0;}
  .header-carousel-button{
    background: #4d2e69;
    border-radius: 0 4px 4px 0;}
  
/*Ajustement du texte pour les Equus et les Pass*/
  .header-currency-value {
    text-align: center;
    margin: 8px 10px 3px;}
  .header-currency-label {
    text-align: center;}
  
/*Englobement de l'icône d'Equus et de Pass*/
  .header-sprite-equus {
    margin-left: 0px;}
  .header-sprite-pass {
    margin-left: 0px;}

}
