css:
@-moz-document domain("fluentcloud.com") {
.sign-in-page .sign-in-bg {
    background-image: url(http://i.imgur.com/lR6NrAa.jpg);
    background-size: cover;
    background-repeat: no-repeat;
    -webkit-filter: blur(0px);
}
.sign-in-page .sign-in {
    border-radius: 5px;
    left: 22%;
}
.sign-in-page .sign-in header {
    border-radius: 5px 5px 0px 0px;
   
}
.sign-in-page form button {
    border-radius: 2px;
}
.navbar.navbar-inverse.navbar-default {
    background: linear-gradient(90deg, #e91e63, #e91e63 16.66%, #673ab7 16.66%, #673ab7 32.32%, #ff9800 32.32%, #ff9800 49.98%, #002244 49.98%, #002244 66.64%, #014923 66.64%, #014923 83.30%, #104e8b 83.30%, #104e8b 99.96%);
}
.navbar.navbar-inverse.navbar-default:hover {
    background: linear-gradient(90deg, #e91e63, #e91e63 16.66%, #673ab7 16.66%, #673ab7 32.32%, #ff9800 32.32%, #ff9800 49.98%, #002244 49.98%, #002244 66.64%, #014923 66.64%, #014923 83.30%, #104e8b 83.30%, #104e8b 99.96%);
}
.loveBox-dropdown .dropdown-menu {
    background: linear-gradient(90deg, #e91e63, #e91e63 16.66%, #673ab7 16.66%, #673ab7 32.32%, #ff9800 32.32%, #ff9800 49.98%, #002244 49.98%, #002244 66.64%, #014923 66.64%, #014923 83.30%, #104e8b 83.30%, #104e8b 99.96%);
}
.loveBox-dropdown:hover .dropdown-menu {
    background: linear-gradient(90deg, #e91e63, #e91e63 16.66%, #673ab7 16.66%, #673ab7 32.32%, #ff9800 32.32%, #ff9800 49.98%, #002244 49.98%, #002244 66.64%, #014923 66.64%, #014923 83.30%, #104e8b 83.30%, #104e8b 99.96%);
}
#repWords i {
    color: #f8f8f8;
}
#startReferring {
    background-color: #f0f0f0;
}
#startReferring a {
    color: #3c58bc;
}
#startReferring a:hover {
    background-color: #3cbc58;
    color: #424242;
}
#startReferring:hover {
    background-color: #3cbc58;
}
.navbar-collapse.collapse.cust-subnav {
    background: linear-gradient(90deg, #e97aa0, #e97aa0 16.66%, #a68ecf 16.66%, #a68ecf 32.32%, #ffce85 32.32%, #ffce85 49.98%, #63809f 49.98%, #63809f 66.64%, #7ab897 66.64%, #7ab897 83.30%, #6f95bc 83.30%, #6f95bc 99.96%);
}
.cust-navbar-header {
    background-color: #e97aa0;
}
.navbar-inverse .navbar-nav>.open>a,
.navbar-inverse .navbar-nav>.open>a:hover,
.navbar-inverse .navbar-nav>.open>a:focus,
.navbar-nav>#adminhelper>.open> a {
    background-color: #e91e63;
}
.dropdown-open {
    background: linear-gradient(90deg, #e91e63 55%, #673ab7 55%, #673ab7 99%, #ff9800 99%);
}
.dropdown-menu {
    background-color:#f0f0f0;
}
.dropdown-menu li.fc_header a {
    color: #3c58bc;
}
.dropdown-menu li.fc_header a:hover {
    color: #f0f0f0;
}
.navbar-nav>li>.dropdown-menu i {
    color: #3c58bc;
}
.navbar-nav>li>.dropdown-menu i:hover {
    color: #f0f0f0;
}
.cust-subnav ul li ul.dropdown-menu li a {
    background-color: #f0f0f0;
    color: #3c58bc;
}
.dropdown-menu.cust-subnav ul li ul.dropdown-menu li a:hover {
    color: #f0f0f0;
}
.icon-circle:before {
    color: #3c58bc;
}

.cust-subnav ul li ul.dropdown-menu{
	background-color:#f0f0f0;
}

.fc_extension a:hover {
    background: linear-gradient(90deg, #e91e63, #e91e63 16.66%, #673ab7 16.66%, #673ab7 32.32%, #ff9800 32.32%, #ff9800 49.98%, #002244 49.98%, #002244 66.64%, #014923 66.64%, #014923 83.30%, #104e8b 83.30%, #104e8b 99.96%);
}
}
