css:
/* ------------------------------------------
 * Couchtuner, Minimal Elegance ~
 *   
 *   This is based on my other style
 *   Couchtuner, Elegant~ but hides the
 *   sidebar and stretches the video players
 *   to better fit the viewport without
 *   having to enter fullscreen.
 *
 *
 *
 * --------------------------------------- */

 @namespace url(http://www.w3.org/1999/xhtml);

  /* global */

@-moz-document domain("vodlocker.com") {
  /* fix vodlocker iframe */
  body,center,table,tbody,tr,td,body > center:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > center:nth-child(1) > div:nth-child(1),#player_code,#flvplayer_wrapper {
   width: 100% !important;
   height: 625px !important;
  }
}

@-moz-document domain("couchtuner.city"),domain("couchtuner.ag"),domain("couchtuner.fr"),domain("iwatchonline.us"),domain("couch-tuner.ag"),domain("couch-tuner.city") {
  * {
    background-color: rgba(40, 40, 40, 1) !important;
    color: rgba(220, 220, 220, 1) !important;
    font-family: noto sans !important;
    font-size: 10pt !important;
    border: none !important;
  }

  /* links */
  a {
    text-decoration: none !important;
    color: rgba(172, 199, 210, 1) !important;
    transition-duration: 0.5s !important;
  }


  a:hover {
    color: rgba(213, 231, 239, 1) !important;
    transition-duration: 0.5s !important;
  }
  
  a:visited {color: rgba(115, 141, 246, 1) !important;}

  /* make iframe larger to better fit viewport */
  iframe {
    margin-bottom: 2% !important;
    width: 99% !important;
    min-height: 625px !important;
    box-shadow: 0px 0px 6px 2px rgba(21, 21, 21, 0.7) !important;
  }
  
  /* remove shadow from disqus iframe */
  .comments_part iframe {
    box-shadow: none !important;
  }

  /* hiding of empty, irrelevant, and ugly elements */
  br,#header,#container > div:nth-child(2),#container > center:nth-child(4),.post > div:nth-child(5),.entry > strong:nth-child(1),.entry > div:nth-child(15) > strong *,.entry > center *,.meta,.comments_part > strong:nth-child(4),.g-plusone,.pagination > center *,#s_top,#footer,#sidebar,
  [src*="//www.facebook.com/plugins/like.php?href=https%3A%2F%2Fwww.facebook.com%2Fcouchtuner&width=450&height=24&colorscheme=dark&layout=standard&action=like&show_faces=false&send=false"],
  [id*="oauth2relay"]
    {display: none !important;}

  /* navigation bar */
  #menu {
    margin: 1% 0 1% -0.5% !important;
  }

  #menu a {
    margin-right: 10px;
    color: rgba(43, 42, 42, 1) !important;
    transition-duration: 0.5s !important;
    box-shadow: 0px 0px 6px 2px rgba(51, 51, 51, 0.4) !important;
  }
  
  #menu a:hover {
    color: whitesmoke !important;
    transition-duration: 0.5s !important;
  }
  
  #menu li {margin: -0.1% !important;}
  
  /* setting the colour of the content */
  iframe,#content *,#menu ul li a,.post *,.post > h2:nth-child(1),.postTabs,.entry * {background-color: rgba(51, 51, 51, 1) !important;}
  
  /* setting content's shadow */
  #content,.post {
    margin-left: -4.5%;
    width: 110%;
    box-shadow: 0px 0px 6px 2px rgba(51, 51, 51, 0.4) !important;
  }

  /* setting the color of the content's border */
  .post {
    min-height: 60px !important;
    width: 105% !important;
    border: 8px solid !important;
    border-color: rgba(51, 51, 51, 1) !important;
    padding: 0 8px 18px 0 !important;
  }

  /* make disqus easier to read by decreasing its width */
  .comments_part {
    margin: 0 0 0 15%;
    width: 65%;
  }
  
  /* field on the search page */
  #searchform #s2 {
    margin-bottom: 5% !important;
    background: whitesmoke !important;
    color: black !important;
  }
  
  /* colour of "Posted by" text */
  .descr,.descr > abbr:nth-child(1) {
    margin-bottom: 2.5%;
    background-color: rgba(51, 51, 51, 1) !important;
    color: rgba(220, 220, 220, 0.2) !important;
  }

  /* player tabs */
  ul.postTabs {
    margin: -3% auto -0.3% auto !important;
    border: none !important;
  }
  
  ul.postTabs li a {
    background-color: rgba(66, 66, 66, 1) !important;
    color: inherit !important;
    border: none !important;
  }

  ul.postTabs li.postTabs_curr a {color: rgba(213, 231, 239, 1) !important;}
  
  /* page navigation on "New Releases" */
  .pagination {margin: -4% 0 0 1%;}
}
