css:
@-moz-document regexp('https://www\\.google\\.(com|co.uk)/search.*') {
  
  #hplogo, #logocont{
    filter: grayscale(1);
    display: none;
  }
  
  .logo-subtext{
    display: none;
  }
  
  #cnt{
    background: white;
  }
  
  #rcnt, #botabar{
    margin-top: 40px !important;
  }
  
  .gb_gf.gb_R.gb_tf.gb_nf{
    display: none;
  }
  
  #hdtb-msb{
    display: none;
  }
  
  #hdtbSum, .sfbgg{
    border-bottom:  0px !important;
  }
  
  #top_nav{
    display: none;
  }
  
  #resultStats{
    width: 500px;
    text-align: right;
    position: absolute;
    top: -52px;
    z-index: 99999;
    left: 625px;
  }
  
  #resultStats nobr{
    display: none;
  }
  
  .gb_b[title='0 Google notifications'], .gb_mc.gb_Ec.gb_R.gb_nc{
    display: none;
  }
  
  #tads a, #tadsb a, #res a, #rhs a, #taw a {
    text-decoration: none;
    color: black !important;
    font-weight: 300;
  }
  
  .tsf-p{
    top: 20px;
    left: 9px;
  }
  
  .sfbg.nojsv, .sfbg.nojsv .sfbgg{
    background: transparent !important;
  }
  
  #footcnt{
    display: none;
  }
}
