@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("http(s)?://.*\\.animezone\\.pl/(?!(v[0-9].[0-9]/)?plugins/|Fplugins|plugins/like).*") {

    body {
                                                                                    background-color: #1e1e1e ! important;
    }

    .footer {
    /*display: none; */
                                            background-color: #1e1e1e ! important;
                                            color: white;
    }

    .news, .panel-body, .categories {
                                            background-color: #2e2e2e ! important;
                                            color: #DEDEDE;
    }

    .well {
                                            background-image: linear-gradient(to bottom,#525252 0,rgba(33, 33, 33, 0.3) 100%);
                                            color: white;
    }

    .label-grey {
                                            background-color: #3A3A3A;
    }

    .categories-collection .categories .label-grey a, .categories-newest .categories .label-grey a, .user-activity .categories .label-grey a {
                                            color: #DEDEDE;
    }

    .site-sidebar .list-group-item {
                                            background-color: #3A3A3A;
                                            color: white;
    }

    .list-group-striped a:hover, .list-group-striped li:hover {
                                            background-color: #292929;
    }

    .list-group-striped a:nth-child(even), .list-group-striped li:nth-child(even) {
                                            background-color: #2D2D2D;
    }

    .panel-body, td, .site-sidebar .ratings .panel-body {
                                            background: #9c9c9c;
                                            background: linear-gradient(to bottom, #1E1E1E 0%,#080808 100%);
                                            box-shadow: inset 0 1px 1px 1px rgba(0, 0, 0, 0.25);
                                            border-radius: 1px;
                                            border-bottom: 1px solid #fcfcfc;
                                            color: white; 
    }

    .table-bordered>thead>tr>th, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>tbody>tr>td, .table-bordered>tfoot>tr>td {
                                            border: 1px solid #6A6A6A;
    }

    .site-main .panel-default .panel-heading, .site-main .category-description h5 {
                                            background: #c7cbd4;
                                            background: linear-gradient(to bottom, #151515 0%,#070707 100%);
                                            box-shadow: inset 0 1px 0 0 #e6e7eb;
                                            color: #FFFFFF;
    }

    .form-control {
                                            display: block;
                                            width: 100%;
                                            height: 34px;
                                            padding: 6px 12px;
                                            font-size: 14px;
                                            line-height: 1.42857143;
                                            color: #E1E1E1;
                                            background-color: #242424;
                                            background-image: none;
                                            border: 1px solid #ccc;
                                            border-radius: 4px;
                                            -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
                                            box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
                                            -webkit-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
                                            transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    }

    .comments-list .comment:nth-child(even) .comment-body {
                                            background-color: #444444;
    }
    .comments-list .comment .comment-body {
                                            width: 88%;
                                            margin-left: 2%;
                                            border-radius: 1px;
                                            background-color: #2D2D2D;
                                            padding: 8px;
                                            position: relative;
                                            color: #FFFFFF;
    }

    .site-sidebar .ratings .panel-footer {
                                            background-color: #1F1F1F;
                                            padding-top: 1px;
                                            border: none !important;
    }

    .pager li>a, .pager li>span {
                                            display: inline-block;
                                            padding: 5px 14px;
                                            background-color: #141414;
                                            border: 1px solid #6F6F6F;
                                            border-radius: 15px;
    }

    th {
                                            font-weight: bold;
                                            background-color: #2F2F2F;
                                            color: white;
    }

    .bs-callout-info {
                                            background-color: #121212;
                                            border-color: #5bc0de;
    }

    .panel {
                                            margin-bottom: 20px;
                                            background-color: #2A2A2A;
                                            border: 1px solid transparent;
                                            border-radius: 4px;
                                            -webkit-box-shadow: 0 1px 1px rgba(0,0,0,.05);
                                            box-shadow: 0 1px 1px rgba(0,0,0,.05);
    }

    .bs-callout-danger {
                                            background-color: #242424;
                                            border-color: #E7DAD9;
    }

    .comments-list .comment .comment-body .close, .comments-list .comment .comment-body .comment-edit, .comments-list .comment .comment-body .comment-report, .comments-list .comment .comment-body small {
                                            color: #F6F6F6;
    }

    .popover {
                                            position: absolute;
                                            top: 0;
                                            left: 0;
                                            z-index: 1010;
                                            display: none;
                                            max-width: 276px;
                                            padding: 1px;
                                            text-align: left;
                                            background-color: #3E3E3E;
                                            background-clip: padding-box;
                                            border: 1px solid #ccc;
                                            border: 1px solid rgba(0,0,0,.2);
                                            border-radius: 6px;
                                            -webkit-box-shadow: 0 5px 10px rgba(0,0,0,.2);
                                            box-shadow: 0 5px 10px rgba(0,0,0,.2);
                                            white-space: normal;
    }

    .comments-list .comment .spoiler {
                                            margin: 5px 0;
                                            color: #FFFFFF;
    }

}
