*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hyperdev.com"), domain("support.hyperweb.space") {
  /* Dark background, light grey text */
  .ace-theme-tricolore.ace-tm, .ace_gutter-cell, .ace_gutter-layer, .ace_gutter, body, #activity-log .header, .log build-log, .log-time, .overlay, .pop-over, .pop-over-info, .project-loader, aside.quote .title, blockquote, .map, .map-collapsed, #reply-control, #reply-control .contents .d-editor-preview, #reply-control .wmd-controls .d-editor-input, #reply-control .d-editor-button-bar, .modal.in, .menu-panel, .user-menu .notifications .read, .badge-card .badge-contents .badge-icon, .topic-map section, .user-main .user-stream .item, .category-name, .nav-pills > li > a, .ace-theme-tricolore.ace-tm .ace_gutter, .read-only, .read-only #ace, .editor-wrap .read-only, .ember-view .user-preferences, .user-main .user-content, body .avatar-wrapper, .advanced-options-pop .public, .advanced-options-pop .private, code, .post-entry p, .post-entry-right p, .site-title a {
    background: #0E0E0E !important;
    background-color: #0E0E0E !important;
    color: #9F9F9F !important;
  }
  /* Transparent background */
  .avatar {
    background-color: transparent !important; 
  }
  /* Lighter background */
  #activity-log, #activity-log pre, .topic-body, .badge-contents, .user-right {
    background-color: #181818 !important;
  }  
  /* Darker text */
  .button-tertiary, #topic-title h1 a, .badge-wrapper.bullet span.badge-category, .title, .badge-card .badge-contents .badge-info, .user-main .user-stream .excerpt {
    color: #626262 !important;
  }
  /* Orange background, white text */
  .nav-pills > li.active > a, .nav-pills > li > a.active {
    color: #fff !important;
    background-color: #e45735 !important;
  }
  /* Dark green background */
  .keyboard-shortcuts .key {
   background-color: #0d361e;
  }
}
