css:
@-moz-document url-prefix("http://www.leningrad.su/makod") {
::-webkit-scrollbar { 
    display: none; 
}
body, 
table,
tr, 
td {
    background-color: #272822;
    color: #f8f8f2;
}
body,
table {
    font-family: Calibri, Arial, sans-serif;
    font-size: 11pt;
}
p {
    font-weight: normal;
}
body>center {
    font-size: 10pt;
    font-weight: normal;
    color: #75715e;
}
body>hr {
    border: 0;
    border-top: 1px dotted #75715e;
}
a,
a:link {
    color: #ae81ff;
}
table {
    border-collapse: collapse;
}
table,
th,
td {
    border: 1px dotted #75715e;
}
}
