css:
@-moz-document domain("imgur.com") {
#fullbleed-bg {
    height: 25% !important;
	background: transparent !important;
	background: -moz-linear-gradient(top,  rgba(43,43,43,0.65) 0%, rgba(0,0,0,0) 100%) !important; /* FF3.6+ */
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(43,43,43,0.65)), color-stop(100%,rgba(0,0,0,0))) !important; /* Chrome,Safari4+ */
	background: -webkit-linear-gradient(top,  rgba(43,43,43,0.65) 0%,rgba(0,0,0,0) 100%) !important; /* Chrome10+,Safari5.1+ */
	background: -o-linear-gradient(top,  rgba(43,43,43,0.65) 0%,rgba(0,0,0,0) 100%) !important; /* Opera 11.10+ */
	background: -ms-linear-gradient(top,  rgba(43,43,43,0.65) 0%,rgba(0,0,0,0) 100%) !important; /* IE10+ */
	background: linear-gradient(to bottom,  rgba(43,43,43,0.65) 0%,rgba(0,0,0,0) 100%) !important; /* W3C */
}
body{
    background: transparent;
    width: 100%;
    height: 100%;
}
html
{
    background: url("http://i.imgur.com/ms4CbqB.jpg") !important;
		background-attachment: fixed !important;

}

#topbar {
	top: 0 !important;
  	bottom: 5 !important;
  	position: fixed !important;
  	padding:0px 0px 0px 0px !important;
  	margin-bottom: 0px !important;
}
    
#cta-container-placeholder {
    z-index: 102 !important;
}

#content {
	top: 50px !important;
}
  
.post-container, #right-content {
  padding-top: 30px !important;
}
    
/*Gallery Page clear background*/
#captions .usertext {
    background-color: rgba(24, 24, 23, 0.6) !important;
    background: rgba(24, 24, 23, 0.6) !important;
    -webkit-border-radius: 11px;
    -moz-border-radius: 11px;
    border-radius: 11px;   
}

/*Non Message Page clear interrim*/
.textbox {
    background-color: rgba(24, 24, 23, 0.6) !important;
    background: rgba(24, 24, 23, 0.6) !important;
    -webkit-border-radius: 11px;
    -moz-border-radius: 11px;
    border-radius: 11px;
}

.comment .caption.contexted .usertext {
    border: 2px solid #85BF25;
    background: rgba(24, 24, 23, 0.6) !important;
    -webkit-border-radius: 11px;
    -moz-border-radius: 11px;
    border-radius: 11px;
}

/*Messages*/
    div.panel {
        background-color: transparent !important;
    }

    div.thread-bottom {
        background-color: transparent !important;
    }
    
    div.messages.nano.has-scrollbar {
        border: 0px !important;
    }

    div.message-wrapper.striped {
        background: rgba(153, 102, 255, .1) !important;
        -webkit-border-radius: 11px;
        -moz-border-radius: 11px;
        border-radius: 11px;
        border: rgba(153, 102, 255, .6) solid 2px;
        max-width: 60% !important;
        padding: 6px 0px 6px 6px !important;
        align-content: left !important;
        margin: 5px !important;
    }

    div.message-wrapper.striped > div.message-text {
        max-width: 100% !important;
        text-align: left !important;
    }

    div.message-wrapper:not(.striped) {
        background: rgba(0, 102, 204, .1) !important;
        -webkit-border-radius: 11px;
        -moz-border-radius: 11px;
        border-radius: 11px;
        border: rgba(0, 102, 204, .6) solid 2px;
        padding: 6px 6px 6px 6px !important;
        align-content: right !important;
        text-align: right !important;
        max-width: 60% !important;
        position: relative !important;
        left: 30% !important;
        margin: 5px !important;
    }

    div.message-wrapper:not(.striped) > div.message-text {
        max-width: 100% !important;
        text-align: right !important;
    }

    .from-user
    {
        display: none !important;
        text-align: right !important;
    }

    .striped .from-user
    {
        text-align: left !important;
    }

    .timestamp
    {
        position: relative !important;
    }

    .striped .message-info .timestamp
    {
        left: 0px !important;
        text-align: left !important;
    }

    .message-info .timestamp
    {
        text-align: right !important;
        right: 0px !important;
    }

	
}
