css:
/*PeerAnswer Dark Style by geerky42 */

@-moz-document url-prefix('https://www.peeranswer.com/') {
  body {
      background: #000 url(https://i.imgur.com/6WP4L9v.png) no-repeat fixed center top / cover!important
  }

  *:not(.colorify-text) {
      color: #fff !important
  }

  *[data-level="100"] .colorify-text {
      color: white !important;
  }

  a,
  .pseudo-link_blue {
      color: #00b1f2 !important
  }

  textarea,
  input {
      background: #40536a !important;
      border-color: #333 !important
  }

  #download_drawing,
  #insert_drawing,
  #pencil_size {
      color: #000 !important
  }

  #pencil_size {
      background-color: #fff!important;
      color: #000 !important
  }

  #toolbar,
  .pop_title,
  #sSearchContainer,
  #sChatBox_footer,
  .bChatTitle,
  .bChatInputContainer {
      background: rgba(0, 0, 0, 0) linear-gradient(to bottom, rgba(70, 70, 70, 1) 0, rgba(30, 30, 30, 1) 100%) repeat scroll 0 0 !important
  }

  #show_more_posts,
  #sChatBox_close {
      background: rgba(0, 0, 0, 0) linear-gradient(to bottom, rgba(130, 130, 130, 1) 0, rgba(70, 70, 70, 1) 100%) repeat scroll 0 0 !important
  }

  #container {
      background-color: rgba(255, 255, 255, .15) !important
  }

  .bubble .arrow {
      background-color: rgba(0, 0, 0, 0) !important;
      width: 0 !important;
      height: 0 !important
  }

  #MyQuestion,
  #OtherQuestionsContainer,
  .SecondColumn,
  #MyQuestionDisplay,
  .MyAnswerDisplay,
  #ReplyContainer,
  #TypingReply,
  #ParticipantPreview,
  #topbar,
  div .NotClicked,
  #my_post_container,
  #need_join,
  .post,
  #profile_left,
  #profile_right_top,
  .profile_right_bottom,
  .white_box,
  .viewers,
  .photoPopup,
  .photoPopup_mainPost,
  .profilePost_likelist,
  .pop,
  .msg,
  #sChatBox_friendsWrapper,
  .bChatMessageContainer,
  #find_friends,
  #discover_groups,
  .profileMenu,
  #people_container,
  #people_container .status,
  .empty {
      background: #333 !important;
      border-color: #333 !important
  }

  div.Clicked,
  div.NotClicked {
      box-shadow: 0 0 0 1px #333 inset !important
  }

  .QuestionsVisitor {
      border-color: #3e3857 !important
  }

  .vChat_msg .msg::after {
      border-color: transparent #333 !important
  }

  button,
  .Questions,
  div.Clicked,
  div.NotClicked:hover,
  .profilePost,
  .activeTab,
  .profileMenu:hover,
  .sChatBox_status,
  .bubble .text,
  .dropDown_content,
  .empty,
  .group_item,
  .minitab,
  #UserQuestionsButton,
  .notifBubble,
  .profile_about_list_option[data-hover="true"],
  .choose_subject_block,
  .menu,
  #people_container .person,
  .dropDown_content.empty,
  .profilePost_likelist,
  .profilePost_options_block,
  .MathJax_Menu,
  .profile_friend_profile:hover {
      background: #475a7a !important;
      border-color: #333 !important
  }

  .bubble .arrow {
      border-top-color: #525870 !important
  }

  .QuestionsUsersPic, .profile_photo {
      border: 3px solid #40536a !important
  }

  #profilePic {
      border: 5px solid #40536a
  }

  #topbarProfile {
      border: 4px solid #40536a !important
  }

  .QuestionsUsersLevel {
      border: 2px solid #40536a !important
  }

  .sChatBox_friends:hover {
      background: #666 !important
  }

  .tChatButton {
      background: #666 none repeat scroll 0 0 !important
  }

  .tChatButtonFlash {
      background: #008abd none repeat scroll 0 0 !important
  }

  .admin_list:hover,
  .member_list:hover,
  #levelButton:hover,
  #onlineFriends:hover,
  .view_group:hover .menu_arrow:hover {
      background-color: #aaa !important;
  }

  .menu_arrow,
  #topbarDonate input {
      background-color: #333!important;
  }

  #topbar {
      box-shadow: 0 4px 2px -2px rgba(0, 0, 0, 0.3);
  }

  .MathJax_Menu {
      box-shadow: 0 1px 1px black !important;
  }

  #profileMenu_wall img {
      content: url("https://i.imgur.com/iJJGkPN.png") !important;
  }

  #profileMenu_photos img {
      content: url("https://i.imgur.com/Y8jYk7B.png") !important;
  }

  #profileMenu_friends img {
      content: url("https://i.imgur.com/UaQ9nTZ.png") !important;
  }

  #profileMenu_about img {
      content: url("https://i.imgur.com/4RsGGEt.png") !important;
  }

  .profile_friend_profile {
      border-color: #475a7a !important;
  }

  .profilePost .menu_arrow {
      background-color: #475a7a !important;
  }

  .bChatMessageSeparator {
      background-image: -webkit-gradient(linear, left top, right top, color-stop(0, #333), color-stop(50, #ccc), color-stop(100, #333));

      background-image: -webkit-linear-gradient(left, #333 0%, #ccc 50%, #333 100%);

      background-image: linear-gradient(to right, #333 0%, #ccc 50%, #333 100%);
  }
}
