css:
@-moz-document domain('ellosnuncaloharian.com') {
  @font-face {
    font-family: 'PT Sans';
    font-style: normal;
    font-weight: 400;
    src: local('PT Sans'), local('PTSans-Regular'), url(https://fonts.gstatic.com/s/ptsans/v8/ATKpv8nLYAKUYexo8iqqrg.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
  }
  html > body {
    background-color: #ffffff;
    background-image: none;
    color: #333;
    font-family: 'PT Sans', sans-serif;
    font-size: 1.1em;
    line-height: 1.4em;
    /* This would be the comments */
  }
  html > body *[bgcolor="#CCCCFF"] {
    background-color: transparent;
    color: inherit;
  }
  html > body table {
    border: 0px solid transparent;
  }
  html > body input[type="submit"] {
    background-color: #93d4c8;
    border-radius: 5px;
    border: none;
    box-shadow: 2px 2px 8px rgba(0, 0, 0, 0.4);
    cursor: pointer;
    font-size: 1em;
    font-weight: normal;
    margin-left: 5px;
    margin-right: 5px;
    padding: 7px;
  }
  html > body input[type="submit"]:hover {
    box-shadow: 2px 2px 12px rgba(0, 0, 0, 0.5);
  }
  html > body input[name="USR"],
  html > body input[name="FORMPWD"] {
    width: 105px;
  }
  html > body #main {
    margin: 0 auto;
    width: 100%;
  }
  html > body #tabs {
    margin: 0;
    background-color: #5b78c4;
    box-shadow: 0 0 25px #000;
  }
  html > body #tabs ul li a {
    background-image: none;
    background-color: #253575;
  }
  html > body #tabs ul li a:hover {
    background-color: #253575;
    background-image: none;
  }
  html > body #tabs .box {
    margin: 0 auto;
    width: 1200px;
  }
  html > body #header {
    background-image: none;
    background-color: #5b78c4;
    width: 100%;
  }
  html > body #header #search {
    margin: 0 auto;
    position: relative;
    width: 1200px;
  }
  html > body #header #search #search-input {
    padding: 5px 0;
    margin: 0 auto;
    border-radius: 15px;
  }
  html > body #header #search #search-input-out {
    background-image: none;
  }
  html > body #header::after {
    content: '';
    background-image: url('data:image/gif;base64,');
    display: block;
    font-family: VT323;
    font-size: 3em;
    height: 35px;
    left: 50%;
    margin-left: -600px;
    position: absolute;
    text-align: left;
    top: 50px;
    width: 621px;
  }
  html > body #footer {
    width: 100%;
  }
  html > body #footer center {
    margin: 0 auto;
    width: 1200px;
  }
  html > body #page {
    background-color: transparent;
    margin: 0 auto;
    padding: 5px;
    width: 1200px;
  }
  html > body #page,
  html > body #page-in,
  html > body #col,
  html > body #footer {
    background-image: none !important;
  }
  html > body #col h3 {
    background-color: #A0B9F3;
    background-image: none !important;
  }
  html > body #col.noprint {
    background: #ffffff;
    background-image: none !important;
  }
  html > body #col-in {
    background-image: none;
  }
  html > body #content {
    width: 980px;
  }
  html > body #content .article {
    background-image: none;
    padding-bottom: 0;
  }
  html > body #content .article h1 {
    font-size: 3em;
    text-align: center;
  }
  html > body #content .article h2 {
    background-image: none;
    color: #8490CA;
  }
  html > body #content .article .date {
    font-size: .8em;
  }
  html > body #content .user {
    font-size: .8em;
    text-align: center;
    display: block;
  }
  html > body #content table tbody tr td center h1 a {
    text-decoration: none;
  }
  html > body #content > center {
    padding: 2em;
    text-align: left;
  }
  html > body #content > center div[style="text-align:left;"],
  html > body #content > center p {
    text-align: justify;
  }
  html > body #content > center table[width='100%'] {
    padding: 20px;
    display: block;
    border-radius: 10px;
  }
  html > body #content > center > form {
    text-align: center;
    font-size: 0.8em;
  }
  html > body #content > center > form input[type='text'] {
    margin-left: 10px;
  }
  html > body .FINF {
    color: inherit;
    font-family: inherit;
    font-size: 1em;
  }
  html > body input[type='text'],
  html > body input[type="password"],
  html > body .FDES {
    color: inherit;
    font-family: inherit;
    font-size: 1em;
    padding: 3px;
  }
  html > body #content center > table[cellpadding="4"] {
    background-color: #eee;
    border-radius: 10px 0 25px;
    font-weight: normal;
  }
  html > body #content center > table[cellpadding="4"]:nth-child(odd) {
    background-color: #ccc;
  }
  html > body #content center > table[cellpadding="4"] > tbody > tr:first-child {
    font-size: .8em;
  }
  html > body .FormText {
    font-size: 1em;
    background-image: none;
  }
}
