css:
@-moz-document domain("pan.baidu.com") {
/* TOP NAV BAR */
.module-header-wrapper {
	background: #F0F0F0 !important;
}

.module-header {
    background: #F0F0F0;
    border-bottom: 1px solid #ddd;
    box-shadow: none;
}

.module-header .logo {
    margin: 7px 0 0 15px;
    background-image: url("http://cdn.iszu.cn/baidudisk/logo.png") !important;
    background-size: 100%
}

.module-header .navs {
    padding: 0 0 3px 25px;
    height: 49px;
    float: left;
    width: 400px;
    padding-bottom: 0!important;
}

.module-header .navs .separate {
    display: none;
}

.module-header .navs a:hover, .module-header .navs a:active {
    text-decoration: none;
    background-color: rgba(0, 0, 0, .04);
}

.module-header .navs .def-nav, .module-header .navs .pulldown-nav, .module-header .navs .separate {
    font-family: 'Lantinghei SC', 'Open Sans', Arial, 'Hiragino Sans GB', 'Microsoft YaHei', 微软雅黑, STHeiti, 'WenQuanYi Micro Hei', SimSun, sans-serif;
    height: 50px;
    line-height: 50px;
}

.module-header .navs .current, .module-header .navs .current .pulldown-nav {
    font-weight: normal;
    color: #337efc !important;
    display: inline-block;
    height: 50px;
    background-color: rgba(0, 0, 0, .02);
    text-align: center;
    line-height: 49px;
}

.module-header .navs .def-nav .app-url {
    left: 150px;
    top: 50px;
    width: 640px;
}

.module-header .pulldown .content {
    border-radius: 0 0 3px 3px;
    border: #ddd 1px solid;
    border-top: none;
    box-shadow: 0 3px 3px rgba(0, 0, 0, 0.05);
}

.module-header .info .info-i {
    font-family: 'Lantinghei SC', 'Open Sans', Arial, 'Hiragino Sans GB', 'Microsoft YaHei', 微软雅黑, STHeiti, 'WenQuanYi Micro Hei', SimSun, sans-serif;
	color: #333;
}

.module-header .search-form {
    float: left;
    height: 30px;
    border: none;
    background-color: transparent;
    margin-top: -6px;
    _margin-top: -4px;
}

.module-header .search-form .search-query {
    border: #ddd 1px solid;
    width: 140px !important;
    height: 26px;
    line-height: 26px;
    padding-left: 6px;
    border-radius: 3px 0 0 3px;
    color: #333;
    transition: all 300ms;
   	background-color: #FFF;
    border-right: none;
}

.module-header .search-form .search-query:hover,
.module-header .search-form .search-query:focus {
	border-color: #2d70df;
}

.module-header .search-form .search-query:focus {
    width: 200px !important;
    transition: all 600ms ease;
}

.module-header .search-form .input-clear {
    display: none;
    z-index: 3;
    position: absolute;
    height: 22px;
    width: 22px;
    line-height: 22px;
    top: 4px;
    right: 50px;
    cursor: pointer;
}

.module-header .search-form .search-button {
    width: 48px;
    height: 30px;
    background: #337efc;
    border: 1px solid #2d70df;
    border-left: none;
    border-radius: 0 3px 3px 0;
}

.module-header .search-form:after {
	position: absolute;
    right: 10px;
    content: '搜 索';
    display: block;
    color: #FFF;
    visibility: visible;
    font-size: 12px;
    height: 30px;
    width: 48px;
    line-height: 30px;
    z-index: 10;
    pointer-events: none;
}

.module-header .search-form .input-placeholder {
    position: absolute;
    top: 6px;
    _top: 8px;
    left: 8px;
    color: #999;
    text-align: left;
    cursor: text;
    z-index: 4;
    pointer-events: none;
}

.module-header .info .no-vip {
    padding-left: 5px;
}

.module-header .info .user-info, .module-header .info .more-info {
    top: 20px;
    left: auto;
    right: -24px;
    width: 116px;
}

.module-header .info .more-info {
    top: 20px;
    left: auto;
    right: 10px;
    width: 116px;
}

/* 隐藏广告栏 客户端下载 会员等按钮 */
.module-header .info .ad-platform-tips,
.module-header .info .user-name .identity,
.module-header .info .info-i:nth-child(4),
.module-header .info .info-i:nth-child(5){
	display: none;
}


/* LEFT MENU */
.module-aside {
    background: #f0f0f0;
    padding-top: 10px;
    border-right: #ddd 1px solid;
    height: 100%;
    position: relative;
}

.module-aside .menu-list a,
.module-aside .b-list-item a{
    display: block;
    position: relative;
    height: 36px;
    line-height: 36px;
    padding: 1px 0;
    font-size: 14px;
    text-decoration: none;
    zoom: 1;
    color: #333;
    margin: 3px 0;
}

.module-aside .menu-list .active a,
.module-aside .b-list-3 .active a {
    height: 36px;
    line-height: 36px;
    background: rgba(0, 0, 0, .1) !important;
    _text-decoration: none;
    border-left: 2px solid #337efc;
}

.module-aside .menu-list a:hover,
.module-aside .b-list-item a:hover {
    height: 36px;
    line-height: 36px;
    background: rgba(0, 0, 0, .05);
    _text-decoration: none;
}

.module-aside .menu-list .img-ico {
    top: 8px;
    background-image: url("http://cdn.iszu.cn/navigator.png");
    background-repeat: no-repeat;
    background-size: 45px 221px;
}

.item-separator {
    display: none;
    margin: 5px 0;
    background: rgba(0, 0, 0, 0.1);
    border: none;
    height: 1px;
    _line-height: 0;
    _font-size: 0;
}

.middle-button-container a, .bottom-button-container a {
    margin: 0;
    display: block;
    height: 36px;
    line-height: 36px;
    padding: 1px 0;
    font-size: 14px;
    text-decoration: none;
    zoom: 1;
    color: #333;
    background: 0 0;
}

.middle-button-container a:hover, .bottom-button-container a:hover {
    background: rgba(0, 0, 0, .05);
    text-decoration: none;
}

.middle-button-container a .g-button-right, .bottom-button-container a .g-button-right, .middle-button-container a:hover .g-button-right, .bottom-button-container a:hover .g-button-right {
    height: 36px;
    line-height: 34px;
    padding-left: 30px;
}

.icon-aside-share {
    background-image: url("http://cdn.iszu.cn/navigator.png");
    background-repeat: no-repeat;
    background-size: 45px 221px;
    background-position: 0 -130px;
}

a:hover .icon-aside-share {
    background-position: -23px -130px;
}

.icon-aside-recyclebin {
    background-image: url("http://cdn.iszu.cn/navigator.png");
    background-repeat: no-repeat;
    background-size: 45px 221px;
    background-position: 0 -150px;
}

a:hover .icon-aside-recyclebin {
    background-position: -23px -150px;
}

.module-aside .side-options {
    visibility: hidden !important;
    height: 120px;
}

.aside-absolute-container {
    position: absolute !important;
    top: auto !important;
    left: 0;
    bottom: 0 !important;
}

.remainingSpaceUi_span {
	background: #337efc !important;
}

/* RIGHT BODY */

.module-toolbar {
    position: relative;
    z-index: 4;
    border-bottom: 1px solid #ddd;
    background: #F6F6F6;
}

.module-toolbar .list-grid-switch a {
    height: 29px;
    width: 32px;
    float: left;
    background: url("http://cdn.iszu.cn/list-switch2.png");
    background-repeat: no-repeat;
    background-size: 65px 98px;
}

.module-list {
    padding: 0 10px;
}

.list-header ul {
    background: #F8F8F8;
    border-radius: 2px;
    border: 1px solid #ddd;
    color: #888;
    height: 41px;
    line-height: 41px;
    overflow: hidden;
    font-size: 14px;
}

.list-header .col {
    float: left;
    border-left: none;
    border-right: 1px solid #ddd;
    cursor: pointer;
    text-indent: 10px;
}

.list-header .col:hover {
    background: none;
}

.list-header .check-icon {
    background-image: url(http://cdn.iszu.cn/list-view.png);
    background-position: -9px -12px;
    background-repeat: no-repeat;
    background-size: 67px 59px;
}

.list-header .order-icon {
    background-image: url(http://cdn.iszu.cn/list-view.png);
    background-position: -50px -45px;
    background-repeat: no-repeat;
    background-size: 67px 59px;
}

.module-list-view .list-view-item .checkbox {
    background-image: url(http://cdn.iszu.cn/checkbox.png);
    background-repeat: no-repeat;
    background-size: 54px 60px;
}

.dir-small,
.dir-app-small,
.fileicon-sys-s-exe,
.default-small{
    background-image: url(http://cdn.iszu.cn/listIcon.png);
    background-repeat: no-repeat;
    background-size: 240px 77px;
}

.fileicon-small-bt,
.fileicon-small-zip,
.fileicon-small-mp3,
.fileicon-small-doc,
.fileicon-small-video,
.fileicon-small-ipk {
	background-image: url(http://cdn.iszu.cn/listIcon_82b617a.png);
    background-repeat: no-repeat;
    background-size: 414px 30px;
}

.dir-small {
    background-position: 0 -30px;
}

.dir-apps-small, .dir-app-small {
    background-position: -60px -30px;
}
.fileicon-small-doc {
    background-position: -60px 0;
}
.fileicon-small-video {
    background-position: -210px 0;
}
.default-small {
    background-position: 0 0;
}
.fileicon-small-bt {
    background-position: -270px 0;
}
.fileicon-small-zip {
    background-position: -240px 0;
}
.fileicon-sys-s-exe {
    background-position: 0 -60px;
}
.fileicon-sys-s-apk {
    background-position: -60px -60px;
}
.fileicon-small-mp3 {
    background-position: -180px 0;
}

/* TIPS */
.module-tip {
    position: absolute;
    padding: 0 8px;
    top: 90px !important;
    background: #f9edbe;
    border: 1px solid #fbda91;
    border-radius: 4px;
    -moz-box-shadow: 1px 2px 5px rgba(0,0,0,.5);
    -webkit-box-shadow: 1px 2px 5px rgba(0,0,0,.5);
    box-shadow: 0 2px 5px rgba(0, 0, 0, .2);
    z-index: 95;
}

/* DIALOG */
.dialog {
    position: absolute;
    border: 2px solid #aaa;
    background: #fff;
    box-shadow: 0 0 9px rgba(0, 0, 0, .2);
    -webkit-transtion: bottom .1s;
    overflow: hidden;
    display: none;
    border-radius: 3px;
}

.offline-list-dialog .headdiv .new-magnet-tips {
    height: 28px;
    line-height: 28px;
    display: inline-block;
    padding-left: 5px;
    color: #F45757;
}

/* SHARE PAGE */

.module-comment .btn-comment {
    color: #FFF !important;
}

li[data-key="vip-center"] {
	display: none !important;
}

.module-share-header .slide-show-header .slide-header-funcs {
    float: right;
    width: 100%;
    margin: 10px 0;
    text-align: right;
}

.btn-icon {
    display: block;
    height: 20px;
    width: 20px;
    background-image: url(http://cdn.iszu.cn/btn_icon.gif);
    background-repeat: no-repeat;
    background-size: 300px 760px;
}
.global-icon-transfer, .global-icon-download, .global-icon-videoplay-save, .global-icon-delete, .global-icon-share-cancel, .global-icon-share-qr, .global-icon-checkbox {
    display: inline-block;
    width: 20px;
    height: 20px;
    background-image: url(http://cdn.iszu.cn/btn_icon.gif);
    background-repeat: no-repeat;
    background-size: 300px 760px;
}

.icon-share-qr {
    top: 6px !important;
    background-position: -208px -441px;
    left: 11px !important;
}

.global-icon-share-cancel {
    top: 6px !important;
}

.tui_icon, .share_icon, .sina_icon, .save_icon, .download_icon, .quick_save_icon {
    background-image: url(http://cdn.iszu.cn/thumb_icon.png);
    background-size: 238px 200px;
    background-repeat: no-repeat;
}

.global-icon-62 {
    background-image: url(http://cdn.iszu.cn/property_icon_101.png);
    background-size: 3000px 300px;
    background-repeat: no-repeat;
}

#bd {
    border: none;
    box-shadow: 0 5px 10px rgba(0, 0, 0, .1);
    border-radius: 5px;
    overflow: hidden;
}

.module-comment .description .area {
    padding: 5px 10px !important;
    overflow: auto;
    width: 100%;
    height: 80px;
    border: 1px solid #ddd;
    word-break: break-all;
    resize: none;
    border-radius: 3px;
    font-size: 14px;
    outline: 0;
}

.module-hot-rec {
	visibility: hidden !important;
}

.module-share-footer a:link, .module-share-footer a:visited, .module-share-footer a:hover, .module-share-footer a:active, .module-share-footer div {
    padding: 0 10px;
    color: #aaa !important;
    font-family: 'Lantinghei SC', 'Open Sans', Arial, 'Hiragino Sans GB', 'Microsoft YaHei', 微软雅黑, STHeiti, 'WenQuanYi Micro Hei', SimSun, sans-serif;
}
}
