css:
@-moz-document domain("msdn.microsoft.com"), domain("social.msdn.microsoft.com"), domain("visualstudio.com"), domain("answers.microsoft.com") {
body,
p,
html > body,
#page,
input,
.shell-header,
div#tocnav div.tocselected,
#BodyBackground,
#Search,
#instantAnswer .examples .code,
.code,
#simplemodal-container,
#ux-footer,
#MainContent,
th,
#Fragment_HeroHeadlines,
.heropanel,
div#ux-footer,
.ResultItemSourceRefinement,
.inner,
#breadcrumbDropDownMenu,
#breadcrumbDropDownMenu>ul>li.current,
#JelloWrapper,
.tabHeaders div,
.tabHeaders .current,
#bodyContainer,
table #mainContentTable,
#sideNav,
#directoryList,
.gallery-home-page-view .tab-collection,
.homepage-searchbox-wrapper .homepage-searchbox-container,
.gallery-item-card,
.category-entry,
.item-details-main-content,
.searchbar,
#msdn-technet-en-thread #threadPageContainer li.message div.menu.thread,
#msdn-technet-en-thread #threadPageContainer li.message div.menu.message,
div.expandedThreadContainer li.message div.menu.thread,
div.expandedThreadContainer li.message div.menu.message,
.hljs,
.section div.alert,
li.message,
.or-t-ml .or-c-pf,
.toc .selectedHolder>ul,
.codeHeader,
#ux-header header div.toc nav ul.navL2,
#ux-header header div.toc nav ul.navL3 {
    background-color: #181818 !important;
    background: #181818 !important;
}
body,
.topic h1.title,
td,
th,
p,
.communityContentAnnotation,
#page,
input,
.codeSnippetContainerCode div,
#BodyBackground,
#Search,
.Msdn #instantAnswer,
.search .ResultDescription,
.pln,
div#ux-footer,
div#ux-footer footer.top .linkListTitle,
div#ux-footer #Fragment_LeftLinks>.linkList>ul>li>a,
#Fragment_Features .block .textContent .link,
#Fragment_Features .block .textContent .linkText,
.breadCrumb,
.breadCrumb>span>a,
.breadCrumb>span>a:link,
.breadCrumb>span>a:hover,
.breadCrumb>span>a:visited,
h1.heading,
h2,
h2.heading,
h2.subheading,
h3,
h3.subheading,
h4,
h4.subheading,
h5,
h5.subheading,
.LW_CollapsibleArea_Title,
#DiscussionsTabPane .threadHeader,
#DiscussionsTabPane .threadHeader .title,
.tabHeaders .current,
.EyebrowElement,
#sideNav,
.sidebar h3,
#sideNav h3,
.gallery-item-cluster .header-container .section-header,
.gallery-item-cluster .header-container a.disabled,
.gallery-item-cluster .header-container a.section-header,
.gallery-item-cluster .header-container a.section-header:visited,
.gallery-item-cluster .header-container a.section-header:active,
.category-entry a,
.item-details-control-root .markdown,
.meta-data-list-container .ux-section-meta-data-list a,
.hljs,
.pdfDownloadHolder a:link,
.pdfDownloadHolder a:visited,
.toc a:link,
.toc a:visited,
.codeHeader {
    color: #C0C0C0 !important;
}
.codeSnippetContainerCode div span,
.kwd,
.hljs-keyword {
    color: #0099CC !important;
}
.typ {
    color: #9900CC !important;
}
.hljs-string,
.hljs-title,
.hljs-parent,
.hljs-tag .hljs-value,
.hljs-rule .hljs-value,
.ruby .hljs-symbol,
.ruby .hljs-symbol .hljs-string,
.hljs-template_tag,
.django .hljs-variable,
.hljs-addition,
.hljs-flow,
.hljs-stream,
.apache .hljs-tag,
.hljs-date,
.tex .hljs-formula,
.coffeescript .hljs-attribute,
.hljs-name {
    color: #ff9966 !important;
}
h1,
h2,
h3,
h4,
dt {
    color: white !important;
}
.shell-footer {
    background: #181818;
}
.topic a:active,
.topic a:visited,
div#MainContent a:active,
div#MainContent a:visited {
    color: #6600CC;
}
/*
ALERT
*/

div.alert,
div#tocnav div.tocselected,
.subMenu,
#projectPage #screenshot tr+tr td,
#reviewList .oddRow,
#UserReview,
#DiscussionsTabPane .threadHeader,
#JelloSizer,
.highlighted,
#directoryContent .browseFilterBar,
div#tocNav div.tocSelected,
div#tocDropdownMenu div.tocSelected,
.table-striped>tbody>tr:nth-of-type(odd),
.gallery-home-page,
.gallery-home-page-view .tab-content,
.main-content.category-main-content,
.hero-section,
.hero-section p,
li.message div.body div.quote,
li.message div.body div.quote p,
#threadsearchbarContainer .searchbar .searchboxinnercontainer,
.searchboxinnercontainer #searchBoxTd input #searchbox,
.alert>p.lf-text-block.lf-block {
    background: #202020 !important;
}
#breadcrumbDropDownMenu,
#directoryContent .browseFilterBar,
.subMenu {
    border: 1px solid #424242 !important;
}
.tabHeaders .current,
div#tocNav div.tocSelected,
div#tocDropdownMenu div.tocSelected {
    border-bottom: solid 1px #181818 !important;
}
#categorySelector li,
#sideNav .section ul li,
#sideNav .section form>div {
    border-bottom: 1px solid #424242;
}
#categorySelector .unselected .categoryRow,
#categorySelector .selected .categoryRow {
    border-bottom: 1px solid #666666;
}
.footerLinksContainer,
.footerSubLinksContainer {
    border-top: 1px solid #424242;
}
.updates-banner,
.gallery-home-page-view .tab-collection-container,
.item-details-control-root .ux-section-banner,
.category-container .filters-container {
    background: #404040 !important;
}
pre,
code {
    background: #202020 !important;
    color: #C0C0C0 !important;
}
.gallery-item-card .text-fadeout {
    background: linear-gradient(to right, rgba(255, 255, 255, 0), #181818);
}
.gallery-item-card,
.category-entry {
    border: 1px solid #202020;
}
.tab-collection .tab:nth-child(1) {
    border-left: 1px solid #353535;
}
.tab-collection .tab {
    border-right: 1px solid #353535;
}
.gallery-home-page-view .tab-collection-container {
    border-bottom: 1px solid #353535;
}
/* Fix for bright borders on content tables */

div#content table,
div#content th,
div#content td {
    border: 1px solid rgb(50, 50, 50);
}
/* Fix for weird syntax table */

.codeSnippetContainerCodeContainer {
    border: 1px solid rgb(80, 80, 80);
}
.codeSnippetContainerTab,
.codeSnippetContainerTabActive {
    border-top: solid 1px rgb(70, 70, 70);
    border-right: solid 1px rgb(70, 70, 70);
}
.codeSnippetContainerTabActive {
    border-bottom: 2px solid rgb(70, 70, 70);
    background-color: rgb(30, 30, 30);
}
.codeSnippetContainerTabActive a {
    color: #0072c6;
}
.codeSnippetContainerTabs > div:last-child {
    border-right: solid 1px rgb(70, 70, 70);
}
.codeSnippetContainerTabs > div:first-child {
    border-left: solid 1px rgb(70, 70, 70);
}
.resourceblock>p {
    background-color: inherit !important;
}
}
