css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('http[s]{0,1}:\/\/([a-z]+\.avature\.net|iats\-[a-z0-9]+\.local(host){0,1}|(obdb){0,1}qa[0-9]+\.xcade\.net)\/.*') {

.crmui_SidebarItem_IconCase .crmui_SidebarItem_iconHolder, .crmui_SidebarItem .crmui_SidebarItem_iconHolder {
    padding: 0;
    margin: 0;
}

.crmui_SidebarMenu_TabsWrapper .crmui_SidebarItem_labelText {
 	overflow: visible;
    text-overflow: unset;
    padding: 2px 6px;
    white-space: pre-wrap;
}

.SmallSidebarItems .crmui_SidebarItem, .crmui_SidebarItem.crmui_SidebarItem_Selected {
 	padding-left: 0;   
}

.crmui_SidebarItem.crmui_SidebarItem_Selected {
    border-left: 0;
    border-right: .5rem solid #46ce7f;
}

.crmui_SidebarItem {
    padding: 0.1rem;
}

.crmui_DragHint {
    display: none;
}

}
