css:
@-moz-document url-prefix("https://www.liveinternet.ru/stat") {
a {
    color: black;
    font-size: 16px;
    line-height: 20px;
    padding: 5px;
     text-decoration:none
}
input[type="submit"] {
    border: lightblue;
    padding: 15px;
    border-radius: 10px;
    background-color: lightblue;
    font-size: 18px;
    font-family: sans-serif;
    margin: 5px;
}
html body center table tr td:nth-child(1) div a font {
    color: #b3b3b3;
    background-clip: content-box;
    background-color: #ffffff;
    border-width: 0;
    padding: 0px;
    text-shadow: 1px 1px 1px black;
    border-radius: 5px;
}
a:hover {
    padding: 5px;
    border-radius: 5px;
    background-color: #deedff;
}
.bc2 {
    background: gray;
    color: white;
    width: 136px;
    border: solid;
    border-width: 5px;
    border-color: grey;
    border-radius: 7px;
}
td {
    font: 16px 'Arial', 'Verdana', sans-serif;
    line-height: 25px;
}
html body center table tr td:nth-child(5) {
    display: none;
}
html body center table tr td p table {
    width: 100%;
}
html body center table tr td p table:nth-child(2) tr td {
    border: 1px;
    border-color: #ffffff;
    border-style: groove;
}
img {
    width: 100%;
    height: 150%;
}
html body center table {
    width: 95%;
}
html body center table tr td p table:nth-child(2) {
    border-collapse: collapse;
}
td font {
    font-size: 16;
    background-color: lightblue;
    padding: 5px;
    border-radius: 7px;
}
html body center table tr td:nth-child(1) div {
    width: 100% !important;
    float: left;
}
a[href*="mastertalk.ru"],
embed,
span[id*="ban"],
a[onclick*="ppc"],
iframe[src*="yadro"],
span[id*="banstat"],
span[id*="rtm"],
img[src="/gg/clik.gif"] { display: none !important; }

}
