css:
@-moz-document regexp(".*/jira/secure/RapidBoard.jspa.*") {
  div.ghx-highlighted-fields {
    display: none;
  }

  div.ghx-issue-content {
    min-height: initial !important;
  }

  div.ghx-flags {
    display: none;
  }

  div.ghx-days {
    display: none;
  }

  div.ghx-issue-fields div.ghx-key {
    display: none;
  }

  div.ghx-issue div.ghx-avatar {
    right: 2px;
    top: 2px;
  }

  div#ghx-header {
    padding: 5px;
  }

  div#ghx-operations {
    padding: 0 5px;
  }

  li.ghx-column {
    padding: 0 !important;;
  }

  div.ghx-heading {
    margin: 3px !important;;
  }

  dt.ghx-detail-term {
    padding-top: 0;
    padding-bottom: 0;
  }

  dd.ghx-detail-description {
    padding-top: 0;
    padding-bottom: 0;
  }

  div.ghx-detail-section h4 {
    margin-top: 10px;
  }

  div.ghx-header {
    margin-top: 10px !important;
  }

  div.ghx-detail-section {
    margin-bottom: 10px;
  }

  div.ghx-quick-content.aui-expander-content {
    margin-bottom: 2px;
  }

  div.ghx-controls-filters dd {
    margin-bottom: 2px;
  }

  div.ghx-controls-work {
    min-height: initial;
  }

  .ghx-swimlane.ghx-closed .ghx-swimlane-header .ghx-expander .ghx-iconfont {
    margin-top: 4px;
  }

  .ghx-swimlane-header .ghx-expander .ghx-iconfont {
    margin-top: 4px;
  }

  div.qf-field {
    padding-top: 0;
    padding-bottom: 0;
  }

  form.aui .field-group {
    padding-top: 2px;
    padding-bottom: 2px;
  }

  .ghx-issue.ghx-has-avatar .ghx-issue-fields {
    padding-right: 33px;
  }

  .ghx-issue .ghx-end {
    top: 6px;
    left: 9px;
    right: auto;
  }

  .aui-badge {
    min-width: 16px;
  }

  .ghx-column-headers .ghx-column {
    border-bottom: 0;
  }
}
