css:
/* i really want this to be global */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.gadu-gadu"), url-prefix("http://gadu-gadu"), domain("gg.pl") {
body, #sr-base-window, .sr-contacts-group-title, .sr-contacts-group, .x-component .base-component .x-component-default, select, textarea, .settings-container .tab-item, .popup-content, .menu,#sr-logo A.active,#sr-account-btn,#sr-logo A.active .spacer
{ background-color: #000 !important; color: orange !important; }
#sr-logo A{background:transparent;}
.sr-menu-popup UL.menu LI:hover, .sr-menu-popup UL.menu LI:active
{ background-color: #634003 !important; color: orange !important; }

[id^="ext"], [id^="ext-gens"], .sr-menu-popup UL.menu LI span
{ color: orange!important; }
.sr-menu-popup{ border: 1px orange solid;background:black}
.btn-add.active
{ background: url(http://www.gg.pl/images/icons.png) no-repeat -55px -527px!important; }

[style*="background-color:#fcfc9a"], .profile-close
{ background-color: #000!important; }

.profile-close:hover, .user-data-links-opt-link:hover
{ background-color: #634003!important; height: 100%; }

.header .title
{ text-shadow: none!important; }

input
{ background-color: #000 !important; color: orange !important; border: 1px orange solid; border-radius: 5px!important; padding: 5px 10px 5px 10px!important; }

label, #sr-contact-list-panel, #sr-contact-list-search
{ background-color: #000 !important; color: orange !important; }

.search-input
{ margin-top: 1px; background: transparent!important; }

.uiBtn LABEL
{ background: transparent!important; }

div, tr, td, th, p, a, .show-meta, .show-name, .user-data-links-opt-link, .profile-close
{ color: orange!important; }

a:hover
{ color: #FF9C40!important; }

span
{ color: #634003!important; }

.sr-contact
{ border: none; }

.sr-contact:hover
{ background: darkgrey!important; }

.sr-contact-name
{ color: orange!important; }

.x-tip, #sr-notifications-widget, #iframe_permanentStatus, #sr-subheader, #logo-container
{ background: #000!important; }

.sr-contact-name span, #logo, #main, footer, #iframe-sr-base-window, #iframe-sr-advert-container
{ display: none }

#loginView.frame
{ border: 1px solid orange; background: #000!important; color: orange!important; }

#sr-main-center div, #sr-navbar-wrapper div, th, tr, .chat-tr, .item-body-text span
{ background: black!important; color: orange!important; }

.min-button i, .close-button i, .min-button, .close-button
{ z-index: 9999; width: 10px; height: 10px; padding: 5px 5px 5px 5px; }

.min-button i, .close-button i
{ top: 0px!important; left: 0px!important; padding: 5px 5px 5px 5px; width: 10px; height: 10px; border: 1px aqua solid }

.min-button i
{ background: lightblue }

.close-button i
{ background: darkred }

.min-button i:hover
{ background: blue; }

.close-button i:hover
{ background: red; }

#content #downloadBtn
{ display: block!important; }

.main-navigation
{ display: none; }
}
