css:
@-moz-document domain("mydealz.de") {
a,
button,
input,
.button {
    transition: all .275s ease-in-out;
}
.topBar,
.section--cyber-monday,
.inline-newsletter,
.top-sixty-merchants,
.sideSection[data-track='{"category":"Banner Sidebar"}'],
.sideSection[data-track='{"category":"Newsletter sign up"}'],
.top-sixty--merchants-hide,
#idealo {
    display: none !important;
}
.search-input {
    height: 50px;
    border: 0;
    border-radius: 0;
    font-size: 14px;
    background-color: #153961;
    color: #fff;
}
.search-button {
    box-shadow: none;
    background: transparent;
    top: 16px;
}
.navTrigger1-row,
.navTrigger1,
.navTrigger2-row,
.navTrigger2,
.nav2Morph-link,
.nav2Morph-link--alt {
    background-image: none !important;
    border: 0;
    box-shadow: none;
}
.navTrigger1-row {
    background-color: #1B4B7E;
}
.navTrigger2-row {
    background-color: #4A9D1D;
}
.navMenu2-item.navMenu2-item--selected {
    border: 0;
}
.nav2Morph-link-bigicon {
    padding: 14px 16px;
}
.userBar-userCenter,
.menu-list {
    box-shadow: 0 3px 6px 0 rgba(0, 0, 0, 0.125);
}
.userBarMenu-item {
    background: #ebebeb;
}
.userBarMenu-item:focus,
.userBarMenu-item:hover,
.userPopOver-footer {
    background: #f9f9f9;
}
.degreeVote-up,
.degreeVote-down,
.button {
    border: 0;
    border-radius: 2px;
    background: #ebebeb;
    text-shadow: none;
}
.button:hover,
.button:focus {
    background: #f4f4f4;
}
.button--type-primary {
    background: #225ba0;
}
.button--type-primary:hover,
.button--type-primary:focus {
    background: #2b67b0;
}
.button--type-link {
    background: #439719;
}
.button--type-link:hover,
.button--type-link:focus {
    background: #57bd32;
}
.sideList-item {
    border-top: 0;
}
.sideList-item--link {
    background: transparent;
    border-top: 0;
}
.sideList .button {
    background-color: #ddd;
}
.button--type-teaser {
    background: #e9680d;
}
.button--type-teaser:hover,
.button--type-teaser:focus {
    background: #fa8623;
}
.button--active,
.button--selected {
    background: #225ba0;
}
.button-icon {
    border-right: 0;
}
.button-group--pos-top>.button {
    background: #ddd;
}
.button-group--pos-top>.button.button--selected {
    background: #225ba0;
}
.degreeMeter {
    background: #ececec;
}
.degreeMeter--hot>.degreeMeter-progress {
    background: #d02626;
}
.degreeMeter--cold>.degreeMeter-progress {
    background: #536dbf;
}
.degreeVote-up.degreeVote-up--disabled,
.degreeVote-up.degreeVote-up--disabled:hover,
.degreeVote-up.degreeVote-up--disabled:focus,
.degreeVote-down.degreeVote-down--disabled,
.degreeVote-down.degreeVote-down--disabled:hover,
.degreeVote-down.degreeVote-down--disabled:focus {
    border: 0;
    background: #f5f5f5;
}
.degreeVote-up--active,
.degreeVote-up:hover,
.degreeVote-up:focus {
    border: 0;
    background: #c61416;
}
.degreeVote-down--active,
.degreeVote-down:hover,
.degreeVote-down:focus {
    border: 0;
    background: #5c79c3;
}
.thread-degree-meter {
    position: absolute;
    top: -20px;
    left: -20px;
    width: calc(100% + 41px);
    padding: 0;
    border-radius: 0;
    overflow: hidden;
}
.degreeMeter,
.degreeMeter-progress {
    height: 6px;
    border: 0;
    border-radius: 0;
}
.thread-degree {
    display: block;
}
.degreeValue ~ span,
.degreeValue {
    display: inline-block;
}
.thread--hasDegree {
    margin-top: 20px;
}
.thread--type-list {
    border-radius: 0;
    border: 0;
    margin-top: 15px;
}
.section--divided--strong {
    border-color: #e9eaed;
    border-width: 10px;
}
ol + .pagi.section {
    padding-left: 0;
    padding-top: 8px;
}
.pagi-item:first-child {
    margin-left: 0;
}
.thread-body .update {
    border-left: 4px solid #e9eaed;
}
.thread-body .update .ico--type-report-red {
    margin-left: -7px;
}
.footer {
    box-shadow: none;
}
.footer-content {
    padding: 1em 0;
}
.footer-pages-title {
    border: 0;
}
.footer .space--top-5 .space--right-2:before {
    content: 'userstyle by florativ.de | ';
}
.userCenter-head {
    background: #225ba0;
    border: 0;
}
.userBarMenu-item {
    background: #ebebeb;
}
.menu--active.menu--active--activity .userBar-button--activity:after,
.menu--active.menu--active--pm .userBar-button--pm:after,
.menu--active.menu--active--user .userBar-button--user:after {
    display: none;
}
.menu--active.menu--active--activity .userBar-button--activity:before,
.menu--active.menu--active--pm .userBar-button--pm:before,
.menu--active.menu--active--user .userBar-button--user:before {
    border-bottom-color: #225ba0;
}
}
