css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.kiva.org") {
body {
    background-color: #F3F3EB !important;
    font-family: Arial,Verdana,sans-serif !important;
    font-size:80% !important;
    color:#282828 !important;
}
div.message,textarea.wysiwyg,span.info_status {line-height:19px !important;}
.b {
    background-color: #FDFDFB !important;
}
.tabbedContent {
    border:#ddd 1px solid !important;
}
ol.teamMessages li.even.followsFeaturedMsg, .TeamActivityItemView, .mainWrapper {
    background-color: #FDFDFB !important;
}
.loancards-list li {
    background-color: #F7F7F2 !important;
}
.loan-card, .b, .left-off-canvas-menu, .filter, .filter-button-group-ul, .borrower-profile-wrapper {
    background-color: #FBFBF7 !important;
}
.small-texts, .message {
    /*font-size:80% !important;*/
}
.TeamActivityItemView {
    padding-left: 12px !important;
    padding-right: 12px !important;
    border-bottom: #ddd 1px solid;
    line-height:1.4em;
}
.TeamActivityItemView:nth-child(odd)  {
    background-color:#F7F7F2 !important;
}
.lenderActivityHeader {
    border:none;
    box-shadow:0px 0px 0px 0px !important;
    -webkit-box-shadow:0 !important;
    border-bottom: #ddd 1px solid;
}    
div.loan-message.urgent {
    width:200px !important;
    height:28px !important;
    background-color: #FDFDFB !important;
    font-size: 18px !important;
    border: #BE0027 2px solid !important;
    padding-top:2px !important;
}
.loan-description p, .introText p, #ac-loan-story-body p {
    font-size:14px !important;
    line-height: 18px !important;
}
.loan-use h2 {
    font-size:18px !important;
    line-height: 24px !important;
}
#ac-more-loan-info-body p {
    font-size:14px !important;
    line-height: 18px !important;
}
.mainWrapper .hPage {
    margin-top:6px;
}
/*a.button:not(.secondary):not(.filter-menu-button), button.lend-action-button, button.actNow {*/
a.button[kvtrackevent*="Add to"], button.lend-action-button, button.actNow {
    background: #61A03F !important;
    color: #FDFDFB !important;
    border-color:#488443 !important;
    box-shadow:0 0.125rem #488443 !important;
}
/*a.button:hover:not(.secondary):not(.filter-menu-button), button.lend-action-button:hover, button.actNow:hover {*/
a.button[kvtrackevent*="Add to"]:hover, button.lend-action-button:hover, button.actNow:hover {
    background-color: #488443 !important;
    color: #FDFDFB !important;
}
a.button[kvtrackevent*="Again"] {
    background: #f3703b !important;
    color: #FDFDFB !important;
    border-color:#f3703b !important;
    box-shadow:0 0.125rem #b53d0ce8 !important;
}
/*a.button:hover:not(.lend-button):not(.filter-menu-button) {*/
a.button[kvtrackevent*="Again"]:hover {
    background: #D84A0E !important;
    color: #FDFDFB !important;
    border-color:#D84A0E !important;
     box-shadow:0 0.125rem #BE410C  !important;
}
a.button[kvtrackevent*="Read"] {
    background: #800080 !important;
    color: #FDFDFB !important;
    border-color:#800080 !important;
    box-shadow:0 0.125rem #800080 !important;
}
/*a.button:hover:not(.lend-button):not(.filter-menu-button) {*/
a.button[kvtrackevent*="Read"]:hover {
    background: #590059 !important;
    color: #FDFDFB !important;
    border-color:#590059 !important;
     box-shadow:0 0.125rem #590059  !important;
}

a.button.msgReply {
    background:#eee !important;
    color:#222 !important;
}
a.button.msgReply:hover {
    background:#bbb !important;
    color:#222 !important;
}
a.helpTip {line-height: 2em}
#basket .pageWrap,
#thanks .pageWrap {
    background-color: #F7F7F2 !important;
}
#basket .main,
#thanks .main {
    background-color: #FDFDFB !important;
}
.impactType {
    margin-top:0px !important;
}
#ac-loan-tags-body p {
    font-size:14px !important;
    line-height:18px !important;
}
.list-view .loan-card-2-image-wrap {
    padding-left: 0.25rem !important; 
    padding-top: 0.25rem !important;
    padding-bottom: 0.25rem !important;
    width: 8rem !important;
}
.loan-card-2 .left-and-to-go-line {
    height: 1.25rem;
    font-size: 0.925rem;
    line-height: 1.25rem;
    color: #000 !important;
    font-style: normal;
    font-weight: bold;
    margin-bottom: 1.25rem;
}
.loan-card-2 .loan-message {
    display:block !important;
    color: #ea3b04 !important;
}
.list-view .short-loan-message {
    display:none !important;
}
.matching-line {
    margin-top:1rem !important;
}
.matching-line span {
    background:yellow;
}
}
