css:
@-moz-document domain("roblox.com") {
.icon-default-logo, .icon-logo {
    background-image: url(https://static.rbxcdn.com/images/NextStyleGuide/logo_ROBLOX_120x30.svg);
    background-repeat: no-repeat;
    background-size: auto auto;
    width: 118px;
    height: 30px;
    display: inline-block;
}

   .rbx-header .rbx-navbar-header {
    max-width: 163px;
    margin: 0 12px;
    float: left;
                              }

   .coverSprite-transitional {
    background-repeat: no-repeat;
    background-image: url('https://static.rbxcdn.com/images/NextStyleGuide/logo_ROBLOX_120x30.svg');
    background-size: 120px;
}

   .headerLeft > .rlogo {
    width: 118px;
    height: 37px;
    margin-top: 3px;
    margin-left: 150;
}

.ui-header h1.header-logo-only {
    background: url('https://m.roblox.com/Images/RobloxLogo.png') center center no-repeat;
}

div.SystemAlert {
    width: 906px;
    margin: 0 auto 5px;
    border: 1px solid gray;
    text-align: center;
    background-color: #F00;
    padding: 1px;
    position: relative;
    top: 5px;
}

.SystemAlert a {
    color: white;
}

.SystemAlertText {
    font-size: 16px;
    font-weight: bold;
    padding: 2px;
    background-color: #F00;
}

.sponsored-product-logo {
    background-image: url(http://betterblox.890m.com/r%20event.png);
}
.sponsored-product-logo {
    width: 220px !important;
    height: 40px !important;
    position: absolute !important;
    margin: 0 auto !important;
    right: 0px !important;
    left: 0px !important;
    bottom: 0px !important;
    top: 70px !important;
}
.icon-default-logo-r, .icon-logo-r {
    background-image: url(http://www.roblox.com/images/NextStyleGuide/logo_R_30X30.svg);
    background-repeat: no-repeat;
    width: 30px;
    height: 30px;
    display: inline-block;
    vertical-align: middle;
    background-size: 30px 30px;
}


}
