css:
@-moz-document url(https://mightytext.net/web8/) {

/*For IFCO-online Portal*/
/*Make the Site More User Friendly - Many of the forms are not even large enough */
/*Ship Log Forms - Increase "From" and "To" Dropdown Width */

.select_240 {
    width: 400px !important;
}
td.lieferdatumCol {
    min-width: 135px;
}
/*Reports (including stock and activity reports) */

.select_240_mehrzeilig {
    width: 100% !important;
    height: 200px;
}
.multiselectSearchInput_240 {
    width: 95%;
}
.filterGroup {
    width: 450px !important;
}

/*Manage Locations/Participants that are visible in the ShipLog Form */

#shipToAssignedSelect {
    width: 400px;
}
#shipToUnAssignedSelect {
    width: 260px;
}
#shipFromUnAssignedSelect {
    width: 260px;
}
#shipFromAssignedSelect {
    width: 400px;
}
.assignmentContainer {
    width: 800px;
}
/*Clean Up & Shrink Left Navigation*/

.jsfMenuContainer {
    width: 150px;
}
.jsfNavi {
    width: 150px;
}
td.tdIFramePgNavigation {
    width: 150px !important;
    padding-right: 0px;
}
td {
    padding-left: 0px;
}

/*Clean Up Report Rows*/

td.dateTimeCol {
    min-width: 190px !important;
}
td.senderKundenNr{
    width: 30px !important;
}

#receiverKundenNrColumn_0{
    width: 30px !important;
}

td.receiverKundenNr{
    width: 30px !important;
}

.senderFirma{
    min-width: 220px;
}

.receiverFirma{
    min-width: 250px;
}

.senderOrt{
    min-width: 80px;
}

td.ersteller{
    width: 400px;
}

}
