@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
/**************
  * GENERAL CSS *
  **************/
 
  body {
    background-image: url("https://images5.alphacoders.com/613/613927.jpg");
  }
 
  .ps-room.ps-room-light {
    background-color:rgba(255,245,214,0.7);
    border-color: #000;
  }
 
  .ps-room.ps-room-light.scrollable {
    background-color:rgba(255,245,195,0.7);
  }
 
  .pad .button, .ps-room.ps-room-light.scrollable p .button {
    background: #FFE278;
    border-color: #79330A;
    color: #79330A;
  }
 
  .pad .button:hover, .ps-room.ps-room-light.scrollable p .button:hover {
    background: #FFE278;
    border-color: #79330A;
  }
 
  .ps-room.ps-room-light .roomlist, .roomlisttop {
    width: 100%;
    max-width: 100% !important;
  }
 
  .ps-room.ps-room-light .roomlist {
    background: rgba(255, 255, 255, 0.1);
    margin-bottom: 10px;
    padding: 5px 15px;
  }
 
  .roomlist a.ilink {
    background: rgb(255,235,120);
    border-color: #000;
    padding: 10px;
    color: #000;
  }
 
  .roomlist a.ilink:hover {
    background: rgb(255,174,127) url("http://i.imgur.com/AdKotKy.jpg") right no-repeat;
    border-color: #000;
    color: #000;
  }
 
  .roomlist a.ilink small {
    color: #000000;
  }
 
  .roomcounters .picon {
    background: url("http://pldh.net/media/pokecons_action/026.gif") bottom no-repeat !important;
  }
 
  .roomcounters button {
    background: #FFAE7F  !important;
    border: 1px outset #000 !important;
    color: #000 !important;
  }
 
  .roomcounters button:hover {
    background: #FFC39F !important;
    border-color: #000 !important;
  }
 
  .textbox, .textbox:hover, .textbox:focus {
    background: #F8FBFD;
    border-color: #000;
    color: black;
  }
 
  .broadcast-blue {
    background-color: #B3E6FF;
    border: 1px solid #0B1361;
    color: #0B1361;
    font-family: sans-serif;
    padding: 8px;
    text-align: center;
    margin: 5px;
  }
 
  .broadcast-blue a {
    color: #0B1361;
  }
 
  .broadcast-red {
    background-color: #FFB3B3;
    border: 1px solid #610B0B;
    color: #610B0B;
    font-family: sans-serif;
    padding: 8px;
    text-align: center;
    margin: 5px;
  }
 
  .broadcast-red a {
    color: #610B0B;
  }
 
  .broadcast-green {
    background-color: #C3FFB3;
    border: 1px solid #19610B;
    color: #19610B;
    font-family: sans-serif;
    padding: 8px;
    text-align: center;
    margin: 5px;
  }
 
  .broadcast-green a {
    color: #19610B;
  }
 
  .message-announce {
    background: #FFF178;
    font-style: italic; color: black;
  }
   /******************
    * General CSS End *
    ******************/
    /*************
  * HEADER CSS *
  *************/
  /* old logo http://i.imgur.com/5onrVwe.png */
  .header {
    background: url("http://i.imgur.com/0i6XNEn.jpg") no-repeat;
 
  }
 
  .header .logo {
    opacity: 0;
  }
 
  .maintabbarbottom {
    background: #FFE278;
    border-color: #000000;
  }
 
  .tabbar a.button.notifying {
    background: #FFC43F;
    border-color: #000000;
    color: #000000;
    text-shadow: 0px 0px 2px #FFE278;
  }
 
  .tabbar a.button.notifying:hover {
    background: #FFDD3F;
    border-color: #000000;
    color: #000000;
  }
 
  .tabbar a.button.subtle-notifying {
    background: #FFDE6A;
    color: #000000;
  }
 
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    background: #FFA834;
    border-color: #000000;
    color: #000000;
  }
 
  .tabbar a.cur + .closebutton {
    color: #000000;
  }
 
  .tabbar a.button, .userbar button.icon {
    background: #FFEE8A;
    border-color: #000000;
    color: #000000;
  }
 
  .tabbar .closebutton {
    color: #000000;
  }
 
  .tabbar .closebutton:hover, .tabbar a.cur + .closebutton:hover {
    color: #000;
  }

  .tabbar a.button:hover, .userbar button.icon:hover {
    background: #FFA834;
    border-color: #000000;
  }
 
  .userbar .username {
    color: black;
    text-shadow: 1px 1px #F6E26F, 1px -1px #F6E26F, -1px 1px #F6F0C9, -1px -1px #F6E26F;
  }
 
  /******************
  * HEADER CSS ~end *
  ******************/
  /***********
  * CHAT CSS *
  ***********/
 
  .chat-log {
    background: rgba(255, 255, 240, 0.8) url("http://pldh.net/media/pokemon/gen6/xy-animated/026.gif") top right no-repeat;
    border-width: 0px 0px 1px 1px;
    border-style: solid;
    border-color: #000;
    color: black;
  }
 
  .chat small {
    color: #000;
  }
 
  .infobox {
    background: transparent;
    border-color: #000;
    border-style: outset;
    padding: 8px;
  }
 
  .infobox code {
    color: #000000;
  }
 
  .message small {
    color: #000 !important;
  }
 
  .message .utilichart .result {
    border: 1px outset #000;
    background: rgba(255, 161, 97, 0.7);
  }
 
  .chat-log a.ilink {
    color: #000000;
  }
 
  .chat-log a.ilink:hover {
    color: #000000;
  }
 
  .infobox a.ilink {
    color: orange;
  }
 
  .infobox a.ilink:hover {
    color: #000000;
  }
 
  .chat.mine {
    background: transparent;
  }
 
  .message-pm {
    color: #000;
  }
 
  .chat-log-add {
    background: rgba(250, 240, 199, 0.5);
    border-color: rgba(241, 231, 194, 0);
  }
 
  .chat-log-add .chatbox label {
    text-shadow: 0px 0px 2px #000;
  }
 
  .userlist {
    background: rgba(255,238,214, 0.7);
    border-color: rgba(241, 231, 194, 0);
  }
 
  .userlist li {
    border-color: #000;
  }
 
  .userlist li span, .userlist li strong {
    text-shadow: none;
  }
 
  .userlist li button:hover {
    background: rgba(255, 255, 255, 0.5);
  }
 
  .userlist-count {
    color: #000;
  }
 
  .userlist li em.group {
    color: #000;
  }
 
  /****************
  * CHAT CSS ~end *
  ****************/
    /**********************
  * MAINMENUWRAPPER CSS *
  **********************/
 
  .menugroup {
    background: rgba(255, 255, 255, 0.3);
    border: 1px outset #000;
    color: black;
  }
 
  .menugroup button, .select, .menugroup .button.mainmenu2, .menugroup .button.mainmenu4 {
    background: #FFE278;
    padding: 3px 8px;
    border: 1px outset #000;
    color: #000;
  }
 
  .menugroup button:hover, .select:hover, .menugroup .button.mainmenu2:hover, .menugroup .button.mainmenu4:hover {
    background: #FFFF8B;
    border-color: #000;
  }
 
  .menugroup a.ilink {
    font-weight: bold;
    font-style: italic;
  }
 
  .menugroup a.ilink:hover {
    background: #FFA041;
  }
 
  .select:disabled, .select:disabled:hover {
    background: #FFFF8B;
    border: 1px solid #000;
  }
 
  .teamselect:disabled strong {
    color: #000;
  }
 
  .menugroup .button.mainmenu1, .menugroup .button.mainmenu3, .menugroup .button.mainmenu5 {
    background: #FFFF7D;
    border-color: #000;
    color: #000;
  }
 
  .menugroup .button.mainmenu1:hover, .menugroup .button.mainmenu3:hover, .menugroup .button.mainmenu5:hover {
    background: #FFA071;
    border-color: #000;
  }
 
  .pm-window h3 {
    background: #FFFF7D;
    border: 1px outset #000;
    color: #000;
  }
 
  .pm-window .closebutton, .pm-window .minimizebutton {
    color: #000;
  }
 
  .pm-window h3 small {
    color: #FFA045;
  }
 
  .pm-window h3:hover {
    background: #FFFF7D;
    border: 1px outset #00000;
    color: #000;
  }
 
  .pm-window .closebutton:hover, .pm-window .minimizebutton:hover, .pm-window h3:hover .minimizebutton {
    color: #000;
  }
 
  .pm-window .closebutton:hover + .minimizebutton {
    color: #000 !important;
  }
 
  .pm-window.focused h3, .pm-window.focused h3:hover {
    background:#FFFF8B;
    border: 1px outset #000;
    color: #000;
  }
 
  .pm-log, .pm-window.focused .pm-log {
    background: #FFE493;
    border-color: #000;
    color: black;
  }
 
  .unread {
    background: none;
  }
 
  .challenge {
    background: #FFFF8B;
    border: 1px outset #000;
    color: #000;
  }
 
  .challenge .buttonbar button {
    background: #FFFF8B;
    padding: 3px 8px;
    border: 1px outset #000;
    color: #000;
  }
 
  .challenge .buttonbar button:hover {
    background: #FFA041;
    border-color: #000;
  }
 
  .pm-log-add, .pm-window.focused .pm-log-add {
    background: #FFFF8B;
    border: 1px outset #000;
    color: #000;
  }
 
  .pm-log-add .textbox {
    border-color: #000;
  }
 
  .mainmenufooter {
    background: rgba(255, 255, 255, 0.3);
    border: 1px outset #000;
    padding: 5px;
    color: #000;
  }
 
  .mainmenufooter a {
    color: #FFA045;
  }
 
  .mainmenufooter a:hover {
    color: #FFFF8B;
  }
 
  /***************************
  * MAINMENUWRAPPER CSS ~end *
  ***************************/ 
  /******************
  * TOURNAMENTS CSS *
  ******************/
 
  .tournament-title {
    background: rgba(255, 245, 190, 0.7);
    color: #000;
  }
 
  .tournament-status, .tournament-toggle {
    background: rgb(255, 245, 130);
    border-color: #000;
    color: #000;
  }
 
  .tournament-status {
    border-left: 1px solid #000;
  }
 
  .tournament-box {
    border-color: #000;
    border-left: 1px solid #000;
  }
 
  .tournament-bracket {
    background: url("http://pldh.net/media/pokemon/gen6/xy-animated/026.gif") center left no-repeat;
    border-color: #000;
  }
 
  .tournament-bracket-tree {
    padding: 15px 0px;
  }
 
  .tournament-tools {
    background: rgba(247, 219, 177, 0.7);
    border-color: #000;
  }
 
  .tournament-tools .button {
    background: #B5D2B8;
    border-color: #49614B;
    color: #49614B;
    text-shadow: 0px 0px 2px #C5E2C8;
  }
 
  .tournament-tools .button:hover {
    background: rgba(255,218,130,0.7);
    border-color: #000;
  }
 
  .tournament-tools .select:disabled, .tournament-tools .select:disabled:hover {
     background: #B5D2B8;
    border-color: #000;
    color: #000;
  }
 
  .tournament-tools .teamselect:disabled strong {
    color: #49614B;
  }
 
  .tournament-tools .select {
    background: rgba(255, 223, 184, 0.7);
    border-color: #000;
    color: #000;
  }
 
  .tournament-tools .select:hover {
    background: rgba(255, 223, 175, 0.7);
  }
 
  .tournament-format {
    color: #000;
    font-size: 12pt;
  }
 
  .ps-popup.tournament-popout-bracket {
    background-image: url("https://images5.alphacoders.com/613/613927.jpg");
  }
 
  /**********************
  * TOURNAMENT CSS ~end *
  **********************/
   /************
  * POPUP CSS *
  ************/
 
  .ps-popup {
    background: rgba(255,245,190, 0.7);
    border: 1px solid #000;
    color: #000;
  }
 
  .ps-popup .buttonbar button {
    background: #B5D2B8;
    padding: 3px 8px;
    border: 1px solid #000;
    color: #000;
  }
 
  .ps-popup .buttonbar button:hover {
    background: rgba(255,218,130,0.7);
    border-color: #000;
  }
 
  .ps-popup a, .ps-popup a.ilink, .popupmenu h3 {
    color: #000;
  }
 
  .ps-popup a:hover, .ps-popup a.ilink:hover {
    color: #000;
  }
 
  .ps-popup a.ilink.yours {
    color: #000;
  }
 
  .popupmenu button.sel {
    border-color: #000;
  }
 
  .popupmenu button.sel:hover {
    background: rgba(255, 255, 255, 0.2);
    border-color: #000;
  }
 
  .popupmenu button:hover {
    background: rgba(255, 255, 255, 0.4);
    border-color: #000;
  }
 
  /*****************
  * POPUP CSS ~end *
  *****************/
  /*******************
   * Team Builder CSS *
   *******************/
    .folderpane {
    border-color: #000;
  }
 
  .folder.cur .selectFolder, .folder.cur .selectFolder:hover, .folder.cur .selectFolder:active, .folderpane h3, .folderlistafter::before, .folderlistbefore::before, .folder .selectFolder, .folderlist .foldersep::before {
    border-color: #000;
  }
 
  .folderpane h3 {
    background: #FFF2A0;
    color: #000;
  }
 
  .folder .selectFolder {
    background: #FFF2A0;
    color: #000;
  }
 
  .folderlistafter::before, .folderlist .foldersep::before, .folderlistbefore::before  {
    background: #FFF2A0 !important;
  }
 
  .folder .selectFolder:hover {
    background: #FFDE93;
  }
 
  .teamlist .team {
    background: #FFE174;
    padding: 3px 8px;
    border: 1px solid #000;
    color: #49614B;
  }
 
  .teamlist .team small {
    text-shadow: none;
  }
 
  .teamlist .team:hover {
    background: #FFE198;
    border-color: #000;
  }
 
  .button.big, .format-select .button {
    background: #FFD565;
    border-color: #000;
    color: #000;
  }
 
  .button.big:hover, .format-select .button:hover {
    background: #FFD294;
    border-color: #000;
  }
 
  .teamchartbox {
    background: rgba(255, 255, 116, 0.5);
    border-color: #000;
    border-width: 3px;
  }
 
  .teambar button {
    background: #FFF39D;
    border: none;
    color: #000;
  }
 
  .teambar button:hover {
    background: #000;
  }
 
  .teambar button span {
    margin: -5px auto;
  }
 
  .setmenu button {
    color: #000;
  }
 
  .setmenu button:hover {
    background: rgba(255, 255, 255,0.5);
    border-color: #000;
    color: #000;
  }
 
  .setchart {
    background: #49614B;
    border-color: #000;
  }
 
  .setchart label {
    color: #000;
  }
 
  .setchart input, .setchart input:hover, .setdetails, .setdetails:hover, .setstats, .setstats:hover, .statform input.numform, .statform input.numform:hover {
    border-color: #000;
  }
 
  .setchart .setdetails label, .setchart .setstats label {
    color: #000;
    text-shadow: none;
  }
 
  .setchart-nickname {
    background: #FFE378;
    border-color: #000;
  }
 
  .setchart-nickname label {
    color: #000;
  }
 
  .setchart-nickname input, .setchart-nickname input:hover {
    border-color: #000;
  }
 
  .utilichart .sortcol.cur, .utilichart .sortcol.cur:hover {
    background: #FFD285;
    color: #000;
    text-shadow: none;
  }
 
  .utilichart .sortrow {
    background: #FFD285;
    border-color: #000;
  }
 
  .utilichart .sortcol {
    color: #000;
  }
 
  .utilichart .sortcol:hover {
    background: #FFD285;
  }
 
  .utilichart .filtercol em {
    border-color: #000;
    color: #000;
  }
 
  .utilichart h3, .dexentry h3, .resultheader h3 {
    background: #FFD285;
    border-color: #FFD285;
    color: #000;
    text-shadow: none;
  }
 
  .teambuilder-results .result a.cur {
    background: rgba(255, 255, 255, 0.3);
    border-color: #000;
  }
 
  .teambuilder-results .result a.cur:hover, .teambuilder-results .result a:hover {
    background: rgba(255, 255, 255, 0.5);
    border-color: #000;
  }
 
  .jslider .jslider-value {
    background: #FFD285;
    border: 1px solid #000;
    color: #000;
  }
 
  .utilichart .numcol {
    color: #000;
  }
 
  /***********************
  * TEAMBUILDER CSS ~end *
  ***********************/
