css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://forum.manjaro.org") {

html {  
	background: -webkit-linear-gradient(to left, #000000 , #434343); /* Chrome 10-25, Safari 5.1-6 */
    background: linear-gradient(to left, #000000 , #434343); /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */
}   
#banner-content li {
    color: #111;
}     
#topic-title h1 a {
    color: #e7e7e7;
}
div.cat a {
    color: #111;
}
#topic-title span.badge-category {
    color: #ffffff !important;
}
.topic-avatar {
    border-top: none;
}
a:hover.reply-new {
    color: #35bf5c;
}
.nav-pills>li>a {
	color: #e7e7e7;
}
div.container.show-badge h1 {
    color: #e7e7e7;
}
a.post-link {
    color: #ffffff;
}    
a.track-link.inbound, a.track-link.outbound {
    color: #35bf5c;
}           
div.custom-message a {
	color: #35bf5c;
}
h1 a.ember-view {
    color: #35bf5c;
}
.popular-topics a, .recent-topics a {
    color: #35bf5c;
}
div.not-found-topic span.badge-category.clear-badge {
	color: #ffffff !important;
}
h1.page-not-found, .page-not-found-search {
    color: #ccc;
}
.popular-topics-title, .recent-topics-title {
    color: #e7e7e7;
}
blockquote li {
	color: #111;
}
.cooked li {
    color: #111;
}
div code {
    color: #e7e7e7 !important;
    background-color: #3d3d3d;
}
div[itemprop] a {
    color: #7ea924;
}
div[itemprop] p, ul {
	color: #ccc;
}
.about, .admins h2 {
	color: #ccc;
}
.user-detail .title {
	color: #ccc !important;
}
div.name-line a {
	color: #ffffff !important;
}
.mfp-title a {
    color: #3c8252;
}
.timeline-container {
   margin-left: calc(100% - 180px);
}
div.timeline-handle {
    background-color: #35bf5c !important;
}
div.timeline-replies {
    color: #ccc;
}
#suggested-topics .topics {
    display: none;
}
#suggested-topics h3:nth-child(1) {
    display: none;
}
#suggested-topics h3 {
	color:#888;
}
#suggested-topics a {
    color: #23b14b;
}
#suggested-topics span.badge-category {
    color: #e7e7e7 !important;
}
.error-page {
    color: #ccc;
}
  
.card-content {
    background-color: #111 !important;
}
.card-content a {
    color: #fff !important;
}
.card-content a.btn {
    color: #111 !important;
}
.card-content a.btn.btn-primary {
    color: #fff !important;
}
.card-content i.fa.fa-shield {
    color: #888 !important;
}
.card-content h2 {
    color: #ccc !important;
}
div.bio {
    color: #fff !important;
}
.location {
    color: #ccc;
}
i.fa.fa-map-marker {
    color: #fff !important;
}  
.desc {
    color: #fff !important;
}
#user-card span.relative-date.date {
    color: #ccc !important;
}
.public-user-fields .user-field-name {
    color: #fff !important;
}
.public-user-fields .user-field-value {
    color: #ccc !important;
}
.user-main .about .details {
        background-color: #111 !important;
}
.user-badge.badge-type-silver .fa, #user-card.show-badges .badge-type-silver.more-user-badges .fa {
    color: #cd7f32 !important;
}

  
/* code by scachemaille */
.wrap {
    max-width: 4096px;
    padding-right: 30px;
    padding-left: 30px;
}
.topic-post {
    padding-top: 5px;
    padding-bottom: 5px;
}
.topic-body {
    width: calc(100% - 180px);
    border-radius: 5px;
    border-style: solid;
    border-color: #86A579;
    border-width: 1px;
}
.topic-meta-data {
    background-color: #f1f1f0;
    border-style: solid;
    border-color: #c6c6b8;
    border-width: 1px;
    border-radius: 5px;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 2px;
    padding-bottom: 2px;
}
.gutter {
    float: left;
    padding-left: 0px;
    width: auto !important;
}
.menu-panel {
    width: 500px !important;
}
.menu-panel span.badge-category {
    max-width: 170px;
}
.embedded-posts.top {
    margin-bottom: 3px;
}
.reply {
    padding-bottom: 10px;
    padding-left: 10px;
}
.list-controls .category-dropdown-menu .badge-wrapper.bullet .badge-category {
    max-width: 250px;
}
.stats {
    display: none;
}  
.share {
    display: none;
}  
.topic-list.categories .featured-topic a.last-posted-at, .topic-list.categories .featured-topic a.last-posted-at:visited {
    float: right;
}
  
}
