css:
@-moz-document domain("endchan.xyz") {
/*
 * Name:   EndGD	
 * Author: TJBK
 */
/* Base styles. */
body {
  background-color: #110e0b;
  background-image: none;
  color: #d8d8d8; }

hr {
  border: 1px solid #1dab37;
  background-color: #3aff6e; }

a {
  color: #1dab37; }

a:hover {
  color: #2bff52; }

nav {
  border: 1px solid #1dab37;
  background-color: #1e1c1b;
  color: #d8d8d8;
  border-top: none;
  border-left: none;
  border-right: none;
  padding: 0.5em;
  padding-top: 0.25em; }

footer {
  border: 1px solid #1dab37;
  background-color: #1e1c1b;
  color: #d8d8d8;
  border-bottom: none;
  border-left: none;
  border-right: none; }

input, button, textarea {
  border: 1px solid #1dab37;
  background-color: #1e1c1b;
  color: #d8d8d8; }

/* All styles. */
#topBox, .infoBox, #divLatestPosts, #divBoards, #newsBox, #divLatestImages {
  border: 1px solid #1dab37;
  background-color: #1e1c1b;
  color: #d8d8d8; }

.infoBox, #divLatestPosts, #divBoards, #newsBox, #divLatestImages {
  width: 100%; }

#boardsCellHeader span {
  background-color: #1dab37; }

.boardsCell:nth-of-type(odd) span {
  background-color: #232323; }

.boardPages, #divPages {
  border: 1px solid #1dab37;
  background-color: #1e1c1b;
  color: #d8d8d8; }

#help {
  border: 1px solid #1dab37;
  background-color: #1e1c1b;
  color: #d8d8d8;
  padding: 1em; }

.innerPost, .markedPost {
  border: 1px solid #1dab37;
  background-color: #1e1c1b;
  color: #d8d8d8; }

.form-post {
  background-color: #232323;
  padding: 1em;
  border-radius: 10px; }
  .form-post th, .form-post td {
    background-color: #1e1c1b; }
  .form-post button {
    border: 1px solid #1dab37;
    background-color: #1e1c1b;
    color: #d8d8d8;
    width: 100%; }
  .form-post input, .form-post textarea {
    background-color: #110e0b;
    border: none;
    color: #d8d8d8;
    width: 100%; }

.linkQuote, .linkSelf, .linkName {
  color: #98e; }

.linkQuote:hover, .linkSelf:hover, .labelSubject {
  color: #e5ccff; }

.quoteLink {
  color: #1dab37; }

.greenText {
  color: #3aff6e; }

.noEmailName, .noEmailName:hover {
  color: #57ffa9; }

.threadPages {
  border: 1px solid #1dab37;
  background-color: #1e1c1b;
  color: #d8d8d8; }
  .threadPages #divPages {
    background-color: #1e1c1b; }
  .threadPages span {
    color: #1dab37; }

.contentAction {
  border: 1px solid #1dab37;
  background-color: #1e1c1b;
  color: #d8d8d8; }
  .contentAction #reportFieldReason, .contentAction #deletionFieldPassword, .contentAction #fieldCaptchaReport {
    border: 1px solid #1dab37;
    background-color: #1e1c1b;
    color: #d8d8d8;
    width: 99%; }

.buttonBox input, #refreshButton {
  border: 1px solid #1dab37;
  background-color: #1e1c1b;
  color: #d8d8d8; }

#modeBanner {
  background-color: #2bff52;
  color: #000000;
  padding: 0.15em; }

.spoiler:hover {
  background-color: #110e0b;
  color: #d8d8d8; }

.settings {
  border: 1px solid #1dab37;
  background-color: #1e1c1b;
  color: #d8d8d8;
  margin: 1em auto; }
  .settings img {
    width: 100%; }

#transferBoardFieldLogin, #saveSettingsJsButton, #boardMessageField, #tagsField, #autoCaptchaThresholdField, #hourlyThreadLimitField, #validMimesField, #autoSageLimitField, #maxThreadFields, #maxFileSizeField, #maxFilesField, #anonymousNameField, #boardDescriptionField, #boardNameField, #spoilerJsButton, #files, #filesSpoiler, #cssJsButton, #deleteBoardJsButton, #transferBoardJsButton, #addVolunteerJsButton, #addVolunteerFieldLogin, #emailField, #logoutJsButton, #passwordJsButton, #newBoardJsButton, #newBoardFieldName, #newBoardFieldDescription, #newBoardFieldUri, #fieldPassword, #fieldConfirmation, #fieldNewPassword, #saveJsButton, #emailField #recoverJsButton, #reloadCaptchaButtonRecover, #reloadCaptchaButton, #registerJsButton, #loginJsButton, #registerFieldLogin, #fieldCaptchaRecover, #recoverFieldLogin, #fieldCaptcha, #registerFieldEmail, #registerFieldPassword, #loginFieldPassword, #loginFieldLogin {
  border: 1px solid #1dab37;
  background-color: #1e1c1b;
  color: #d8d8d8;
  width: 100%; }

/* Layouts */
body {
  margin: 0;
  padding: 0; }

nav {
  text-align: center; }
  nav a {
    float: none; }
  nav span {
    display: none; }

#topBox {
  margin-top: 3em; }

.threadPages {
  width: 99%; }

.contentAction {
  width: 25em;
  float: none;
  margin: 0 auto; }
  .contentAction #reportFieldReason, .contentAction #deletionFieldPassword, .contentAction #fieldCaptchaReport {
    width: 99%; }
  .contentAction img {
    width: 100%; }
  .contentAction input[type="checkbox"] {
    width: inherit; }

.buttonBox input {
  width: 100%; }

}
