css:
@-moz-document domain("vast-journey-9935.herokuapp.com"), domain("glowfic.com") {
#header { background-image: url("https://i.imgur.com/ONTDjJ3.jpg"); background-position: right top; }
#logo, #nav-bottom { background-color: transparent !important; }
#header #user-info, #header #nav-top { background-color: rgba(5, 5, 15, 0.9); }

#content .sub { color: #f3f3f3; }
#content .sub, .post-navheader, #content .subber, .subber { background-color: #182152; }
#content .beige { background-color: #484357; color: #DADADA; }
#content .beige a, #content th a, .post-header a { color: #B5AFCB; }
#content .beige a:visited, #content th a:visited, .post-header a:visited { color: #9C9AA4; }

#post-menu-box { border-top: 1px solid rgba(128,128,128,0.5); }
#post-menu-box div { border-bottom: 1px solid rgba(128,128,128,0.5); }
#post-menu-box a { color: inherit; }

.post-navheader .view-button, .view-button.selected, .view-button, .link-box.green, .link-box.blue, .link-box.red { background-color: #463c65; }
#post-editor .green { background-color: #484357; color: #DADADA; }
.post-subheader { background-color: #484357; color: #F3F3F3; }

.subber, .subber a, .subber a:visited { color: #9c9aa4; }
.subber b { color: #F3F3F3; }

.post-icon, #current-icon-dropdown, .post-author { background-color: #0E0B1E; }
#post-editor #current-icon-dropdown { background-color: #0E0B1E; }
.post-character, .post-screenname, .post-author { font-weight: bold; }
#character-selector { font-weight: normal; }
.post-screenname { color: #9c9aa4; }
.post-character, .post-character a, .post-character a:visited, .post-author, .post-author a, .post-author a:visited { color: #F3F3F3; }

.pagination .current { background-color: #484357; }
.pagination a { color: #F3F3F3; }
.pagination a:visited { color: #9c9aa4; }
.pagination .disabled { color: #58546b; }

#content .beige .chosen-container, #content .beige .chosen-container a, .chosen-container, .chosen-container a { color: #444; }

body { background-color: #211e2f; color: #DADADA; }
#content th.subber { color: #DADADA; }

#content .sub, .post-navheader, #content .subber, .subber { background-color: #131937; }
#content th, .post-header { background-color: #0d0b1e; }
.post-character, .post-screenname { background-color: #111842; }

#post-menu-box { background-color: #191919; }
#post-menu-box a:hover div { background-color: #252525; }

#post-menu-box, #content .even, #content .odd, .post-post, .post-reply { color: #DADADA; }
#content .even, .post-post, .post-reply:nth-child(even), #post-editor, .post-reply:nth-child(odd) blockquote { background-color: #31323b; }
#content .odd, .post-reply:nth-child(odd), .post-post + #post-editor, .post-post + a + #post-editor, .post-post blockquote, .post-reply:nth-child(even) blockquote { background-color: #25262e; }

.green { background-color: #323232; }

a { color: #B5AFCB; }
a:visited { color: #9C9AA4; }
.flash.success, .flash.error, .flash.inbox { background-color: #484357; color: #DADADA; }

#current-icon-dropdown select { background-color: #333; color: #CCC; }

#message_form input.button { color: unset; }
#post-form-wrapper textarea, .form-table input, .form-table textarea, #header-forms input, .gallery input, #post-editor input, #signup input, .chosen-container .chosen-results li.no-results, .chosen-container-multi .chosen-choices, .chosen-container .chosen-drop, .chosen-container-active.chosen-with-drop .chosen-single, .chosen-container-single .chosen-single, #icon-table input, input#icon_files, .select2-container--default .select2-selection--single, .select2-search input { background-color: #4D4B5B; color: #DADADA; background-image: none; box-shadow: none; } /* Inputs should be darker */
button, input.button { background-color: #4D4B5B; color: #DADADA; background-image: none; box-shadow: none; }
button:focus, input.button:focus { background-color: #474554; } /* Make buttons darker too */
.paginator .right-align a, .chosen-single span, .chosen-results li.group-result, .select2-container--default .select2-selection--single .select2-selection__rendered { color: #DADADA; } /* Fix the text colors */
#header #nav-top .button { background-color: #3b3a42; color: #DADADA; background-image: none; box-shadow: none; } /* Logout button should be darker */
.select2-container--default .select2-selection--multiple, .select2-results__option[aria-selected], .chosen-container .chosen-results li.active-result, .select2-dropdown { background-color: #4D4B5B; color: #DADADA; } /* Select2 Dropdown Options should be darker */
.select2-container--default .select2-results__option[aria-selected=true], .chosen-container-multi .chosen-drop .result-selected { background-color: #474554; } /* Selected Select2 things should be darker */
.select2-container--default .select2-results__option--highlighted[aria-selected], .select2-container--default .select2-results__option[aria-disabled=true], .chosen-container .chosen-results li.highlighted { background-color: #585669; background-image: none; } /* As should search things and disabled things and hovered things */
.select2-container--default .select2-results__option[aria-disabled=true] { color: #bbb; } /* See above */
.select2-container--default .select2-selection--multiple .select2-selection__choice, .chosen-container-multi .chosen-choices li.search-choice { background-color: #474554; background-image: none; color: #DADADA; box-shadow: none; } /* As should your displayed choices */
.select2-container--default.select2-container--focus .select2-selection--multiple { border-color: #AAAAAA; } /* And don't screw with the border on focus */

 /*TODO: Edit when doing dark editor mode!!*/
textarea.tinymce, body.tinymce_dark { background-color: #211e2f; color: #DADADA; }
.mce-panel, .mce-btn { background-color: #191723; }
.mce-btn:hover, .mce-btn:focus, .mce-btn.mce-active, .mce-btn.mce-active:hover { background-color: #322e44; border-color: #474161; }
.mce-btn.mce-active:hover { background-color: #433d5a; }
.mce-menu { background-color: #322e44; color: #CCC; }
.mce-menu-item-normal.mce-active { background-color: #4d4769; }
.mce-menu-item:hover, .mce-menu-item.mce-selected, .mce-menu-item:focus { background-color: #433e5d; }
.mce-menu-item .mce-ico, .mce-menu-item .mce-text { color: #CCC; }
.mce-container, .mce-container *, .mce-widget, .mce-widget *, .mce-reset { color: #CCC; }
.mce-edit-area { border-top-color: #504971; }
.mce-btn-group:not(:first-child) { border-left-color: #504971; }
.mce-textbox { background-color: #463c65; color: #FFFFFF; border-color: #5b4e84; }
.mce-combobox input { border-right-color: #5b4e84; }
.mce-btn button { color: #CCC; }
.mce-floatpanel .mce-btn { border-color: #5b4e84; }
#post-form-wrapper > button, #post-form-wrapper > input.button { background: none; background-color: #463c65; color: #FFFFFF; border: 0px; }
}
