css:
@-moz-document domain("play.spotify.com") {
#bar-inner {
  background: /*[[bcolor]]*/ !important;
}
.main-menu.narrow-menu a.active:after, .main-menu.narrow-menu a.selected:after {
  background-color: /*[[bcolor]]*/ !important;
}
.nav > li.active > a:after {
  background-color: /*[[bcolor]]*/ !important;
}
.btn:hover:not(.just-changed):not(.stay-added) {
  color: /*[[bcolor]]*/ !important;
  border-color: /*[[bcolor]]*/ !important;
}
.btn-primary {
  color: #FFFFFF;
  background: /*[[bcolor]]*/ !important;
  border-color: /*[[bcolor]]*/ !important;
}
.list-group-item.list-group-item-media.active:before {
  background: /*[[bcolor]]*/ !important;
}
.btn-primary:not(.just-changed):not(.stay-added):hover {
  color: /*[[bcolor]]*/ !important;
  background: #121314;
  border-color: /*[[bcolor]]*/ !important;
}
.btn:hover:not(.just-changed):not(.stay-added):after {
  display: block;
  border: 1px solid;
  border-color:/*[[bcolor]]*/ !important;
}
.main-menu.narrow-menu a.active, .main-menu.narrow-menu a.active:hover {
    color: /*[[svgcolor]]*/ !important;
}

.tl-row.added .tl-save .btn:before {
    color: /*[[bcolor]]*/ !important;
}
.tl-row.added .tl-save .btn:after {
    color: /*[[bcolor]]*/ !important;
}
.nav>li.active>a {
    color: /*[[svgcolor]]*/ !important;
}
.main-menu.narrow-menu a#nav-messages.active:before, .main-menu.narrow-menu a#nav-notifications.active:before {
    color: gold;
}
.button::after {
    content: "";
    top: -1px;
    left: -2px;
    right: -1px;
    bottom: -1px;
    transform: translate(0.5px, 0px);
    border-radius: 500px;
    border: 1px solid;
  border-color: /*[[svgcolor]]*/ !important;
}
.tl-row:hover.playing .tl-highlight {
    color: /*[[bcolor]]*/ !important;
}
.tl-row.playing .tl-cell, .tl-row.playing .tl-cell a:link, .tl-row.playing .tl-highlight {
    color: /*[[svgcolor]]*/ !important;
}
.btn.btn-add.added:after {
    display: block;
    border: 1px solid;
  border-color: /*[[bcolor]]*/ !important;
}
.btn.btn-add.added:hover:not(.just-changed):not(.stay-added):after {
    display: block;
    border: 1px solid;
border-color: /*[[svgcolor]]*/ !important;
}
.ads-leaderboard-container {
		visibility: hidden !important;
	}
#now-playing-widgets {
		visibility: hidden !important;
	}
#header.container {
		display: none !important;
	}
#vol-bar-inner {
    background: /*[[svgcolor]]*/ !important;
}
.form-control focus {
    background-color: red;
}
.s-scroller{
    background-image:url("http://www.demi.fi/sites/demi/files/general/76/1109076/Great-Milky-Way-photography-2.jpg");
}
.spoticon-search-32::before {
    font-size: 83px;
    color:  white;
}
.input-wrapper{
    color: red;
}
.selectable{
    background: transparent !important;
}
.form-control {
    color: white;
    vertical-align: middle;
    background-color:black;
    background-image: none;
    border: 1px dotted white;
    border-radius: 3px;
}
.form-control:focus {
    background-color: black;
    color: white;
}
spoticon-x-16, .spoticon-search-16 {
    position: absolute;
    top: 25px;
    color: white;
}
.spoticon-search-32::before {
    font-size: 83px;
    color: /*[[svgcolor]]*/ !important;
}
.main-menu.narrow-menu .logo {
    height: 34px;
    margin: 0 5px;
    background: url('http://i.imgur.com/1HrNMZg.png') no-repeat center;
    background-size: 83%;
}
}
