css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https://www.kiva.org/lend($|\\?+.*=.*|/[a-zA-Z]*)") {


.loancards-list li {
    width:100% !important;
    padding-bottom:10px !important;
    /*background-color:#FBFBF7 !important;*/
}
.loancards-list li:nth-child(odd)  {
    background-color:#FBFBF7 !important;
}
.loancards-list li:nth-child(even) {
    background-color:#FDFDFB  !important;
}
div.loan-card {
    width:100% !important;
    height:100px !important;
    max-width:100% !important;
    margin:0px !important;
    border-top: #ddd 1px solid !important;
    border-bottom: #ddd 1px solid !important;
    /*background-color:#FDFDFB !important;*/
}
div.loan-card:after {
    clear:both;
}
.borrower-img-wrap {
    float:left !important;
    width:100px !important;
    height:100px !important;
    background-size: cover !important;
    background-repeat:no-repeat !important;
    overflow:hidden !important;
    padding-bottom:0px !important;
}
.borrower-details-wrap {
    float:left !important;
    width:60% !important;
    height:100px !important;
}
.loan-card .borrower-details-wrap a {
    color:#4B7D31 !important;
}
.lenderassist_additional ~ .borrower-details-wrap{
    width:40% !important;
}
.loan-image-footer {
    float:left !important;
    margin:0 6px !important;
    width:36px !important;
    position: relative !important;
}
.loan-image-footer a {
   position: relative !important;
}
div.loan-message.urgent {
    width:100px !important;
    font-size:11px !important;
    line-height:14px !important;
    height:20px !important;
    text-wrap:none !important;
    overflow:hidden !important;
    text-align:right !important;
    top:80px !important;
}
.lenderassist_additional {
    float:left !important;
    width:20% !important;
    height:99px !important;
    font-size:16px !important;
    line-height:16px !important;
    border:none !important;
    position:relative;
}
.lenderassist_partner {
    /*margin-top:18px !important;*/
    height:36px !important;
    position:absolute; bottom:6px;
}
.borrower-footer-wrap, div.bottom-bar {
    float:right !important;
	width:20% !important;
	max-width:20% !important;
}
.borrower-footer-wrap {
    padding-top:6px !important;
    padding-right:6px !important;
}
.bottom-bar {
    height:34px !important;
    display:block !important;
}
.bottom-bar a.button {
    font-size:15px !important;
    line-height:32px !important;
    border:#61A03F 1px solid !important;
    color:#61A03F !important;
    height:30px !important;
    background-color:inherit !important;
    margin-right:3px !important;
}
.bottom-bar a.button {
    width:97% !important;
}
.bottom-bar a.half-width {
    width:48% !important;
}
.bottom-bar.no-hide, .no-touch .bottom-bar.no-hide, .bottom-bar .message {
    font-size:12px !important;
    height:1.5rem !important;
    line-height:1.5rem !important;
}
.bottom-bar.no-hide, .no-touch .bottom-bar.no-hide {
	margin-bottom:29px !important;
}
.bottom-bar.show-short {
    /*display:none !important;*/
    margin-bottom:30px !important;
}
.bottom-bar.show-short a {
    border:0px !important;
    font-size:12px !important;
    color:#9C021A !important;
}
.bottom-bar.show-short .button .icon{
    display:none;
}
.bottom-bar .button .icon,.bottom-bar .message .icon {
    fill: #FFFFFF !important;
    width: 1rem !important;
    height: 1rem !important;
    position: absolute !important;
    left:6px !important;
    top:5px !important;
}
.hide-for-touch a.success {
    background-color:#488443 !important;
    color:#fbfbfb !important;
}
div.fundraising-meter {
    border:#61A03F 1px solid
}
.fundraising-meter .meter {
    background-color: inherit;
    background-image: repeating-linear-gradient(-60deg, white, white 3px, #E6E6CC 3.5px, #E6E6CC 5px) !important;
	background-size: 100% 100% !important;
}
.matching-message {
    top:50px;
}

}
