css:
 @-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
/**************
  * GENERAL CSS *
  **************/
  
  body {
    background-image: url("https://images6.alphacoders.com/661/661573.jpg");
  }
  
  .ps-room.ps-room-light {
    background: rgba(154, 255, 160, 0.6);
    border-color: #49614B;
  }
  
  .ps-room.ps-room-light.scrollable {
    background: rgba(154, 255, 154, 0.6);
  }
  
  .pad .button, .ps-room.ps-room-light.scrollable p .button {
    background: #9AFF9A;
    border-color: #49614B;
    color: #000;
  }
  
  .pad .button:hover, .ps-room.ps-room-light.scrollable p .button:hover {
    background: #24D330;
    border-color: #000;
  }
  
  .ps-room.ps-room-light .roomlist, .roomlisttop {
    width: 95%;
    max-width: 95% !important;
  }
  
  .ps-room.ps-room-light .roomlist {
    background: rgba(204, 255, 204, 0.4);
    border: 2px  groove #000;
    margin-bottom: 10px;
    padding: 5px 15px;
  }
  
  .roomlist a.ilink {
    background: rgba(154, 255, 154, 0.8);
    border-color: #46914B;
    border-radius: 8px;
    padding: 10px;
    color: #000;
  }
  
  .roomlist a.ilink:hover {
    background: rgba(102, 255, 102, 0.8) url("http://pldh.net/media/pokemon/gen6/xy-animated/001.gif") right no-repeat;
    border-color: 46914B;
    border-radius: 8px;
    color: #000;
  }
  
  .roomlist a.ilink small {
    color: #46914B;
  }
  
  .roomcounters .picon {
    background: url("http://pldh.net/media/pokemon/gen3/rusa_action/001.gif") bottom no-repeat !important;
  }
  
  .roomcounters button {
    background: #9AFF9A !important;
    border: 2px groove #46914B !important;
    color: #000 !important;
  }
  
  .roomcounters button:hover {
    background: #90EE90 !important;
    border-color: #46914B !important;
  }
  
  .textbox, .textbox:hover, .textbox:focus {
    background: #CCFFCC;
    border-color: #49614B;
    color: 000;
  }
  
  .broadcast-blue {
    background-color: #B3E6FF;
    border: 1px solid #0B1361;
    color: #0B1361;
    font-family: sans-serif;
    padding: 8px;
    text-align: center;
    margin: 5px;
    text-shadow: 0px -1px 0px #D7F2FF;
  }
 
  .broadcast-blue a {
    color: #0B1361;
  }
 
  .broadcast-red {
    background-color: #FFB3B3;
    border: 1px solid #610B0B;
    color: #610B0B;
    font-family: sans-serif;
    padding: 8px;
    text-align: center;
    margin: 5px;
    text-shadow: 0px -1px 0px #FFD7D7;
  }
 
  .broadcast-red a {
    color: #610B0B;
  }
 
  .broadcast-green {
    background-color: #C3FFB3;
    border: 1px solid #19610B;
    color: #19610B;
    font-family: sans-serif;
    padding: 8px;
    text-align: center;
    margin: 5px;
    text-shadow: 0px -1px 0px #D8FFD7;
  }
 
  .broadcast-green a {
    color: #19610B;
  }
  
  .message-announce {
    background: #32CD22;
    font-style: italic;
  }
  
  /*******************
  * GENERAL CSS ~end *
  *******************/
  /*************
  * HEADER CSS *
  *************/
  .header {
    background: rgba(204, 255, 204, 0.6) url("http://i.imgur.com/UHfTlDV.gif") no-repeat;
  }
  
  .header .logo {
    opacity: 0;
  }
  
  .maintabbarbottom {
    background: #90EE90;
    border-color: #469B14;
  }
  
  .tabbar a.button.notifying {
    background: #FF66FF;
    border-color: #469B14;
    color: #000;
  }
  
  .tabbar a.button.notifying:hover {
    background: #33FF33;
    border-color: #469B14;
    color: #000;
  }
  
  .tabbar a.button.subtle-notifying {
    background: #00CD00;
    color: #000;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    background: #00EE00;
    border-color: #469B14;
    color: #000;
  }
  
  .tabbar a.cur + .closebutton {
    color: #000;
  }
  
  .tabbar a.button, .userbar button.icon {
    background: #6EFF70;
    border-color: #469B14;
    color: #000;
  }
  
  .tabbar .closebutton {
    color: #000;
  }
  
  .tabbar .closebutton:hover, .tabbar a.cur + .closebutton:hover {
    color: #000;
  }
  
  .tabbar a.button:hover, .userbar button.icon:hover {
    background: #00CD00;
    border-color: #469B14;
  }
  
  .userbar .username {
    color: black;
  }
  
  /******************
  * HEADER CSS ~end *
  ******************/
  
     /***********
  * CHAT CSS *
  ***********/
  
  .chat-log {
    background: rgba(204, 255, 204, 0.6) url("http://i.imgur.com/wZ8mEI6.jpg") top right no-repeat; /*url("http://i.imgur.com/8psSQSi.jpg"); right no-repeat;*/
    border-style: groove;
    border-width: 0px 0px 1px;
    border-color: #49614B;
    color: black;
  }
  
  .chat small {
    color: black;
  }
  
  .infobox {
    background: transparent;
    border-color: #000;
    border-style: solid;
    border-radius: 4px;
    padding: 8px;
  }
  
  .infobox code {
    color: #000;
  }
  
  .message small {
    color: #000 !important;
  }
  
  .message .utilichart .result {
    border-radius: 4px;
    border-color: #000;
    border-style: solid;
    background: transparent;
  }
  
  .chat-log a.ilink {
    color: #3D8B37;
  }
  
  .chat-log a.ilink:hover {
    color: #009900;
  }
  
  .infobox a.ilink {
    color: #3D8B37;
  }
  
  .infobox a.ilink:hover {
    color: #009900;
  }
  
  .chat.mine {
    background: transparent;
  }
  
  .message-pm {
    color: #000;
  }
  
  .chat-log-add {
    background: rgba(254, 255, 160, 0.5);
    border-color: rgba(0, 0, 0, 0.0);
  }
  
  .userlist {
    background: rgba(152, 251, 152, 0.5);
    border-color: rgba(0, 01, 0, 0.6);
  }
  
  .userlist li {
    border-color: #469B14;
  }
  
  .userlist li span, .userlist li strong {
    text-shadow: none;
  }
  
  .userlist li button:hover {
    background: rgba(255, 255, 255, 0.5);
  }
  
  .userlist-count {
    color: #000;
  }
  
  .userlist li em.group {
    color: #000;
  }
  
  /****************
  * CHAT CSS ~end *
  ****************/
  /**********************
  * MAINMENUWRAPPER CSS *
  **********************/
  
  .menugroup {
    background: rgba(254, 251, 254, 0.6);
    border-radius: 4px;
    border-color: #000;
  }
  
  .menugroup button, .select, .menugroup .button.mainmenu2, .menugroup .button.mainmenu4 {
    background: #98FB98;
    padding: 3px 8px;
    border-radius: 4px;
    border-color: #000;
    color: #000;
  }
  
  .menugroup button:hover, .select:hover, .menugroup .button.mainmenu2:hover, .menugroup .button.mainmenu4:hover {
    background: #7CCD7C;
    border-radius: 4px;
    border-color: #000;
  }
  
  .menugroup a.ilink {
    font-weight: bold;
    font-style: italic;
  }
  
  .menugroup a.ilink:hover {
    background: rgba(154, 255, 154, 0.8);
  }
  
  .select:disabled, .select:disabled:hover {
    background: rgba(154, 255, 127, 0.5);
    border-radius: 4px;
    border-color: #000;
  }
  
  .teamselect:disabled strong {
    color: #49614B;
  }
  
  .menugroup .button.mainmenu1, .menugroup .button.mainmenu3, .menugroup .button.mainmenu5 {
    background: #7CCD7C;
    border-radius: 4px;
    border-color: #000;
    color: #000;
  }
  
  .menugroup .button.mainmenu1:hover, .menugroup .button.mainmenu3:hover, .menugroup .button.mainmenu5:hover {
    background: #98FB98;
    border-color: #496B14;
  }
  
  .pm-window h3 {
    background: #98FB98;
    border-radius: 4px;
    border-color: #000;
    color: #000;
  }
  
  .pm-window .closebutton, .pm-window .minimizebutton {
    color: #000;
  }
  
  .pm-window h3 small {
    color: #000;
  }
  
  .pm-window h3:hover {
    background: #7CCD7C;
    border-radius: 4px;
    border-color: #000;
    color: #000;
  }
  
  .pm-window .closebutton:hover, .pm-window .minimizebutton:hover, .pm-window h3:hover .minimizebutton {
    color: #000;
  }
  
  .pm-window .closebutton:hover + .minimizebutton {
    color: #000 !important;
  }
  
  .pm-window.focused h3, .pm-window.focused h3:hover {
    background: #98FB89;
    border-radius: 4px;
    border-color: #000;
    color: #000;
  }
  
  .pm-log, .pm-window.focused .pm-log {
    background: rgba(204, 255, 204, 0.9);
    border-color: #49614B;
    color: black;
  }
  
  .unread {
    background: #597368;
  }
  
  .challenge {
    background: #7CCD7C;
    border-radius: 4px;
    border-color: #000;
    color: #000;
  }
  
  .challenge .buttonbar button {
    background: #98FB98;
    padding: 3px 8px;
    border-radius: 4px;
    border-color: #000;
    color: #000;
  }
  
  .challenge .buttonbar button:hover {
    background: 7CCD7C;
    border-color: #469B14;
  }
  
  .pm-log-add, .pm-window.focused .pm-log-add {
    background: #CCFFCC;
    border-radius: 4px;
    border-color: #000;
    color: #000;
  }
  
  .pm-log-add .textbox {
    border-color: #49614B;
  }
  
  .mainmenufooter {
    background: rgba(154, 255, 154, 0.6);
    border-radius: 4px;
    border-color: #000;
    padding: 5px;
    color: #000;
  }
  
  .mainmenufooter a {
    color: #000;
  }
  
  .mainmenufooter a:hover {
    color: #000;
  }
  
  /***************************
  * MAINMENUWRAPPER CSS ~end *
  ***************************/

  /******************
  * TOURNAMENTS CSS *
  ******************/
  
  .tournament-title {
    background: #9AFF9A;
    color: #000;
  }
  
  .tournament-status, .tournament-toggle {
    background: #7CCD7C;
    border-color: #469B14;
    color: #000;
  }
  
  .tournament-status {
    border-left: 1px solid #469B14;
  }
  
  .tournament-box {
    border-color: #469B14;
    border-left: 2px solid #469B14;
  }
  
  .tournament-bracket {
    background: url("http://i.imgur.com/rdCV4lx.png?1") center left no-repeat;
    border-color: #496B14;
  }
  
  .tournament-bracket-tree {
    padding: 15px 0px;
  }
  
  .tournament-tools {
    background: #CCFFCC;
    border-color: #469B14;
  }
  
  .tournament-tools .button {
    background: #7CCD7C;
    border-color: #469B14;
    border-radius: 4px;
    color: #000;
  }
  
  .tournament-tools .button:hover {
    background: #71C671;
    border-color: #469B14;
  }
  
  .tournament-tools .select:disabled, .tournament-tools .select:disabled:hover {
     background: #9AFF9A;
    border-color: #469B14;
    color: #000;
  }
  
  .tournament-tools .teamselect:disabled strong {
    color: #000;
  }
  
  .tournament-tools .select {
    background: #7CCD7C;
    border-color: #469B14;
    border-radius: 4px;
    color: #000;
  }
  
  .tournament-tools .select:hover {
    background: #71C671;
  }
  
  .tournament-format {
    color: #000;
    font-size: 12pt;
  }
  
  .ps-popup.tournament-popout-bracket {
    background: rgba(154, 255, 154, 0.5) url("https://images6.alphacoders.com/661/661573.jpg");
  }
  
  /**********************
  * TOURNAMENT CSS ~end *
  **********************/
    /************
  * POPUP CSS *
  ************/
  
  .ps-popup {
    background: rgba(144, 238, 144, 0.8);
    border: 1px solid #469B14;
    border-radius: 4px;
    color: #000;
  }
  
  .ps-popup .buttonbar button {
    background: #567E3A;
    padding: 3px 8px;
    border: 1px solid #469B14;
    border-radius: 4px;
    color: #000;
  }
  
  .ps-popup .buttonbar button:hover {
    background: #526F35;
  }
  
  .ps-popup a, .ps-popup a.ilink, .popupmenu h3 {
    color: #000;
    font-style: bold;
  }
  
  .ps-popup a:hover, .ps-popup a.ilink:hover {
    color: #597368;
    font-style: bold;
  }
  
  .ps-popup a.ilink.yours {
    color: #597368;
    font-style: bold;
  }
  
  .popupmenu button.sel, popupmenu button.sel:havor {
    border-color: #469B14;
    border-style: solid;
    border-radius: 4px;
  }
  
  
  .popupmenu button:hover {
    background: rgba(188, 237, 145, 0.4);
    border-color: #469B14;
    border-style: solid;
    border-radius: 4px;
  }
  
  /*****************
  * POPUP CSS ~end *
  *****************/  
    /*************
  * BATTLE CSS *
  *************/

.sunweather, .sunnydayweather, .desolatelandweather 
   { background: #FFEEBB url(http://i.imgur.com/DNMtOWR.gif) no-repeat scroll left top; 
     color: #664411; 
     text-shadow: #FFFFFF 1px 1px 0, #FFFFFF 1px -1px 0, #FFFFFF -1px 1px 0, #FFFFFF -1px -1px 0; 
     background-size:cover;
   }
   
   .rainweather, .raindanceweather, .primordialseaweather {
