   }
  
  .ps-room.ps-room-opaque {
    background: rgba(144, 238, 144, 0.6);
  }
  
  .ps-room .battle-controls {
    background: rgba(78, 238, 148, 0.4);
  }
  
  .ps-room .battle-controls button, .ps-room .battle-controls .button {
    background: #4EEE94;
    padding: 3px 8px;
    border: solid #469B14;
    border-radius: 4px;
    color: #000;
  }
  
  .ps-room .battle-controls button:hover, .ps-room .battle-controls .button:hover {
    background: #43CD80;
  }
  
  .battle-controls .whatdo {
    color: #000;
  }
  
  .battle-controls .whatdo small {
    border-color: #000;
  }
  
  .moveselect button {
    text-decoration: underline;
    background: url("http://i.imgur.com/GPzZsG1.png") center left no-repeat !important;
    background-position: left 8px !important;
    padding-left: 30px !important;
    border: none !important;
    color: #000 !important;
  }
  
  .movemenu button {
    height: 50px;
    color: #000 !important;
    font-weight: bold;
    border-radius: 3px !important;
    border-style: solid !important;    
  }
  
  .movemenu button small {
    text-shadow: none !important;
    font-weight: normal;
  }
  
  
  .movemenu button.type-Normal {
    background: #C7AFB5 url("http://i.imgur.com/3C1Bacx.png?2") center no-repeat !important;
    border-color: #75525C !important;   
  }
  
  .movemenu button.type-Normal small {
    color: #75525C;
  }
  
  .movemenu button.type-Normal:hover {
    background: #DFCDD2 url("http://i.imgur.com/3C1Bacx.png?2") center no-repeat !important;
  }
  
  
