  }
  
  
  .movemenu button.type-Grass {
    background: #42C665 url("http://i.imgur.com/628FI5x.png?2") center no-repeat !important;
    border-color: #147B3D !important;   
  }
  
  .movemenu button.type-Grass small {
    color: #147B3D;
  }
  
  .movemenu button.type-Grass:hover {
    background: #56D879 url("http://i.imgur.com/628FI5x.png?2") center no-repeat !important;
  }
  
  
