  }
  
  .movemenu button.type-Poison:hover {
    background: #BF8EF6 url("http://i.imgur.com/d3QQ9Af.png?2") center no-repeat !important;
  }

  
  .movemenu button.type-Fighting {
    background: #EF6239 url("http://i.imgur.com/0fjqBIB.png?2") center no-repeat !important;
