    background: #F369B3 url("http://i.imgur.com/QzQIgYi.png?2") center no-repeat !important;
    border-color: #A52A6C !important;   
  }
  
  .movemenu button.type-Psychic small {
    color: #A52A6C;
  }
  
  .movemenu button.type-Psychic:hover {
    background: #FD7DC1 url("http://i.imgur.com/QzQIgYi.png?2") center no-repeat !important;
  }
  
  
  .movemenu button.type-Dark {
    background: #595978 url("http://i.imgur.com/dpxY9u7.png?2") center no-repeat !important;
    border-color: #040707 !important;   
  }
  
  .movemenu button.type-Dark small {
    color: #040707;
