css:
@-moz-document domain("trello.com") {

/*Pimp Trello for better usability*/
body .list-card-labels {
  float: right;
  top: -2px;
  right: -6px;
  max-width: 200px;
  margin: 0;
  text-align: right;
}
 
body .list-card-labels .card-label {
  float: right;
  width: auto;
  height: 16px;
  line-height: 16px;
  padding: 0 3px;
  margin: 0 0 2px 2px;
  font-size: 9px;
  border-radius: 3px;
  overflow: visible;
}
 
body .list-card-details {
  padding: 4px 8px;
}
 
 
/* display card id */
 
body .card-short-id.hide {
  position: absolute;
  top: 0;
  left: 0;
  display: block;
  height: 16px;
  line-height: 16px;
  padding: 0 3px;
  font-weight: bold;
  font-size: 11px;
  background: gold;
  border-radius: 3px 0;
}
 
body .has-cover .card-short-id.hide {
  border-radius: 0 0 3px 0;
}
 
body .list-card-title {
  margin: 16px 0 10px;
}
 
 
/* bottom edit link */
 
body .list-card-operation {
  top: auto;
  right: -2px;
  bottom: -2px;
  padding: 1px;
  border-radius: 3px;
  box-shadow: 1px 1px rgba(0,0,0,.3), -1px -1px white;
}
 
body .list-card {
  margin-top: 10px;
}
 
body .list-card:first-child {
  margin-top: 2px;
}
 
 
/* display total cards count */
 
body .list-header-num-cards.hide {
  display: inline-block;
  font-size: 11px;
}



/*TRELLO RESPONSIVE - Trello Responsive - Origgami */

#board{
    display:inline-block !important;
    text-align:center;    
    position:relative !important;
}

.board-canvas{
    height:auto !important
}
.body-board-view{
    overflow-y:visible
}
.js-list{
    display:inline-block !important;
    vertical-align:top;
    margin:0 0 10px 10px;    
}
.u-gutter{
    margin-left:0;
}



/*Trello theme: plain boxes*/
html, body, input, select, textarea {font-size: 13px !important;} /* instead of 14px */

/**************** HEADER *****************/

/* header bar */
#header {background: #2B2B2B !important;}

/* header buttons */
.header-btn.header-notifications, .header-btn.header-boards, 
.header-btn.header-search, .header-btn.header-login, 
.header-btn.header-signup {
    background: #6B6B6B !important;
    }
.header-btn:hover {background: #868686 !important;}

/* header search box */
#header-search input[type="text"] {background: #6B6B6B !important;}
#header-search input[type="text"]:focus {background: #fff !important;}

/* board title */
#board-header .board-name {font-weight: normal !important;}

/**************** BOARD*****************/

/* board background */
#board {
    background: #fff !important; 
    box-shadow: 0 0 2px #C7C7C7 !important;
    padding-top: 20px;
    white-space:initial;
}


/**************** LISTS - container of cards *****************/

/* list */
.list {
    border: none !important;
    background: transparent !important;
    box-shadow: none !important;
    border-radius: 0 0 0 0 !important;
    }
/* list title */
.list-title {margin-left: 11px !important;}
.list-title h2 {
    color: #1F86C8 !important;          /* title text color */
    text-transform: uppercase !important; 
    font-size: 14px !important; 
    line-height: 24px !important;
    font-weight: bold !important;
    letter-spacing: 1px !important;
    }
/* list top and bottom divider */
.list-gradient-top, .list-gradient-bottom {background: none !important;}
/* card labels */
.list-card-labels .card-label {
    border: 1px solid white;
    border-radius: 0;
}

/* "Add a card" link */
.open-card-composer {
    padding: 7px 14px !important;
    margin-left: 8px !important;
    margin-right: 8px !important;
    text-transform: uppercase !important;
    font-size: 10px !important;
    border-radius: 0 !important;
    }
.open-card-composer:hover {
    background: #646464 !important; 
    color: #fff !important;
    text-decoration: none !important;
    }
/* "Add a list" link */
.list-wrapper.mod-add.is-idle {
    background-color: white;
    border-radius: 0;
    color: #8c8c8c;
}
.list-wrapper.mod-add.is-idle:hover {
    background: #646464 !important; 
    color: #fff;
}
.list-wrapper.mod-add .placeholder {
    text-transform: uppercase !important;
    font-size: 10px !important;  
    color: inherit;
}

/**************** Individual CARDS / items *****************/

/* card - default state */
.list-card {
    background: #E6E6E6 !important;     /* card default background color */
    border-color: #D6D6D6 !important;
    border: none !important;
    border-bottom: 0px solid #ddd !important;
    border-radius: 2px 2px 2px 2px !important;
    padding: 6px 8px 5px !important;
}

/* card - on hover/selected */
.list-card.selected, .list-card.active-card {      
    background: #E8E8E8 !important;     /* card background color on hover */
    box-shadow: none !important;
    }
/* transparent background of icon (arrow down on hover) */
.list-card .card-operation {background-color: transparent !important;}
/* badge complete */
.badge.complete {background: #1F86C8 !important; border-color: #1F86C8 !important;}
/* label colors */
.list-card .card-labels .card-label {
    border: 1px solid white !important;
    border-width: 0 1px 1px 0px !important;
    margin-right: 0 !important;
    }
.list-card .card-labels .card-label:first-child {border-left-width: 1px !important;}




/**************** CARD CONTENT (back side, popup window) *****************/

.window {
    background-color: #f7f7f7;
} 
/* labels */
.card-label {text-shadow: none !important;}

/* card content buttons */
.button-link:hover {
    background: -moz-linear-gradient(center top , #676767 0%, #3F3F3F 100%) repeat scroll 0 0 transparent !important;
    border-color: #3F3F3F !important;
    }

/* More links */
.pop-over-list li > a:hover,
.uploader:hover .fakefile {background: #424242 !important; border:none !important;}

/* button for comment etc */
input.primary[type="submit"], input.primary[type="button"], 
button.primary, .button.primary {
    background: -moz-linear-gradient(center top , #676767 0%, #3F3F3F 100%) repeat scroll 0 0 transparent !important; }

/* image/attachment icon and background */
.attachment-list li .preview .check-icon {background-color: #676767 !important;}
.attachment-list li .preview.is-cover {background-color: #676767 !important;}

/**************** TOGGLE BAR *****************/

/* toggle arrow on the right */
.extra-large-window .board-wrapper.disabled-all-widgets .board-side-btn.right-side .arrow, 
.large-window .board-wrapper.disabled-all-widgets .board-side-btn.right-side .arrow, 
.medium-window .board-wrapper.disabled-all-widgets .board-side-btn.right-side .arrow {
    border-right-color: #757575 !important;} /* open arrow */ 
.extra-large-window .board-side-btn.right-side .arrow, 
.large-window .board-side-btn.right-side .arrow, 
.medium-window .board-side-btn.right-side .arrow {
    border-left-color: #757575 !important;} /* close arrow */ 
/* toggle bar */
.extra-large-window .board-side-btn:hover, 
.large-window .board-side-btn:hover, 
.medium-window .board-side-btn:hover {background: #DADADA !important;}

/**************** SIDE MENU *****************/
.board-menu {
    background-color: #f7f7f7;
}

/*HIDE COMMENT ACTIVITIES*/

.js-list-actions{
  display: none !important;
}

.new-comment{
  display: none;
}

/*CUSTOM*/

.window-main-col{
	
	float: left;
    overflow-x: hidden;
    overflow-y: auto;
    margin: 0;
    min-height: 20px;
    padding: 24px 11pt 30pt;
    position: relative;
    width: 95%;
    z-index: 0;
    text-align: center;
}

.window-sidebar{
	float: right;
    padding: 8px 9pt 9pt 0;
    width: 95%;
    overflow: hidden;
    z-index: 10;
}

.window-sidebar .button-link{
	float : left;
}

.add-comment-section{
	display : none;
}

.icon-activity{
 	display : none;
}

.icon-activity h3{
 	display : none;
}

.card-detail-item-labels{
	text-align : center;
}

.js-card-detail-labels-list {
	text-align : center;
    margin-left:  220px;
    width: 100%;
}

.editable .current{
 cursor : initial;
}

}
