css:
@-moz-document url-prefix("https://m.facebook.com"), url-prefix("https://mobile.facebook.com"), url-prefix("https://touch.facebook.com"), url-prefix("https://mbasic.facebook.com"), url-prefix("https://x.facebook.com"){
/*
Name: m.facebook.com Corak Debug (2015.06.07)
Author: Corak the Avatar
Description: http://www.facebook.com/corakavatar
*/

/* m.facebook mobile */
.e #viewport
	{max-width: 100%!important}
#m_newsfeed_stream ._4prr,
#m_newsfeed_stream ._5sgg,
#m_newsfeed_stream ._-_b,
#m_newsfeed_stream ._403j
	{max-width:100%!important;
	max-height:100%!important}
#partial-side-menu .item .content
	{padding:0!important;
	height:10px!important;
	line-height:0!important}

#partial-side-menu .item
	{height:22px!important}
#mFinchContainer .tlUnit, 
.touch .storyStream > article .msg,
.touch .inlineStatusBoxContainer,
.touch .storyStream .storyAggregation.carded,
.touch .storyAggregation.carded .caption
	{padding:0!important;
	margin:0!important}
.touch .storyStream > article
	{padding:0!important;
	margin:0 0 4px!important}
#m_newsfeed_stream ._5rgt,
#m_newsfeed_stream ._5rgu
	{padding:0!important}

._5t7n,	
._1otp,
#mFinchContainer .tlAboveUnit,
#mFinchContainer .tlUnitContent,
#mFinchContainer ._5j40,
._5gh8 ._147s,
._5gh8 ._291x,
.touch .mSideMenu .icon,
#partial-side-menu .item .image .touch .mSideMenu .icon
	{margin:0!important}
#partial-side-menu .item .image
	{height:10px!important}
.slim #blueBar,
.fbx #pageHead, #blueBar #pageHead
	{height:auto!important;
	padding:0!important}
#mFinchContainer .tlAttachment,
.-cx-PRIVATE-webMessengerComposer__root
	{padding:0!important}
#viewport #root,
.timelineLayout #contentArea,
.timelineLayout #contentArea ._2w3,
.fbStarGrid.fbPhotosRedesignBorderOverlay.fbStarGridAppendedTo
	{width:100%!important}
#viewport .maxwidth
	{max-width:100%!important}

.touch ._55wr
	{padding:1px}
	
/* Photo */	
#viewport .touch ._55wr
	{padding:0!important}
	
#viewport ._15ks ._15kl._15kl
	{width:33%!important;
	padding:0!important}
	
/* m.fb.com Profile size fix */
#m_timeline_cover_section div.navItem
	{margin:0!important}

/* m.facebook Menu */
div.mSideMenu [id*='.reactRoot[0].[0].[0].[1].[0].[0]{app}'],
div.mSideMenu [id*='.reactRoot[0].[0].[0].[1].[0].[0]{group}.[0]'],
div.mSideMenu [id*='.reactRoot[0].[0].[0].[1].[0].[0]{group}.[1]'],
div.mSideMenu [id*='.reactRoot[0].[0].[0].[1].[0].[0]{friend_list}'],
div.mSideMenu [id*='.reactRoot[0].[0].[0].[1].[0].[0]{page}'],
div.mSideMenu [id*='.reactRoot[0].[0].[0].[1].[0].[0]{interest_list}'],
div.mSideMenu div.apl,
div.mSideMenu nav.mSideMenu
	{display:none}
.mSideMenu .item .image
	{height:auto!important}
.mSideMenu .item .content
	{height:auto!important;
	padding:0!important}
#composer_form div.acw,
#messageGroup .touch .voice
	{padding:0!important;
	margin:0!important}
#messageGroup .touch .mfss
	{margin:0!important}
#m_newsfeed_stream i.img[id*='u_']
	{width:100%!important}
#viewport #ufiCommentList div.ufiComment
	{padding:0!important}
#viewport #ufiCommentList .actions
	{margin-top:0!important}
#viewport ._5rgr .widePic 
	{margin:0 -10px!important}
#viewport ._stb._stb
	{position:relative!important}

#viewport ._5rgt p,
#viewport ._5t8z p {z-index:200}

#viewport ._5msi a._5msj
	{display:none!important}
	
}
