css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mantis.infogones.com") {
  
  body, td, span, option, input, button{
    font-family: "Segoe UI", sans-serif;
  }
  
  html{
    background: #FEFEFE url("http://subtlepatterns2015.subtlepatterns.netdna-cdn.com/patterns/crossword.png");
  }
  
  
  a{
    color: #1e69de;
  }
  a:hover{
    color: #6db3f2;
  }
  
  body{
    background: #FEFEFE;
    max-width: 1680px;
    margin: 0 auto;
    padding: 0 10px;
    border: 1px solid #CCC;
    border-width: 0 1px;
    box-shadow: 0 0 10px rgba(0,0,0,.125);
    min-height: 100vh;
  }
  
  table{
    border-collapse: collapse;
  }
  
  table.width100, table.width50{
    border-color: transparent;
    background: transparent;
    border: none;
  }
  
  form[name=bug_action]{
    display: block;
    padding: 0 3px;
  }
  
  #buglist tr{
    border-bottom: 1px solid #EEE;
  }
  
  #buglist tr:last-of-type{
    background: rgb(238,238,238);
    border-radius: 0px 0px 5px 5px;
  }
  
  #buglist tr td{
    padding: 5px 3px;
    vertical-align: middle;
  }
  
  #buglist tr[bgcolor] td:nth-of-type(-n+4){
    text-align: center;
  }
  
  #buglist tr td[bgcolor="#999999"]{
    font-size: 0;
  }
  
  .spacer, .spacer td{
    height: 0;
    display: none;
  }
  
  td.menu{
    color: rgba(0,0,0,0);/* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#f2f5f6+0,e3eaed+37,c8d7dc+100;Grey+3D+%234 */
    background: rgb(238,238,238);
    border-radius: 5px 0 0 5px;
  }
  
  td.menu.right{
    border-radius: 0 5px 5px 0;
  }
  
  td.menu a {
    display: inline-block;
    text-decoration: none;
    padding: 5px;
    border-radius: 5px;
    color: #1e69de;
  }
  
  td.menu a:hover{
    background: linear-gradient(to bottom, rgba(109,179,242,1) 0%,rgba(84,163,238,1) 50%,rgba(54,144,240,1) 51%,rgba(30,105,222,1) 100%);
    color: white;
    text-shadow: 1px 1px 3px rgb(34,50,65);
    box-shadow: 0 1px 0px rgb(34,50,65);
  }
  
  tr.row-category{
    background: rgba(238,238,238,1)
  }
  
  td.category{
    background: rgba(238,238,238,1);
   
  }
 

  tr.row-category2{
    background: #EEE;
    background: rgb(238,238,238);
    background: linear-gradient(to bottom,  rgba(238,238,238,1) 0%,rgba(204,204,204,1) 100%);
  }
  
  form#filters_form_open tr.row-category2 td{
    border: 1px solid #CCC;
    border-bottom: none;
  }
  
  form#filters_form_open tr.row-1 td{
    border: 1px solid #CCC;
    border-top: none;
    background: #FEFEFE;
  }
    
  form#filters_form_open table{
    background: rgb(238,238,238);
  }
  
  form#filters_form_open tr:last-of-type, #filter_closed tr:last-of-type{
    border-radius: 0px 0px 5px 5px;
    background: rgb(238,238,238);
    box-shadow: 0 2px 0 rgba(127,127,127,.25);
  }
  
  tr.bugnote{
    border-bottom: 1px solid #FFF; 
  }
  
  td.bugnote-public, td.bugnote-private{
    width: 15%;
  }
  
  td.bugnote-public{
    background: #E8E8E8;
  }
  
  td.bugnote-private, td.bugnote-note-private{
    background: #CCC;
  }
  
  td.bugnote-public input, td.bugnote-private input{
    font-size: 10px;
    padding: 2px;
  }
  
  td.form-title{
    background: rgb(238,238,238);
    background: linear-gradient(to bottom,  rgba(238,238,238,1) 0%,rgba(204,204,204,1) 100%);
    border-radius: 5px 5px 0 0;
    padding: 5px;
  }
  
  input[name=search]{
    min-width: 250px;
  }
  
  
  button, input[type="reset"], input[type="button"], input[type="submit"], input.button{
    border:none;
    border-radius: 3px;
    padding: 3px 5px;
    background: linear-gradient(to bottom, rgba(109,179,242,1) 0%,rgba(84,163,238,1) 50%,rgba(54,144,240,1) 51%,rgba(30,105,222,1) 100%);
    color: white;
    text-shadow: 1px 1px 3px rgb(34,50,65);
    box-shadow: 0 1px 0px rgb(34,50,65);
    vertical-align: top;
    cursor: pointer;
    min-height: 19px;
  }
  
  input[type=text], input[type=password], select{
    vertical-align: middle;
    min-height: 20px;
  }
  
  input[type=checkbox], input[type=radio]{
    border: 1px solid red;
  }
  
  option{
    min-height: 20px;
    vertical-align: middle;
  }
  
  button:hover, input[type="reset"]:hover, input[type="button"]:hover, input[type="submit"]:hover {
    background: linear-gradient(to bottom, rgba(160,202,239,1) 0%,rgba(106,174,237,1) 50%,rgba(105,172,239,1) 51%,rgba(30,105,222,1) 100%);
    box-shadow: 0 1px 0px rgb(68,100,130);
  }
  
  
  /* End of view issue section */
  #bugnote_add_open table tr:last-of-type td, table.width50 tr:last-of-type td{
    background: #E8E8E8;
    background: linear-gradient(to bottom,  rgba(238,238,238,1) 0%,rgba(204,204,204,1) 100%);
    border-radius: 0 0 5px 5px;
  }
  
  
  /* Responsive ? */
  @media (max-width: 768px) {
    #buglist td{
      display: inline;
    }
    
    td.menu{
      display: block;
      width: auto;
      border-radius: 0 !important;;
    }
    table.hide td{
      display: block;
      width: auto;
    }
    
    table.hide{
      margin-bottom: 1em;
    }
    
    table.hide td.login-info-left{
      text-align: center;
    }
    
    body > div{
      text-align: center;
    }
  }
  
}
