css:
@-moz-document domain("idownloadblog.com") {
body {
    background-color:white;
    color:black;
}
a { 
    color:black;
    font-weight:700;
}
a:visited  { 
    color:black;
    font-weight:700;
}


#top_banner {
    content:url(http://i.imgur.com/ELAwzMM.jpg);
    width:100%;
    background-color:white;
    padding-top:25px;
    padding-bottom:25px;
    padding-right:500px;
    padding-left:500px;
    margin-right:-475px;
}


#logo {
	display:none;
}

.top_posts {
	display:none;
}

#related_posts h3 {
	display:none;
}

#top_nav {
	background:black;
}

ul li {
	color: red;
}

.related_post, .ad300x600, .single_share_bar, #related_posts {
	display:none;
}

#featured_posts {
	display:none;
}


#sidebar {
	display:none;
}

.related_post li a:hover {color:red;text-decoration:none !important;}

.most_recent_posts h4 a:hover {color:red;text-decoration:none !important;}



h1 a:visited, h2 a:visited, h3 a:visited, h4 a:visited{color:black;}


.container_24 .grid_16 {
	margin-left:170px;
    padding-top:50px;
}


#footer, #copyright, .right, .more-link {
    display: none;
}


.wp-pagenavi {
    color: black;
    margin: auto;
    width: 50%;
    padding-left: 100px;
}


.wp-pagenavi {clear: both;}
.wp-pagenavi {color:black;font-weight:700;margin-bottom:10px;}
.wp-pagenavi a, .wp-pagenavi span {text-decoration: none;border: 1px solid black;padding: .5em .5em;margin: 2px;font-size:1.5em;background:none;}
.wp-pagenavi a:hover, .wp-pagenavi span.current {border: 2px solid black}
.wp-pagenavi span.current {font-weight: bold;}



.clear {
    display: none;
}
}
