css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(wikitetasforo.com) {
body {
    font-size:1em !important;
    line-height: 1.1 !important;
  }
  *.featured-topic {
    margin: 0px !important;
  }
  *.emoji {
    width: 1.1em !important;
    height: 1.1em !important;
  }
  #main-outlet, *.wrap {
    max-width: 100% !important;
  }
  #list-area {
    margin: 0px !important;
  }
  *.topic-body {
    width: -moz-calc( 100% - ( 15em + 45px ) ) !important;
    width: calc( 100% - ( 15em + 45px ) ) !important;
  }
  *.timeline-container {
    margin: 0px !important;
    right: 0px !important;
    width: 15em;
  }
.container{
    margin-left: 200px;
    margin-right: 200px;
}
/* Buscador */
.search-title{
    margin: 0 auto;
    max-width: 100%;
	width: -moz-calc( 100% - ( 15em + 45px ) ) !important;
    width: calc( 100% - ( 15em + 45px ) ) !important;
}
.fps-result{
    margin: 0 auto;
    max-width: 100%;
	width: -moz-calc( 100% - ( 15em + 45px ) ) !important;
    width: calc( 100% - ( 15em + 45px ) ) !important;
}
.search-footer{
	margin-top: 20px;
}
div .search .row .clearfix{
	margin-left: 200px;
}
}
