css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(wikitetasforo.com) {
body {
    background-color:#000000;
    color: #c0c0c0;
    font-size:1em !important;
    line-height: 1.1 !important;
  }
.topic-list.categories .category h3 a[href]{
	color:#c0c0c0;
}
.nav-pills>li>a{
	color:#c0c0c0;
}
#reply-control.open .grippie{
	background: green;
}
#reply-control{
	background: #000000;
}
*.topic-body{
    padding: 10px;
	background: #333333;
}
#topic-title h1 a{
	color:#c0c0c0;
}
  *.featured-topic {
    margin: 0px !important;
  }
  *.emoji {
    width: 1.1em !important;
    height: 1.1em !important;
  }
  #main-outlet, *.wrap {
    max-width: 100% !important;
  }
  #list-area {
    margin: 0px !important;
  }
  *.topic-body {
    width: -moz-calc( 100% - ( 15em + 45px ) ) !important;
    width: calc( 100% - ( 15em + 45px ) ) !important;
  }
  *.timeline-container {
    margin: 0px !important;
    right: 0px !important;
    width: 15em;
  }
.container{
    margin-left: 200px;
    margin-right: 200px;
}
}
