css:
@-moz-document domain("allegro.pl") {
div#installments-banner-container,
div.instalment-wrap,
div.main-nav,
li.subservices-links,
li.new-item,
h2.logo {
    display: none !important;
}

div.main-wrapper > div.vela {
    /*height: 90px;*/
}

.delivery-and-payment > .row.body {
    border: 1px solid rgba( 200, 200, 200, 128);
}

div#maWrapper {
    margin-top: 0;
}

ul.main-tabs {
    padding-top: 0 !important;
    margin-bottom: 0 !important;
}

ul.main-tabs > li {
    padding-top: 0 !important;
}

div#maContent > h2.themeHead {
    padding: 0;
    margin: 0;
    position: relative;
    top: 20px;
    left: 10px;
}

#pagecontent1 div#maContent > h2.themeHead {
    top: 0;
}

img.m-brands-allegro {
    display: none !important;
}

ul#breadcrumbs-list {
    left: -35px;
    top: -6px;
}

input[type=checkbox] {
    transform: scale(2);
    position: relative;
}

table#items-list input[type=checkbox] {
    left: 10px;
    top: 10px;
}

form[name=remove_form] {
    margin-top: 40px;
}

div.attributes-container {
    padding: 0;
    margin: 0;
}

div.user-links-wrapper {
    top: 60px;
}

div#search-bar > div {
    padding-left: 300px;
}

div.header-namespace {
    position: relative;
    top: -40px;
}

div#showitem-main {
    position: relative;
    top: -40px;
}

input#main-search-text {
    padding-top: 10px;
    padding-bottom: 10px;
}

input.search-btn {
    height: 41px !important;
}

.vela .main-search .search-layer label input {
    left: -2px;
}

div.listing-module {
    top: -40px;
}

.vela .wrapper.breadcrumbs-wrapper {
    top: -65px;
}

div.user-links-wrapper,
div.wrapper-fluid,
ul.user-nav {
    display: inline !important;
}

div.img-main-wrapper {
    border: 0 !important;
}

ul.pagination {
    margin: 0 !important;
}

div.listing-sorter > dl {
    margin-bottom: 0;
}

table#items-list th {
    padding-top: 0;
    padding-bottom: 4px;
}

.buynow-container .main > div > .row,
.buynow-container,
.buynow-container > .row {
    padding: 0;
}

.nav.main-tabs > li {
    padding-top: 0;
}

ul#ma-layer {
    left: 0px;
    width: 150px;
}
}
