.p_stable,.cons_stable{ position:absolute !important; z-index:2 !important; left:65px !important; top:207px !important; }
.p_garage,.cons_garage{ position:absolute !important; z-index:2 !important; left:287px !important; top:280px !important; }
.p_church,.cons_church{ position:absolute !important; z-index:2 !important; left:391px !important; top:99px !important; }
.p_church_f,.cons_church_f{ position:absolute !important; z-index:2 !important; left:390px !important; top:99px !important; }
.p_snob,.cons_snob{ position:absolute !important; z-index:2 !important; left:159px !important; top:63px !important; }
