/* NPCs */
.smith_anim{ position:absolute !important; z-index:3 !important; left:174px !important; top:322px !important;}

/* xmas-tree */
.christmas_tree{ position:absolute !important; z-index:2 !important; left:499px !important; top:188px !important; }



div.label { background-color: #668833 !important; padding: 1px !important; border: 1px black solid !important; min-width:20px !important; color: white !important}
div.label_night { background-color: #202060 !important; padding: 1px !important; border: 1px black solid !important; min-width:20px !important; color:white !important}
div.label a:link	{ color:#DDDDDD !important }
div.label a:visited	{  color:#DDDDDD !important }
div.label a:active	{  color:#DDDDDD !important }
