div.label_night a:link { color:#CCCCCC !important}
div.label_night a:visited { color:#CCCCCC !important}
div.label_night a:active { color:#CCCCCC !important}
div.label_night a:hover { color:white !important}
div.label img { display:none !important; }
div.label_night img { display:none !important; }

.inv_empty{position:absolute !important; z-index:3 !important;width:600px !important; height:430px !important;}

.inv_map{position:absolute !important; z-index:2 !important;}

.inv_archer{top:157px !important;left:193px !important;}
.inv_axe{top:257px !important;left:128px !important;}
.inv_catapult{top:0px !important;left:38px !important;}
.inv_heavy{top:24px !important;left:76px !important;}
.inv_light{top:56px !important;left:245px !important;}
.inv_marcher{top:217px !important;left:483px !important;}
.inv_ram{top:157px !important;left:361px !important;}
.inv_spear{top:43px !important;left:99px !important;}
.inv_snob{top:276px !important;left:398px !important;}
.inv_spy{top:264px !important;left:340px !important;}
.inv_sword{top:208px !important;left:336px !important;}



/* Forum */
.forum { display:inline-block !important; background-color:#F1EBDD !important; margin-bottom:4px !important; padding:1px !important; border:1px solid #000 !important; white-space:nowrap !important; line-height:150% !important; }
.selected { background-color:#FADC9B !important;}

.shared_forum { display:inline-block !important; background-color:#DDDDDD !important; margin-bottom:4px !important; padding:1px !important; border:1px solid #000 !important; white-space:nowrap !important; line-height:150% !important; }
.shared_selected { background-color:#b7c3c9 !important;}

table.map_container { background-color: #DDCCAA !important; border: 1px solid #000000 !important; }
table.map td { font-size:8pt !important; text-align:center !important; vertical-align:middle !important;padding:0 !important;}
table.map div.lay1 { position: relative !important; z-index: 1 !important; left: 10px !important; top: 0px !important; }
table.map div.lay2 { position: relative !important; margin-top:-15px !important; }

#info_bonus_image{background-image:url(http://de35.die-staemme.de/graphic/background/content.jpg) !important;}

td.space-left { border-left:1px solid #175E36 !important; } /* zwischen normalen Feldern */
td.space-bottom { border-bottom:1px solid #175E36 !important; }
td.border-left { border-left:1px solid #103530 !important; } /* "Sektorgrenzen" S1/S2/S6 */
td.border-bottom { border-bottom:1px solid #103530 !important; }
td.sector-left { border-left:1px solid #DED3B9 !important; } /* Sektorgrenzen */
td.sector-bottom { border-bottom:1px solid #DED3B9 !important; }

td.space-left-new {}
td.space-bottom-new {}
td.border-left-new { border-left:1px solid #214B18 !important; }
td.border-bottom-new { border-bottom:1px solid #214B18 !important; }
td.sector-left-new { border-left:1px solid #DED3B9 !important; }
td.sector-bottom-new { border-bottom:1px solid #DED3B9 !important; }
td.con-left-new { border-left:1px solid #000000 !important; }
td.con-bottom-new { border-bottom:1px solid #000000 !important; }

tr.center td { text-align:center !important; }
tr.lit td { font-weight:bold !important; background-color:#F0D49A !important; background-image:none !important;}
tr.lit2 td { font-weight:bold !important; background-color:#F0E6C8 !important; background-image:none !important;}
tr.admin td { background-color:#F0E6C8 !important; }
tr.mini td { font-style: italic !important; font-size: x-small !important }
th		{ font-size:9pt !important; text-align: left !important; font-weight:bold !important; background-color:#DFCCA6 !important; }
.top_background { background-image: url(http://de35.die-staemme.de/graphic/index/bg-top.jpg) !important; background-repeat: repeat-x !important; height: 27px !important; width: 100% !important; position: absolute !important; top:0 !important; z-index: -1 !important; }
body	{ background-color: #d2c09e !important; background-image:url(http://de35.die-staemme.de/graphic/background/bg-tile.jpg) !important; font-size:9pt !important; font-family: Verdana, Arial !important; width:100% !important; margin: 0 !important; }
/* !!! ACHTUNG !!! 'Wenn css nicht als UTF-8 gespeichert, dann wird dies hier kaputt gehen !!! */
h1		{ font-size:16pt !important;}
h2		{ font-size:14pt !important;}
h3      { font-size:12pt !important; font-weight:bold !important; }
h4      { font-size:10pt !important; font-weight:bold !important; font-style:italic !important; margin-bottom:4pt !important}
h5      { font-size:10pt !important; font-weight:bold !important; margin-bottom:2pt !important}
img   { border: none !important; }
img.middle { vertical-align:middle !important; }

.grey  { color: gray !important; }
.warn  { color: rgb(220, 0, 0) !important; }
.error { color: rgb(180, 0, 0) !important; font-weight:bold !important; }
.inactive { color: #C5A76B !important; }
.nowrap { white-space: nowrap !important; }
.small { font-size: xx-small !important; }

hr {
	border-color : #997733 transparent #FFEECC transparent !important;
	border-width : 1px 0 !important;
	border-style : solid !important;
}

table.box {
	background-color:#F7EED3 !important;
	background-repeat: repeat-x !important;
	border: 1px solid !important;
	border-color:#997733 #FFDD99 #FFEECC #BB9955 !important;
	vertical-align: middle !important;
}

table.box tr td { white-space:nowrap !important; }

input, select { font-size: 8pt !important; /*background-color:#FDFBF3 !important;*/ }

input.attack { background-color: rgb(250, 210, 210) !important }
input.support { background-color: rgb(210, 250, 210) !important }

input.check { margin:0px !important; margin-right:5px !important; }

table.quote td.quote_author { font-weight: bold !important; font-size: 8pt !important; }
table.quote td.quote_message { background-color:#FFFFFF !important; font-size: 8pt !important; }

.post { border: 2px solid #804000 !important; background-color: #f8f4e8 !important; padding:0px !important; margin-bottom:5px !important;}
.igmline { background-color:#efe6c9 !important; padding-left:4px !important; border-bottom:1px solid #804000 !important; position:relative !important;}
.text { padding:6px !important; }
.date { font-size:x-small !important; right:5px !important; position:absolute !important;}
.right { right:5px !important; position:absolute !important; }

#igm_groups { position:absolute !important;z-index:100 !important; border: 2px solid #804000 !important; background-color:#efe6c9 !important;}
#igm_groups td { padding:2px !important; }

#bb_sizes { position:absolute !important;z-index:100 !important; border: 2px solid #804000 !important; background-color:#efe6c9 !important; top: 24px !important; left: 200px !important;}
#bb_sizes td { padding:2px !important; }

#igm_to { position:absolute !important; z-index:1 !important; border: 2px solid #804000 !important; background-color:#DED3B9 !important;}
#igm_to_content { background-color:#efe6c9 !important; height: 250px !important; width:200px !important; overflow:auto !important}
#igm_to td { padding:2px !important; }

#box{
color:black !important;
background-color : #F8F4E8 !important;
width:100% !important;
height:200px !important;
text-align:left !important;
padding : 0px !important;
overflow:auto !important;
position:relative !important;
}

#inline_popup {
	border: 2px solid #804000 !important;
	background-color:#ded3b9 !important;
	position:absolute !important;
	display: none !important;
	width:298px !important;
	z-index:5 !important;
}
#inline_popup_menu{
	width:100% !important;
	text-align:right !important;
}
#inline_popup_main{
	overflow:auto !important;
	height:300px !important;
	width:288px !important;
	background-image: url('http://de35.die-staemme.de/graphic/background/content.jpg') !important;
	padding:3px !important;
}

ul.help{padding-left:1px !important; margin-left:0px !important}
li.help{list-style-type:none !important; background-color:#DED3B9 !important; margin:2px !important; padding-left:8px !important; padding-top:4px !important; padding-bottom:4px !important;}

ul.help li.help ul.help li.help{ background-color:#F1EBDD !important; }
ul.help li.help ul.help li.help ul.help li.help { background-color:#F8F4E8 !important; }
/*ul.help ul.help ul.help{background-color:#F8F4E8 !important;}*/

ul.submenu{padding-left:1px !important; margin-left:0px !important}
li.submenu{list-style-type:none !important; margin:2px !important; padding-left:8px !important; padding-top:4px !important; padding-bottom:4px !important;}

a.sel { color:#0082BE !important; text-decoration:underline !important;}

.bonus_icon{background-image:url(http://de35.die-staemme.de/graphic/overview/bonus_icons.png) !important;width:14px !important;height:14px !important;display:inline-block !important;margin-left:2px !important;}
.bonus_icon_1{background-position:0 0 !important}
.bonus_icon_2{background-position:0 98px !important}
.bonus_icon_3{background-position:0 84px !important}
.bonus_icon_4{background-position:0 14px !important}
.bonus_icon_5{background-position:0 56px !important}
.bonus_icon_6{background-position:0 42px !important}
.bonus_icon_7{background-position:0 28px !important}
.bonus_icon_8{background-position:0 70px !important}

.server_info{
	font-size: 7pt !important;
	margin-top:0px !important;
	margin-bottom:0px !important;
}


}
