css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("spanishdict.com") {
	body { background-color: rgb(35, 35, 35); }

    .nav-content, .nav-content-item {
        background-color: rgb(45, 45, 45);
    }

    .nav-content-item {
        border-color: rgb(45, 45, 45);
    }

    .nav-content-item a:hover {
        color: white;
        text-shadow: 1px 1px 2px rgb(226, 226, 226);
    }

    .usage-note {
        background-color: rgb(45, 45, 45);
    }

    .card {
        color: rgb(226, 226, 226);
        background-color: rgb(53, 53, 53);
        border: 1px solid rgb(76, 76, 76);
    }

    .nav-content-item.nav-active {
        background-color: rgb(45, 45, 45);
        border-left-color: rgb(45, 45, 45);
    }

    .nav-content-item.nav-active a, .nav-content-item > a:active a, .nav-content-item > a:hover a {
        color: rgb(230, 230, 230);
    }

    span.conj-irregular {
        color: rgb(245, 0, 0);
    }

    .conj-match {
        background-color: rgb(42, 42, 42);
    }

    .vtable {
        border-color: rgb(170, 170, 170);
    }

    .vtable tr:hover {
        background-color: #222;
    }

    a.vtable-title-link {
        color: rgb(240, 240, 240);
    }

    .input-table-table td table td button {
        color: rgb(226, 226, 226);
        background-color: #222;
    }

    .input-table-table td table td button:hover {
        background-color: #444;
    }

    .dictionary-neodict .def, .dictionary-neoharrap .def {
        color: rgb(226, 226, 226);
    }

    .search-form-parts {
        border-color: rgb(76, 76, 76);
    }

    .search-form-input {
        color: rgb(226, 226, 226);
    }
  
    .search-form-input {
        background-color: rgb(53, 53, 53);
    }

    .dictionary-entry a:not(.dictionary-neodict-translation-translation) {
        color: rgb(226, 226, 226);
    }

    .dictionary-quickdef-only-text, .dictionary-untext-en, .dictionary-untext-es {
        color: rgb(240, 240, 240) !important;
    }

    .sdmd-accent-box-text {
        background-color: #2a5175 !important;
        border-color: #2a8b9f !important;
    }

    a.translate-quicktip {
        color: rgb(240, 240, 240) !important;
    }

    a.translate-quicktip strong {
        color: #59a3e6;
    }

    .dictionary-line {
        border-top-color: rgb(76, 76, 76);
    }

    .search-form-parts {
        background-color: #2f2f2f;
    }

    .search-form-parts.search-expand .search-form-input {
        border-bottom-color: rgb(76, 76, 76);
    }

    /* Language switch */

    .lang-tabs > li.active > a,
    .lang-tabs > li.active > a:hover {
        background-color: #353535;
        color: rgb(226, 226, 226);
    }

    .lang-tabs > li > a:hover {
        background-color: rgb(35, 35, 35);
        color: rgb(206, 206, 206) !important;
    }

    .lang-tabs,
    .lang-tabs > li {
        border-color: rgb(76, 76, 76);
    }

    .dictionary-collins .ph .c {
        color: rgb(226, 226, 226);
    }

    /* Search suggestions */

    .input-table-table td table td button {
        /* background-color: #353535; */
        /* color: rgb(226, 226, 226); */
    }

    /* Examples */

    .megaexamples-highlight {
        color: rgb(226, 226, 226);
        background-color: rgb(100, 100, 100);
    }

    .megaexamples-row,
    .megaexamples-row + .megaexamples-row,
    .megaexamples-row + .megaexamples-pagination,
    .megaexamples-page {
        border-color: rgb(106, 106, 106);
    }

    .megaexamples-page:first-child {
        background-color: rgb(100, 100, 100);
    }

    .megaexamples-container a {
        color: #63a0d7;
    }

    .megaexamples-page:hover {
        background-color: rgb(100, 100, 100);
    }

    .megaexamples-row:not(:last-child)  {
        border-bottom-color: rgb(76, 76, 76);
    }

    .megaexamples-show-more {
        border-top-color: rgb(76, 76, 76);
    }

    /* Conjugations */

    .vtable, .vtable-title, .vtable-word {
        border-color: rgb(120, 120, 120);
    }

    /* Sidebar */

    .fluencia-sidebar-title-short {
        color: rgb(220, 220, 220);
    }

    .wotd-sidebar-word {
        color: rgb(220, 220, 220);
    }

    .lg-article {
        border-bottom-color: rgb(76, 76, 76);
    }

    /* Guide: you in spanish (http://www.spanishdict.com/guide/you-in-spanish) */

    .sdmd-table-header {
        background-color: rgb(45, 45, 45) !important;
    }

    .sdmd-examples tr:nth-child(2n+1), .sdmd-table tr:nth-child(2n+1) {
        background-color: #404040;
    }

    .sdmd-examples-container, .sdmd-table-container,
    .sdmd-table-header th, .sdmd-table td {
        border-color: rgb(120, 120, 120);
    }

    .sdmd-accent-box-info .sdmd-accent-box-text {
        background-color: rgb(80, 80, 80);
        border-color: rgb(75, 75, 75);
    }
}
