css:
@-moz-document domain("gossipcop.com") {
h2 a{
    background-color: #000;
    color: #0057FC !important;
    }

#content {
    background-color: black;
    color: #909090;
}

.post a {
    font-family: "Comic Sans MS", cursive, sans-serif;
    font-weight: bold;
}

a:hover, a:hover *:not(img) {
    color: #FFE900 !important;
        }

#header-wrapper {
    background: url(http://www.gossipcop.com/page/9/images/header-bg.png) no-repeat 50% 0 #1F485D;
    background-color: black;
}

#content-wrapper {
    background: url(http://www.gossipcop.com/page/9/images/bg-badge.png) repeat 50% 35% fixed;
    margin: 0 auto;
    padding: 0 0 10px;
    clear: both;
    background-color: black;
}

#header {
    background: url(http://www.gossipcop.com/page/9/images/brushed-steel.png) repeat-x #D8D8D8;
    height: 174px;
    width: 998px;
    margin: 0 auto;
    border: solid #000;
    border-width: 1px 1px 0;
    background-color: black;
}

.featured-post-title {
        color: #DF1D1D;
}

#hot-topics {
    width: 100%;
    height: 41px;
    background: url(http://www.gossipcop.com/page/9/images/hotbar-background.png) repeat-x;
    background-color: black;
}

.sidebar .sidebar-item {
    margin: 10px 0 10px 0;
    background-color: #000;
    border: 1px solid #F11212;
}

span.video-label.video-title.trc_ellipsis {
        color: #0723FF;
}

span.trc_rbox_header_span {
        color: #A91E1E;
}

.hot-box a {
    line-height: 11px;
}

a {
    color: #A91E1E;
    line-height: 44px;
}

#twitter-feed h3 a {
    text-decoration: none;
    color: #240AEB;
}

#sidebar-lower h4, #sidebar h4 {
        color: #A91E1E;
    }

strong {
    color: #A91E1E;
}

.subscribe-email-widget {
    font-family: "Comic Sans MS", cursive, sans-serif;
    font-weight: bold;
    background-color: #000000;
    color: #0723FF;
}

.post p {
    font-family: "Comic Sans MS", cursive, sans-serif;
    font-weight: bold;
    }

.post h1 {
    font-size: 31px;
    line-height: 33px;
    margin: 0 0 8px 0;
    color: #0057FC;
    font-family: "Comic Sans MS", cursive, sans-serif;
    font-weight: bold;
}

span.video-label.video-title {
        color: #03C00B;
    }

form.searchform {
    background-color: #000000 !important;
}

#s  {
background-color: #2e2e2e !important;
    color: #18FF2C !important;
    font-family: "Comic Sans MS", cursive, sans-serif;
    font-weight: bold;
}

strong a{
    color: #C60404 !important;
}

a.permalink {
    color: #6DE317;
}

#am-ngg-image {
        background-color: black;
}

#am-ngg-cont {
    background-color: black;
}

div.jcarousel-container.jcarousel-container-horizontal {
    background-color: black;
        width: 566px;
}

input {
    background-color: black;
    color: #1BAF0B;
    font-family: "Comic Sans MS", cursive, sans-serif;
    font-weight: bold;
}

p {
    font-family: "Comic Sans MS", cursive, sans-serif;
    font-weight: bold;
}
    
    
}
