@-moz-document domain("leakforums.net") {
/*======[ Package 3 - Color and type enhancements ]=======*/
/* Show more */
.q-collapsed:after {color:--color}

/* OP tag same color as theme */
b[data-type="post_op"] {
    color: --color !important;
}

/* Better like count background-color */
a[style="background: #607E60;"] {
	background-color: rgba(66,255,0,.31)!important;
}

/* Darken scrollbar BG a bit */
::-webkit-scrollbar-track {
	background: rgba(44, 44, 44, 0.51);
}

/* Improved link color */
a:link, a:visited {
	color: #CACACA;
}

/* Typography stuff */
body {
	font-family: 'Roboto', 'Helvetica Neue','Open Sans', 'PT Sans', 'Lato', 'Raleway', 'Sans-serif';
	letter-spacing: .02em;
}

/* Reputation positive and negative */
.reputation_positive {
	color: #5FC76B;
}
.reputation_negative {
	color: #DE5048;
}

/* Message, reply, quote etc. buttons */
.post_buttons a, .shadetabs a, .pagination a, input.textbox, textarea, .pagination a, a.large_button, .post_buttons a, .expcolimage {
	background: rgba(96, 96, 96, 0.16);
}
.post_buttons a:hover {
	background: rgba(96, 96, 96, 0.50);
}

/* Better navigation bar */
.navigation li.active {
	padding: 10px;
	color: #E0E0E0;
}
.navigation li {
	background: #1F1F1F;
}
.navigation {
	background: #1F1F1F;
}
.navigation li:not(.active):after {
	border-left:30px solid #1F1F1F;
}
.navigation li:not(.active):before {
	content: " ";
	display: block;
	width: 0;
	border-top: 50px solid transparent;
	border-bottom: 50px solid transparent;
	border-left: 30px solid rgba(255,255,255,.2);
}
}

@-moz-document domain("leakforums.net") {
/*======[ Package 4 - Various ]=======*/
/* Table modifications */
table[cellpadding="4"] tr:last-child .trow1, table[cellpadding="4"] tr:last-child .trow2 {box-shadow:0 1px 2px rgba(0,0,0,.7);padding:5px 9px 5px 9px}
table[cellpadding="4"].tborder {background:rgba(0, 0, 0, 0.15)}
.tborder[cellpadding="4"] .tcat {
	border-top: none;
	border-radius: 3px 3px 0px 0px;
	padding: 5px 9px 5px 9px;
	background-color:rgba(0,0,0,.3);
}

/* No spacing on images in threads */
img[alt*="Image:"]{
    vertical-align: top;
}
/* Prittier [Hidden content] container */
.fag {
	box-shadow: inset 0px 0px 4px 1px rgba(255,255,255,0.05);
	border: 1px solid rgba(12,12,12,0.25);
	border-right: 3px solid --color;
	border-left: 3px solid --color;
	-webkit-border-radius: 4px;
	border-radius: 4px;
}
/* Fixed border on profile */
td[width="69%"] {
	border-top: 1px solid #303030;
}

/* Spoiler coloured both sides */
.spoiler_header {
	border-right: 3px solid --color;
}

/* Better dropdowns */
.menu ul li a[href*="=sfw"], .menu ul li a[href*="r&uid"], #panel ul{
	border-bottom:solid thick --color;
}

.menu ul li ul li, #panel ul {
	box-shadow: 0px 4px 5px 3px rgba(0,0,0,.1) !important
}

.menu ul li ul li a, #panel ul li a {
	background-color: #303030 !important;
	background-image: url('https://i.pi.gy/Y8O2.png') !important;
	background-position: left;
	transition: ease .2s;
}

.menu ul li ul li a:hover, #panel ul li a:hover {
	background-position: right;
}

/* New default avatar */
.post_user [data-type="post_avatar"] img[src*="/default"],.profile_avatar img[src*="/default"] {
	content:url('https://i.pi.gy/plJl.png');
}

/* Better User CP design */
.usercp_container {
	border: none !important;
	border-right: solid 1px rgba(0,0,0,.4) !important;
	padding-right: 20px !important;
	box-shadow: 1px 0px 0px rgba(255,255,255,.1);
}

/* Improved "More" and "Forum" dropdown panels*/
.menu ul li ul li {
	margin: 0;
	padding: 0;
	box-shadow: 0px 2px 4px 2px rgba(0,0,0,.1);
	font-size: 12px;
}

/* Better dropdown + animations */
.menu a[href$="fw"], .menu a[href*="r&"], .dd ul{
	border-bottom: solid thick --color;
}

.menu li ul li a, .dd ul {
	box-shadow: 0px 4px 5px 3px rgba(0,0,0,.1) !important;
}

.menu li ul li a, .dd a {
	background-color: #303030 !important;
	background-image: url('https://i.pi.gy/Y8O2.png') !important;
	background-position: left;
	transition: ease .2s;
}

.menu a:hover, .dd a:hover {
	background-position: right !important;
}

/* Improved alert box */
.alert-nav {
	border: none;
	border-radius: 0;
	box-shadow: 0px 4px 13px 2px rgba(0,0,0,.2);
}
.alert-bottom {
	margin-top: 2px;
	border-top: none;
	border-bottom-left-radius: 0;
}
.alert-close {
	margin-top: 2px;
	margin-left: -1px;
	border-bottom-right-radius:0;
	border-top: none;
	height: 17px;
	color: rgba(255,255,255,.4)
}
.alert-close:hover {
	color: rgba(255,255,255,1)
}

/* Online / offlien indicator */
.post_user img[alt="Offline"] {
	content: url("https://i.pi.gy/vYQb.png");
	margin-bottom: -2px !important;
	height: 100%;
}
.post_user img[alt="Online"] {
	content: url("https://i.pi.gy/1a3o.png");
	margin-bottom: -3px;
	height: 100%;
}

/* Better UserCP notepad/textarea boxes spacing */
.usercp_notepad, #message {
	padding: 10px;
}

/* Background patterns */
#container, #content, .head {
	background: #222 url('https://i.pi.gy/D5ZQ.png');
	background-repeat: repeat;
}
.tcat {
	border-top: solid 1px rgba(255,255,255,.06);
	background: rgba(0, 0, 0, 0.15);
}
.trow1, .trow2 {
	background: rgba(0, 0, 0, 0.01);
}
tbody {
	background: #252525 url("https://i.pi.gy/LmLq.png");
}

/* Fixes Cake's postbit being upside down or whatever */
#posts [uid="7"] .post_user {
	transform: none !important;
}
}

