css:
@-moz-document domain("new.ppy.sh") {
.osu-layout--body:before {
    background-image: URL(https://i.imgur.com/eMU3x9t.png);
}
#beatmaps-listing {
    background: #212121;
}
#beatmaps-listing .sorting {
    font-size: 16px
}
#search .more a {
    font-size: 14px
}
#search {
    background: #212121;
    height: 200px;
}
#search .box {
    margin-top: 40px;
}
#search.expanded {
    height: 240px;
}
.beatmapset-panel__panel {
    box-shadow: 0px 0px 10px rgba(150, 150, 150, .6);
    background: #1B1B1B;
}
.user-profile-header__basic--flags {
    background-color: rgba(0, 0, 0, 0);
}
.osu-layout__section--extra {
    background-color: rgba(0, 0, 0, .35);
}
.page-extra {
    background-color: #333;
}
.chart__tick-text {
    fill: #FFF;
}
a {
    color: #28B8FF;
}
.h1,
.h2,
.h3,
.h4,
.h5,
.h6,
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #FF72AC;
}
.detail-row {
    border-bottom: 1px solid #2D2D2D;
}
body {
    color: #FFF;
}
.osu-layout__row--page-profile {
    background-color: #333;
}
.profile-header__userbar-container {
    background: hsla(0, 0%, 100%, .65);
    height: 60px;
}
.community-support>.page-container .perk__container>.text {
    color: #FFF;
}
.community-support .convinced>h3 {
    color: #CAB9B9;
}
.forums .description {
    color: #333;
}
.well {
    background-color: #2A2A2A;
    border: 1px solid #FD71AA;
}
.detail-row__text-score--pp {
    color: #EB0B92;
}
.beatmapset-row__info {
    color: #EB0B92;
}
.osu-layout__row--page-beatmapset {
    background-color: #333;
}
.beatmapset-scoreboard__row-item--header {
    color: #FFF;
}
.beatmapset-scoreboard__tab {
    color: #FFF;
}
.beatmapset-scoreboard__tab--active {
    color: #b17;
}
.beatmapset-header__preview-button {
    color: #FF7DB7;
    opacity: 1;
}
.page-extra-tabs__container {
    border-bottom: 5px solid #FD71AA;
}
.beatmaps #nav-background #nav-gradient-overlay,
.store #nav-background #nav-gradient-overlay,
.community #nav-background #nav-gradient-overlay {
    background: #FD71AA;
}
.osu-layout__section--full {
    padding-top: 10px
}
#nav-sm {
    padding-top: 30px;
}
#nav-sm a,
#nav-sm a:after,
#nav-sm a:before {
    color: hsla(0, 0%, 100%, 1);
}
.home #nav-background #nav-gradient-overlay {
    background: #F269A6;
}
}
