/* Широкоформатный Mapcam(1).info v0.4 16.06.2016    */
/* Автор -    3D_MASTER       */
/* Для широких экранов     1440+                 */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mapcam.info"), domain("mapcam1.info")

{
/* Главная страница шире. */
.wrapper {
    max-width: 1440px;
    margin: 0 auto;
    padding: 0;
}
/* Строка меню по центру. */
    .menu {
        text-align: center;
    /*display: block;
    padding: 0px;
    padding-top: 0px;
    padding-right: 0px;
    padding-bottom: 0px;
    padding-left: 0px;
    margin: 0;
    margin-top: 0px;
    margin-right: 0px;
    margin-bottom: 0px;
    margin-left: 250px;*/
}
/* Меню на карте черно/белое. */
        #map_main_menu nav {
        background: rgba(0, 0, 0, 0.9) none repeat scroll 0% 0%;
        background-color: rgba(0, 0, 0, 0.9);
        background-image: none;
        background-repeat: repeat;
        background-attachment: scroll;
        background-position: 10% 20%;
        background-clip: border-box;
        background-origin: padding-box;
        background-size: auto auto;
}
/* Скролл маршрута на карте, при просмотре. Спасибо MVV23RUS. */
.adp {
	overflow-x: hidden;
	overflow-y: scroll;
	max-height: 60vh;
}
/*уберет ненужное расширение страницы комментариев к объектам при вставке слишком длинного текста - типа URL к панорамам и пр. Спасибо (R)ound.*/
.comment_holder > div {
    overflow: auto;
    text-overflow: ellipsis;
}
}
