css:
@-moz-document domain("thebridge.jp") {
ins,
.site-header .search,
.site-header .social,
.contents .breadcrumb,
.contents header .share-this-page,
.side #mc_embed_signup
{ display:none !important; }

.wrap {
  width: 700px;
  padding-top: 100px;
}

.contents {
  display: flex;
  flex-direction: column;
}

.contents>.side {
  float: none;
  width: auto;
}

.contents>.side>.author h2,
.contents>.side>.author p,
.contents>.side .post-list article h3 {
  width: 570px;
}

}
