@-moz-document domain("trello.com") {
    body {
        background: #f9f9fb !important;
        font-family: "Open Sans", "HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif !important;
        font-weight: 300 !important;
    }
    
    h3 {
        font-weight: 300 !important;
    }
    
    a {
        transition: color .2s ease-out;
    }
    
    /* ----------------------- */
    /* ---      ICONS      --- */
    /* ----------------------- */
    
    .dark-hover, .dark-hover .icon-lg, .dark-hover .icon-sm, .icon-lg.dark-hover, .icon-sm.dark-hover,
    .light-hover, .light-hover .icon-lg, .light-hover .icon-sm, .icon-lg.light-hover, .icon-sm.light-hover{
        transition: color .2s ease-out, background .2s ease-out;
    }
    
    .quiet a:not(.disabled) {
        transition: color .2s ease-out;
    }
    
    .quiet-button {
        transition: color .2s ease-out;
    }
    
    .header-woof {
        display: none !important;
    }
    
    #header {
        background: /*[[theme-color]]*/ !important;
        padding: 10px !important;
    }
    
    .js-open-board {
        font-weight: 300 !important;
        border: 1px solid #eee;
    }
    
    .js-open-board:hover {
        border-color: #ddd;
    }
    
    .board-canvas {
        padding-top: 20px;
    }
    
    .thumbnail {
        background: #979798 !important;
    }
    
    .sidebar-section-header-title {
        text-decoration: none !important;
        font-weight: 300 !important;
    }
    
    #search-text {
        border-radius: 0px !important;
    }
    
    input[type=text].header-search-input {
        transition: background .2s ease-out, width .2s ease-out !important;
    }
    
    .board-header {
        margin-bottom: 20px !important;
        padding-bottom: 0px !important;
    }
    
    .board-menu-section-header,
    .board-menu-navigation-item-link {
        transition: background-color .2s ease-out, color .2s ease-out;
    }
    
    .board-menu-navigation-item-link .board-menu-navigation-item-link-icon {
        transition: color .2s ease-out;
    }
    
    .show-more {
        transition: background .2s ease-out, color .2s ease-out;
    }
    
    /* header buttons */
    
    .header-btn {
        background: rgba(255,255,255,.2) !important;
        transition: background .2s ease-out;
    }
    
    .header-btn:hover {
        background: none !important;
        background: rgba(255,255,255,.5) !important;
    }
    
    .header-btn.header-notifications,
    .header-btn.header-notifications:hover,
    .header-btn.header-member,
    .header-btn.header-member:hover,
    .header-btn.header-boards,
    .header-btn.header-boards:hover,
    .header-btn.header-search,
    .header-btn.header-search:hover,
    .header-btn.header-login,
    .header-btn.header-login:hover,
    .header-btn.header-signup,
    .header-btn.header-signup:hover {
        background: none !important;
    }
    
    .header-user {
        top: 10px !important;
        right: 5px !important;
        font-weight: 300 !important;
    }
    
    .board-header {
        height: 40px !important;
        background: /*[[theme-color]]*/ !important;
    }
    
    .header-btn-text {
        font-weight: 300 !important;
    }
    
    .board-header-btn {
        transition: background .2s ease-out, color .2s ease-out !important;
    }
    
    .board-header-btn-text {
        text-decoration: none !important;
    }
    
    .board-header-btn-name {
        font-weight: 300 !important;
    }
    
    .text {
        text-decoration: none !important;
    }
    
    .header-member {
        background: none !important;
    }
    
    .member {
        border-radius: 20px !important;
        border: none !important;
        background: none !important;
    }
    
    .member img {
        border-radius: 0 !important;
        border: none !important;
        transition: opacity .2s ease-out;
    }
    
    .list-wrapper {
        margin: 0 5px !important;
    }
    
    .list-wrapper.placeholder {
        background: #eee;
    }
    
    .list-wrapper:first-child {
        margin-left: 10px !important;
    }
    
    .list-area {
        margin-top: 30px !important;
    }
    
    .list {
        padding: 8px !important;
    }
    
    .list-header {
        margin: 5px 0px 10px 0px !important;
    }
    
    .list-header-name {
        color: /*[[theme-color]]*/ !important;
        font-weight: 700 !important;
        text-transform: none;
    }
    
    .list-header-extras-menu {
        transition: color .2s ease-out, background .2s ease-out !important;
    }
    
    .list {
        background: #fff !important;
        border: 1px solid #eee !important;
        border-top: none !important;
        border-radius: 0px;
    }
    
    .list:before {
        content: '';
        position: absolute;
        top: -3px;
        left: 0;
        width: 100%;
        height: 5px;
        background: /*[[theme-color]]*/;
        transition: top .1s ease-out;
    }
    
    .list:hover:before {
        top: 0;
    }
    
    .list-card {
        border: 1px solid #eee !important;
        padding: 5px !important;
        box-sizing: border-box;
        transition: border .2s ease-out !important;
    }
    
    .active-card {
        background: #fff !important;
        border: 1px solid /*[[theme-color]]*/ !important;
    }
    
    .list-card-operation {
        background: none !important;
        visibility: visible !important;
        opacity: 0;
        transition: color .2s ease-out, opacity .2s ease-out !important;
    }
    
    .list-card.active-card .list-card-operation {
        opacity: 1;
    }
    
    /* Add list field */
    .js-add-list {
        background: none !important;
        padding: 0px !important;
    }
    
    .edit-controls {
        margin-top: 8px !important;
    }
    
    .js-add-list:hover {
        color: /*[[theme-color]]*/ !important;
    }
    
    .js-add-list span {
        color: #bbb !important;
    }
    
    .js-add-list span:hover {
        color: /*[[theme-color]]*/ !important;
    }
    
    .list-name-input {
        background: #fff !important;
        border: 1px solid #eee !important;
        padding: 12px !important;
        box-shadow: none !important;
    }
    
    /* Add card field */
    .open-card-composer {
        color: #999 !important;
        transition: color .2s ease-out !important;
    }
    
    .open-card-composer:hover {
        text-decoration: none !important;
        background: none !important;
        color: /*[[theme-color]]*/ !important;
    }
    
    .js-composer:hover {
        border: 1px solid #eee !important;
    }
    
    .window-overlay {
        background: rgba(255, 255, 255, .8) !important;
    }
    
    strong {
        font-weight: 300 !important;
    }
    
    .js-open-move-from-header {
        color: /*[[theme-color]]*/ !important;
        font-weight: 300 !important;
    }
    
    .window-title {
        margin-top: 20px;
    }
    
    .window-title-text {
        font-weight: 300 !important;
        color: /*[[theme-color]]*/ !important;
    }
    
    .button-link {
        font-weight: 300;
        background: #f8f9f9 !important;
        transition: background .2s ease-out, border-color .2s ease-out, color .2s ease-out;
    }
    
    .button-link .icon-sm, .button-link .icon-lg {
        transition: color .2s ease-out;
    }
    
    .button-link:hover {
        background: /*[[theme-color]]*/ !important;
        border-color: /*[[theme-color]]*/ !important;
    }
    
    .attachment-add-link-submit {
        background: /*[[theme-color]]*/ !important;
        box-shadow: none !important;
        font-weight: 700 !important;
        color: #fff !important;
    }
    
    /* Primary button */
    input.primary {
        background: /*[[theme-color]]*/ !important;
        border: 1px solid rgba(238, 238, 238, 0);
        box-shadow: none !important;
        font-weight: 700 !important;
        transition: background .2s ease-out, color .2s ease-out, border .2s ease-out;
    }
    
    input.primary:hover {
        background: /*[[theme-color]]*/ !important;
    }
    
    input.primary:disabled {
        background: none !important;
        border: 1px solid #eee;
        color: #ddd !important;
    }
    
    /* Cancel icon button */
    .js-cancel {
        margin-left: 4px;
    }
    
    .ui-state-active {
        background: #F45B5B !important;
    }
    
    /* ------------------- */
    /* --- CARD DETAIL --- */
    /* ------------------- */
    
    .window {
        background-color: /*[[theme-color]]*/ !important;
        box-shadow: 0 19px 38px rgba(0, 0, 0, .30), 0 15px 12px rgba(0, 0, 0, .22);
        border-radius: 0px;
    }
    
    .dialog-close-button {
        margin: 12px 12px 0px 0px !important;
    }
    
    .window-wrapper {
        margin-top: 4px;
        background: #fff !important;
    }
    
    .window-wrapper a {
        text-decoration: none !important;
    }
    
    /* Window header */
    .window-header {
        margin-top: 0px;
    }
    
    .window-header-icon {
        display: none !important;
    }
    
    .window-title-text {
        font-weight: 700 !important;
    }
    
    .field {
        box-shadow: none !important;
    }
    
    /* hide 'in Liste bla' */
    .window-header-inline-content {
        display: none;
    }
    
    /* label */
    .card-label {
        text-shadow: 1px 1px 0px rgba(0, 0, 0, .2), 0 0 0px #000;
    }
    
    .card-label.mod-card-detail {
        transition: opacity .2s ease-out;
    }
    
    /* card description */
    .card-detail-item-header-edit {
        display: none !important;
    }
    
    .card-detail-item-header {
        display: none !important;
    }
    
    .card-detail-item {
        margin-top: 12px;
        font-weight: 500;
    }
    
    /* separators */
    .card-detail-item {
        margin-bottom: 40px;
    }
    
    .window-module {
        margin-bottom: 40px;
    }
    
    /* section titles */
    .window-module-title-icon {
        display: none !important;
    }
    
    h3 {
        font-weight: 300 !important;
        color: #000;
    }
    
    .checklist-progress-bar-current {
        background: /*[[theme-color]]*/ !important;
    }
    
    .checklist-item {
        transition: background .2s ease-out;
    }
    
    /* comments */
    .action-comment {
        box-shadow: none !important;
        border-left: 2px solid /*[[theme-color]]*/;
        border-radius: 0px !important;
        margin-top: 12px;
        margin-bottom: 12px;
    }
    
    .comment-frame {
        transition: box-shadow .2s ease-out;
    }
    
    .phenom {
        border-bottom: 1px solid #eee;
    }
    
    .window-module-title {
        border-top: 2px solid /*[[theme-color]]*/;
        padding: 14px 0px 18px 0px;
    }
    
    /* ------------------- */
    /* --- BOARD MENU --- */
    /* ------------------- */
    
    .board-menu {
        background: #fff !important;
    }
    
    /* ----------------------- */
    /* --- BOARDS SWITCHER --- */
    /* ----------------------- */
    
    .boards-drawer {
        box-shadow: 0 3px 6px rgba(0, 0, 0, .16);
        padding: 0px 24px;
    }
    
    .boards-drawer input {
        margin: 6px 0px;
    }
    
    input.js-search-boards {
        border: none;
        border-bottom: 1px solid #ddd;
        background: none;
        border-radius: 0px;
        padding: 0px;
        margin-top: 12px;
        margin-bottom: 24px;
    }
    
    input.js-search-boards:hover {
        border-color: /*[[theme-color]]*/;
    }
    
    input.js-search-boards:focus {
        border-color: /*[[theme-color]]*/;
        box-shadow: none;
    }
    
    .js-board-drawer-buttons {
        margin: 36px 0px;
        margin-bottom: 12px;
        border-top: 2px solid /*[[theme-color]]*/;
    }
    
    .sidebar-section-header-toggle-icon:hover {
        background: none;
    }
    
    .quiet-button:hover {
        background: none;
        color: /*[[theme-color]]*/;
    }
    
    .boards-drawer .empty,
    .boards-drawer .quiet-button {
        margin: 12px 0px;
        padding: 0px;
    }
    
    .sidebar-section-header {
        margin: 1pc 30px 10px 0px;
        position: relative;
    }
    
    /* ----------------------- */
    /* --- BOARDS SWITCHER --- */
    /* ----------------------- */
    
    .js-add-board {
        color: #999 !important;
        background: #fff !important;
        border: 1px solid #eee !important;
        padding: 8px 9pt !important;
        transition: border-color .2s ease-out, color .2s ease-out !important;
    }
    
    .js-add-board:hover {
        color: /*[[theme-color]]*/ !important;
        border-color: /*[[theme-color]]*/ !important;
    }
    
    .js-open-board {
        transition: border-color .2s ease-out;
    }
    
    /* ----------------------- */
    /* ---   BOARD MENU    --- */
    /* ----------------------- */
    
    .compact-board-tile-fade {
        transition: background .2s ease-out;
    }
    
    .compact-board-tile.selected .compact-board-tile-link-thumbnail {
        transition: opacity .2s ease-out;
    }
    
    .segmented-control-item {
        transition: background .2s ease-out, color .2s ease-out, opacity .2s ease-out !important;
    }
    
    .segmented-control-item:hover {
        text-decoration: none !important;
        background: /*[[theme-color]]*/ !important;
        opacity: .8;
    }
    
    .segmented-control-item.is-active {
        background: /*[[theme-color]]*/ !important;
    }
    
    .board-menu-header-back-button {
        transition: transform .2s ease-out, color .2s ease-out !important;
    }
    
    /* ----------------------- */
    /* ---  INPUT FIELDS   --- */
    /* ----------------------- */
    
    .iframe-input, input[type=text], input[type=password], input[type=email] {
        transition: box-shadow .2s ease-out, border-color .2s ease-out, background .2s ease-out !important;
    }
    
    .iframe-input.focus, input[type=text]:focus, input[type=password]:focus, input[type=email]:focus {
        border-color: /*[[theme-color]]*/ !important;
        box-shadow: 0 0 2px /*[[theme-color]]*/ !important;
    }
    
    .comment-box-input, .comment-box-input:active, .comment-box-input:focus, .comment-box-input:hover {
        border-bottom: solid 30px transparent !important;
    }
    
    .meta-link-list-item-link, .next-tip-link, .tip {
        transition: background-color .2s ease-out, color .2s ease-out;
    }
    
    .pop-over-list li>a {
        transition: background-color .2s ease-out, color .2s ease-out;
    }
    
    .pop-over-list li>a:hover {
        background-color: /*[[theme-color]]*/ !important;
    }
}
