css:
@-moz-document domain("roblox.com") {
/* Dropdown search bar/container */

.search-input-container, .dropdown-menu, #PaymentContainer, #AccountUpgradesConfirmationContainer #Message {
    background-color: #252525 !important;
}
.rbx-login-partial-legacy, .request-error-page-content, .default-error-page, .avatar-card-btns {
    background-color: #353535 !important;
}
.stack .stack-list .stack-row {
    background-color: #202020 !important;
}
div#BuyGroupPopup.modalPopup.blueAndWhite.simplemodal-data {
    color: #ffffff !important;
    position: static !important;
}
.input-group .form-control:first-child, .input-addon-btn {
    background-color: #505050 !important;
}
.rbx-header .rbx-navbar li a:hover,
.rbx-header .rbx-navbar li a:focus {
    border-radius: 0px;
}

.result-item-container, .group-card{
   background-color: #303030 !important; 
}


.rbx-header .rbx-navbar-search .rbx-navbar-search-option.selected {
    box-shadow: 4px 0 0 0 #F0C000 inset;
    background-color: #505050 !important;
}
.dropdown-menu .rbx-navbar-search-option.selected,
.dropdown-menu .rbx-navbar-search-option:hover {
    background-color: #505050 !important;
}
.arrow:after {
    content: " ";
    top: 1px;
    margin-left: -10px;
    border-top-width: 0px;
    border-bottom-color: #252525 !important;
}
.popover-content > ul {
    background-color: #252525 !important;
}
.popover li:hover {
    background: none #353535 !important;
    box-shadow: 4px 0 0 0 #fc0 inset;
    box-shadow: 4px 0 0 0
    #F0C000
    
    inset;
}
/* ---- GLOBAL LEADERBOARDS --------------------------- */

.rbx-leaderboard-container * {
    border-color: #303030 !important;
}
#leaderboards-body {
    color: #d0d0d0 !important;
}
#leaderboards-top {
    background: #101010 !important;
}
#leaderboards-top-container,
#leaderboards-controls,
.leaderboards-header,
.leaderboards-row {
    background: none transparent !important;
    border-color: #202020 !important;
}
#fixed-leaderboards-buttons > div {
    background: none #202020 !important;
    border-color: #050505 !important;
}
.flyout {
    background-color: #202020 !important;
    border-color: #181818 !important;
}
.flyout > div.time-filter-dropdown {
    border-color: #181818 !important;
    color: #d0d0d0 !important;
}
.flyout > div.time-filter-dropdown:hover {
    color: white !important;
}
.flyout div.notch-top {
    border-bottom-color: #202020 !important;
}
.flyout div.notch-shadow {
    border-bottom-color: #181818 !important;
}
.rbx-leaderboard-my {
    overflow: hidden !important;
}
/* ---- GLOBAL STYLING/LAYOUT CHANGES ----------------- */

html,
body,
div#MasterContainer,
div#RepositionBody,
.nav-content,
.content {
    background: #101010 !important;
    color: white !important;
}
.section-content {
    background-color: #202020;
}
/* Revert content to Arial/Helvetica and not the fancy-looking new font */

#Body,
.text,
pre {
    font-family: Arial, Helvetica, sans-serif !important;
    font-size: 12px !important;
}
a,
a:link,
a:visited,
cite,
.show-more-link {
    color: #d80 !important;
    color: #D08000 !important;
    text-shadow: 0 1px 0 #655;
}
a:hover,
a > .rbx-title:hover,
a > .friend-name:hover,
.show-more-link:hover {
    color: #fc0 !important;
    color: #F0C000 !important;
    text-shadow: 0 1px 0 black;
    text-decoration: none !important;
}
a[disabled="disabled"]:hover {
    color: rgb(128, 128, 128) !important;
}
/* Selector for changing text to white */

h1,
h2,
h3,
h4,
p,
pre,
.text,
.form-label,
.details *,
.voting-details,
.stat-label,
.Label,
div.BadgesPager_Container,
.FooterPager,
.StandardBox,
.right-column .buttons div,
.stat,
.title,
td,
th,
tr,
.text,
.UserOwns,
.TotalSales,
a.btn-medium,
a.btn-medium:hover,
a.btn-small,
a.btn-small:hover,
a.btn-large,
a.btn-large:hover,
input.paging_input,
a.btn-control:hover,
.builder-name,
.checkbox-label,
li[role="tab"] > a,
.FooterNav,
.small {
    color: white !important;
}
/* Selector for setting backgrounds of global classes transparent */

.StandardPanelWhite,
.StandardBox,
.loading {
    background: transparent !important;
}
h1,
.GamesPageHeader {
    color: white !important;
    width: 100% !important;
    text-shadow: 1px 2px 0 #454545 !important;
    margin-left: auto !important;
    margin-right: auto !important;
    text-align: center !important;
}
.normalHeader {
    top: 70px !important;
}
.tabsdiv {
    top: 140px !important;
}
.see-more-img {
    display: none;
}
.SeeMore:after,
#SeeMore > a:after {
    content: " >";
}
/* Ensure no images are getting a background (color) */

img {
    background: none !important;
}
#MasterContainer,
#Container,
footer,
#Footer,
.subMenu,
#Body,
#BodyWrapper {
    background: none transparent !important;
}
li.footer-link:hover {
    box-shadow: 0 -2px 0 0 #fc0 inset;
    box-shadow: 0 -2px 0 0
    #F0C000
    
    inset;
}
/* Change border color throughout site */

.divider-top,
.divider-bottom,
.divider-left,
.divider-right,
#UserAssetsPane,
#Footer .legal {
    border-color: #050505 !important;
}
.separator {
    background-color: #050505 !important;
}
/* Modal popups (e.g. sell item) */

.unifiedModal,
.Message,
.ConfirmationModalFooter {
    color: white;
}
.simplemodal-data {
    background-color: #151515 !important;
}
.titleBar,
.ConfirmationModal .Title,
.modal-header {
    background-color: #151515 !important;
    color: white !important;
    padding-top: 6px !important;
}
.PurchaseModalBody,
.unifiedModalContent,
.GenericModalBody,
.modal-content {
    background-color: #303030 !important;
}
.simplemodal-container input.robux {
    width: 100px !important;
}
.modal-dialog button.close {
    background-color: #858585 !important;
}
/* Vertical global nav menu */
a.avatar {
    background-color: #404040 !important;
}
#header,
.header-links {
    background-color: #202020 !important;
}
#header ul.nav.rbx-navbar li a:hover,
.logo:hover {
    box-shadow: 0 -2px 0 0 #fc0 inset !important;
}
#navigation {
    background-color: #353535 !important;
}
.mCSB_scrollTools {
    background-color: #353535 !important;
}
.mCSB_dragger_bar {
    background-color: #858585 !important;
}
li.user {
    margin-bottom: 3px !important;
}
.nav > li > a.menu-item {
    margin-right: 0 !important;
    border-right: 1px solid transparent !important;
}
.nav > li > a.menu-item:hover {
    border-right-color: #fc0 !important;
    border-right-color: #F0C000  !important;
}
.rbx-highlight {
    background-color: #fc0 !important;
    background-color: #F0C000 !important;
    box-shadow: rgba(0, 0, 0, 240) 1px 1px 4px;
    color: #202020 !important;
}
.navigation-container li:hover a {
    background-color: #404040 !important;
}
.user .username,
.header-links > a > div {
    color: white !important;
}
.rbx-divider {
    background-color: #202020 !important;
}
.rbx-upgrade-now a,
.membership-update-container > a {
    background-color: #202020 !important;
    border: none !important;
}
.rbx-upgrade-now a:hover,
.membership-update-container > a:hover {
    background-color: #404040 !important;
}
.content .page-content .rbx-tab-content .settings-social-networks-container .form-group p .membership-name,
.content .page-content .rbx-tab-content .settings-privacy-container .form-group p .membership-name,
.content .page-content .rbx-tab-content .settings-billing-container .form-group p .membership-name {
    color: gold;
}
/* ---- GAME/PLACE/ITEM PAGE STYLING/LAYOUT CHANGES -------- */

.BuyPriceBox {
    background-color: #202020 !important;
    border-color: #181818 !important;
}
.robux-text {
    color: #D08000 !important;
}
.item {
    border-color: #050505 !important;
}
.AlternatingBadgeRow {
    background-color: #151515 !important;
}
.ajax__tab_body .ajax__tab_panel {
    background-color: #101010 !important;
}
/* Comments appearance changes */

.AjaxCommentsContainer,
div.comments-container {
    width: 100% !important;
    overflow: hidden;
}
.comments-container .list-item {
    border-top: none !important;
}
.comment-content {
    border-top: 1px dashed #606060 !important;
}
.comment-body {
    border-bottom: 1px solid #050505 !important;
}
textarea.rbx-comment-input {
    resize: vertical !important;
    max-height: 300px;
}
.CommentText,
.CommentText > .Buttons,
.PostContainer .Post,
.PostContainer .PostBottom {
    background: none transparent !important;
}
.SelectedYouTubeGalleryIcon {
    border: 1px solid #fc0 !important;
    border: 1px solid
    #F0C000
    
    !important;
}
.rbx-pager-prev > a,
.rbx-pager-next > a {
    background-color: #777 !important;
    border-color: black !important;
}
.rbx-pager-prev > a:hover,
.rbx-pager-next > a:hover {
    background-color: #454545 !important;
    border-color: #fc0 !important;
    border-color: #F0C000 !important;
}
/* Remove ads from the loading place screen for NBC users */

#videoPrerollPanel {
    background-color: #151515 !important;
    height: auto !important;
    border: 1px solid white !important;
}
#videoPrerollPanel * {
    color: white !important;
}
#videoPrerollLoadingDiv {
    padding-top: 10px !important;
    padding-bottom: 10px;
}
#videoPrerollLoadingBar {
    border-color: #050505 !important;
}
#videoPrerollLoadingBarCompleted {
    background-color: #fc0 !important;
    background-color: #F0C000 !important;
}
#videoPrerollTitleDiv,
#videoPrerollMainDiv,
#videoPrerollTitleData,
#videoPrerollJoinBC,
#videoPrerollCompanionAd {
    display: none !important;
    color: white !important;
}
a.game-item,
.card .card-item,
.game-card-container,
.item-card-container,
.store-card {
    background-color: #202020;
}
.game-card-name-secondary {
    color: #ddd;
    text-shadow: none;
}
.vote-mask .segment {
    background-color: #202020 !important;
}
.rbx-game-title::after,
.card .card-item .card-text-notes::after {
    content: "";
    background: linear-gradient(to right, rgba(32, 32, 32, 0) 0px, #202020 85%) !important;
    height: 100%;
}
.card .card-item .card-title,
.card .card-item .card-text-notes {
    background-color: transparent;
}
/* Dropdown button-menu thingies */

.gear-button-wrapper {
    background: none transparent !important;
    border: none !important;
}
.button.gear,
.gear-button {
    background: #151515 url("http://imgur.com/0Oym2uz.png") no-repeat !important;
}
.button.gear:hover,
.button.gear.active,
.gear-button:hover,
.gear-open {
    background-position: 0 100% !important;
    cursor: pointer;
}
a.btn-dropdown,
a.btn-dropdown-active,
.GrayDropdown .Button {
    background: #151515 url("http://imgur.com/2yL7B7u.png") right top no-repeat !important;
    border-color: #050505 !important;
    color: white !important;
}
a.btn-dropdown:hover,
.SetListDropDown a:hover,
.dropdown-list li:hover,
#build-dropdown-menu a:hover,
.GrayDropdown .Button:hover,
.GrayDropdown .Button.Active,
.Menu a:hover,
.Menu .Item:hover {
    background-color: #252525 !important;
}
.SetListDropDown .menu,
.dropdown-list,
#build-dropdown-menu,
.GrayDropdown .Menu,
.rbx-dropdown-menu {
    background-color: #151515 !important;
    border-color: #050505 !important;
    color: white !important;
}
.rbx-dropdown-menu li:hover {
    box-shadow: 5px 0 0 0 #fc0 inset !important;
    box-shadow: 5px 0 0 0
    #F0C000
    
    inset !important;
}
.SetListDropDown a,
.dropdown-list a,
#build-dropdown-menu a,
.Menu .Item,
.dropdown-list span {
    color: white !important;
}
/* ---- GLOBAL TAB (VERTICAL/HORIZONTAL) APPEARANCE --- */
/* Fix Roblox"s sloppy CSS */

.ajax__tab_tab,
.ajax__tab_body,
.AssetThumbnail {
    background: none transparent !important;
}
.ajax__tab_hover .ajax__tab_tab {
    margin-right: 4px !important;
}
.ajax__tab_tab {
    padding-right: 8px !important;
}
/* Ensure white text color on tab title */

.SquareTabGray a,
.SquareTabGray a:hover,
.GroupsPeopleTabs_Container a,
.verticaltab a {
    background-color: transparent !important;
    color: white !important;
}
.tab-content,
.tab-active {
    background: none transparent !important;
    border: none !important;
}
/* Horizontal tab container */

div.GroupsPeopleTabs_Container,
.ui-tabs-nav,
.tab-container,
.WhiteSquareTabsContainer {
    background: none transparent !important;
    border: none !important;
    box-shadow: #fc0 0 -1px 0 0 inset !important;
    box-shadow:
    #F0C000
    
    0 -1px 0 0 inset !important;
}
.ajax__tab_header {
    border-bottom: none !important;
}
.ajax__tab_body {
    border: none !important;
    border-top: 1px solid #fc0 !important;
    border-top: 1px solid
    #F0C000
    
    !important;
    margin-top: 1px !important;
}
/* Horizontal tab INACTIVE */

li[role="tab"],
.tab,
.ajax__tab_inner,
.tab-container > div,
.tab-container > li.ng-scope,
.ItemTabs {
    background: #202020 !important;
    border: 1px solid #050505 !important;
    border-bottom: none !important;
}
/* Horizontal tab HOVER */

li[role="tab"]:hover a,
.tab:hover,
.ajax__tab_inner:hover,
.tab-container > div:hover,
.ItemTabs:hover {
    background-color: #353535 !important;
}
/* Horizontal tab ACTIVE */

li[role="tab"].ui-tabs-active,
.tab.active,
.ajax__tab_active .ajax__tab_inner,
li[role="tab"].ui-tabs-active:hover,
li[role="tab"].ui-tabs-active:hover a,
.tab-container > .tab-active,
.tab-container > .tab-active:hover,
.ItemTabs.selected {
    background-color: #101010 !important;
    border-color: #fc0 !important;
    border-color: #F0C000 !important;
    border-bottom: 1px solid #101010 !important;
}
/* new style horizontal tabs */

.content .rbx-tab .rbx-tab-heading .rbx-lead {
    color: white;
}
.content .rbx-tab:hover .rbx-tab-heading .rbx-lead {
    color: #fc0;
    color: #F0C000;
}
.rbx-tabs-horizontal .rbx-tab > a,
.rbx-tab .rbx-tab-heading {
    background-color: #202020;
}
.rbx-tabs-horizontal .rbx-tab.active > a,
.rbx-tabs-horizontal .rbx-tab > a:hover {
    background-color: #303030;
    box-shadow: 0 -4px 0 0 #fc0 inset !important;
    box-shadow: 0 -4px 0 0 #F0C000 inset !important;
}
/* new style vertical tabs */

.rbx-tabs-vertical .nav-tabs {
    background-color: #202020;
    box-shadow: rgba(0, 0, 0, 200) 0 1px 3px 0;
}
.rbx-tabs-vertical .rbx-tab.active .rbx-tab-heading,
.rbx-tabs-vertical .rbx-tab:hover > a {
    box-shadow: -4px 0 0 0 #fc0 inset !important;
    box-shadow: -4px 0 0 0
    #F0C000
    
    inset !important;
}
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:hover,
.nav-tabs > li.active > a:focus,
.nav-tabs > li > a:hover {
    background-color: #303030 !important;
}
/* Vertical tab INACTIVE */

.tab-item,
.verticaltab {
    background-color: #101010 !important;
}
/* Vertical tab HOVER */

.tab-item:hover,
.verticaltab a:hover {
    background-color: #151515 !important;
    border-right: 1px solid #fc0 !important;
    border-right: 1px solid
    #F0C000
    
    !important;
}
/* Vertical tab ACTIVE */

.tab-item-selected,
.verticaltab.selected {
    background-color: #151515 !important;
    border: none !important;
}
/* Nav "pills" */

.nav-pills a {
    color: white !important;
    border-color: white !important;
}
.nav-pills .active a,
.nav-pills a:hover {
    background-color: #252525 !important;
}
/* ---- Landing page (logout, log in) ------------------*/

.navbar-landing,
div#SignUpFormContainer {
    background-color: #151515 !important;
}
li#magic-line {
    background-color: #fc0 !important;
    background-color: #F0C000 !important;
}
a#HeaderForgotPassword {
    font-size: 0.9em !important;
}
footer.row > div.col-xs-12 {
    background-color: #101010 !important;
}
/* ---- MISC. STYLING/LAYOUT CHANGES ------------------ */

#report-body {
    color: white !important;
}

.rbx-text-overflow,
.news-link {
    background-color: transparent !important;
}
.rbx-text-overflow::after,
.content .friends-container .friends-card .friends-name::after,
.para-overlow::after {
    content: "...";
    background: linear-gradient(to right, rgba(32, 32, 32, 0) 0px, #202020 85%) !important;
    height: 100%;
}
.news-link::after {
    content: "";
    background: transparent linear-gradient(to right, rgba(0, 0, 0, 0) 0px, #202020 85%) !important;
    height: 18px;
}
.profile-about p::after,
.chat-friend-list .chat-friend-info .chat-friend-message::after {
    background-image: linear-gradient(to right, rgba(255, 255, 255, 0) 0px, #202020 90%);
    content: "";
    height: 24px;
    position: absolute;
    right: 0px;
    bottom: 0px;
    width: 10%;
}
/* Recolor Chat/Party slideout */

#chat-body,
.dialog-container {
    background-color: #202020;
}
.chat-friend-list .chat-friend-info .chat-friend-message {
    background-color: transparent !important;
}
.chat-friends > li.chat-friend:hover > div.chat-friend-container,
.chat-friend-container:hover .chat-friend-info .chat-friend-name {
    background-color: #303030 !important;
}
.chat-container .chat-friend-container:hover .rbx-text-overflow::after,
.chat-friend-container:hover .chat-friend-info .chat-friend-message::after,
.chat-friend-container:hover .chat-friend-info .chat-friend-name::after {
    background-image: linear-gradient(to right, rgba(255, 255, 255, 0) 0px, #303030 90%) !important;
    content: "";
    height: 24px;
    position: absolute;
    right: 0px;
    bottom: 0px;
    width: 10%;
}
.dialog-container .dialog-header,
.dialog-container .dialog-header .dialog-header-title,
.chat-main .chat-header {
    background-color: #303030;
}
.dialog-container .dialog-header .dialog-header-title::after {
    background-image: linear-gradient(to right, rgba(255, 255, 255, 0) 0px, #303030 90%);
    height: 100%;
}
.dialog-container .dialog-body {
    background-color: black;
}
.dialog-container .dialog-message-container .dialog-message {
    background-color: #252525;
}
.dialog-container .dialog-message-container .dialog-triangle::before {
    border-color: transparent transparent transparent #252525;
}
.dialog-container .dialog-message-container.message-inbound .dialog-message {
    background-color: #505050;
}
.dialog-container .dialog-message-container.message-inbound .dialog-triangle::before {
    border-color: transparent #505050 transparent transparent;
}
.dialog-container .dialog-input-container {
    border-color: #777;
}
.dialog-container .group-select-container .group-select-friend {
    background-color: #101010;
}
.group-dialog .group-member-header {
    background-color: #202020;
}
.popover-content .rbx-dropdown-menu li a:hover {
    background-color: #353535;
}
/* Recolor all "items" throughout the site (same appearance as in the Catalog */

.CatalogItemInner,
.SmallCatalogItemView,
ul#dropdownUl,
ul#dropdownUl > li,
ul.slideOut,
ul.slideOut li.slideHeader,
#gearSecondColumn {
    background-color: #101010 !important;
}
.CatalogItemInner:hover,
ul#dropdownUl li a:hover,
ul.slideOut a:hover {
    background-color: #252525 !important;
    border-color: #050505 !important;
}
.CatalogHoverContent span.CatalogItemInfoLabel,
li.slideHeader span {
    color: #bbb !important;
}
.CatalogHoverContent span.HoverInfo,
ul#dropdownUl a {
    color: white !important;
}
/* Global recolor of notifications (universe place, message sent, etc.) */

.status-confirm {
    background-color: #202020 !important;
    border-color: #fc0 !important;
    border-color: #F0C000  !important;
    color: white !important;
}
/* Color various inputs (text boxes, buttons, dropdowns, etc.) to match theme*/

textarea,
.MultilineTextBox,
select,
input[type="text"],
input#searchbox,
button,
input[type="submit"],
input[type="button"],
#ctl00_cphRoblox_SearchTextBox,
.btn-control,
.ui-widget-content select,
input.text-box,
input.text-box-large,
#Username,
#Password,
#UserName,
select#place-creationcontext-drop-down,
input.form-control,
.form-control,
.fake-input-lg,
a[type="button"],
a.rbx-btn-control-xs,
.rbx-btn-secondary:hover,
.rbx-btn-secondary-lg:hover,
.rbx-btn-secondary-md:hover,
.rbx-btn-secondary-sm:hover,
.btn-secondary-xs,
.rbx-btn-secondary:focus,
.rbx-btn-secondary-lg:focus,
.rbx-btn-secondary-md:focus,
.rbx-btn-secondary-sm:focus,
.btn-secondary-xs:focus,
.rbx-btn-primary-xs,
a.btn-primary-md,
.btn-control-xs {
    -moz-appearance: none;
    -webkit-appearance: none;
    background: #252525 !important;
    border: 1px solid #050505 !important;
    color: white !important;
    resize: both !important;
    /* Allow resizing */
    
    transition: background-color 0.2s;
}
.btn-more {
    border-color: #fc0 !important;
    border-color: #F0C000 !important;
}
input[type="button"]:hover,
input[type="submit"]:hover,
select:hover,
button:hover,
input[type="submit"]:hover,
.btn-control:hover,
a[type="button"]:hover,
a.rbx-btn-control-xs:hover {
    cursor: pointer;
    background: #353535 !important;
    box-shadow: rgba(0, 0, 0, 200) 0 1px 3px 0;
}
input[type="text"] {
    border-color: #050505 !important;
    color: white !important;
}
a.btn-more {
