css:
@-moz-document domain("golang.org") {
body {
    background: #222;
    color: #DDD;
}
a {
    color: #AAA;
    opacity: 0.8;
}
a:hover {
    opacity: 1;
}
pre {
    background: #444;
}

#topbar, div#topbar, #banner, .top-bar {
    background: #204F7A;
}
.top-heading a, a.logo {
    color: #FFF;
}

.nav {
    fill: #EEE;
}

input.inactive {
    opacity: 0.8;
}

h2 {
    background: #333;
}

h1, h2, h3, h4, .rootHeading {
    color: #EEE;
}

.gopher, #gopher {
    display: none;
}

div#learn .input, div#learn .input textarea, div#learn .output, div#learn .output pre {
    background: #363636;
    color: #DDD;
}

pre .comment {
	color: #5bfb5b;
    opacity: 0.7;
}

div.play .input, div.play .input textarea, div.play .output, div.play .output pre {
    background: #363636;
    color: #DDD;
}

a, .exampleHeading .text {
    color: #cacaca;
    text-decoration: none;
}

pre .ln {
    background: #444;
    color: #AAA;
}

pre .selection, pre .selection-comment {
    background: #714e1f;
}

/* playground */

#wrap, #about {
    background: #363636;
    color: #DDD;
}
#code {
    color: #DDD;
}

/* blog */

#content .code {
    background: #444;
}
#content .code pre {
	background: inherit;
}
#sidebar {
    background: #333;
}

/* tour */

.output {
    background-image: none;
    background-color: inherit;
}

.slide-content {
    background: #222;
}

.module-bar {
    background: #2e2e2e;
}

#right-side {
    background: #333;
}

pre {
    color: #DDD;
}

.CodeMirror {
    color: #DDD;
    background: #444;
}
#file-editor .CodeMirror-lines, #file-editor .CodeMirror-gutters {
    background: #444;
}
#file-editor {
    background: #4e4e4e;
}

.output > pre {
    background: #444;
}

.module-title {
    color: #ddd;
}

.lesson {
    background: #3e3e3e;
}
.CodeMirror div.CodeMirror-cursor {
    border-left: 1px solid white;
}
#explorer, #file-menu {
    background: #666;
}
.cm-s-default .cm-string {
    color: #d44;
}

.cm-s-default .cm-keyword {
    color: #b3d;
}

.cm-s-default .cm-variable {color: #eee;}
.cm-s-default .cm-operator {color: #eaeaea;}

.article .image, .article svg {
 	background: #ccc;   
}

table.codetable tr {
 	background-color: #3a3a3a;   
}

table.codetable tr:nth-child(2n), table.codetable tr.first {
    background-color: #1e1e1e;
}

#output .stderr, #output .error {
    color: #b11;
}
}

@-moz-document domain("tour.golang.org") {
.container a {
    color: #AAA;
    opacity: 0.8;
}
.container a:hover {
    opacity: 1;
}
.container {
    background: #2e2e2e;
}
}

@-moz-document domain("build.golang.org") {
.dashboards > a {
    color: #22B;
}

table tr:nth-child(2n+1) {
    background: #3a3a3a;
}

.paginate > nav > a {
    color: #22B;
}

table tr.commit:hover {
    background-color: #515151 !important;
}

.result .fail {
 	color: #B33;   
}

}
